<?php	class PhotoGalleryPhotoModel extends CRUDModel {				public $smallImage;		public $image;		public $category;				public function __construct() {			$messages = Application::$messages['common_messages'];						$this->smallImage = new ModelFMImageField("smallImage", $messages['smallImage'], false, false);						$this->image = new ModelFMImageField("image", $messages['image'], false, false);						$categories = PhotoGalleryCategoryModel::getAsKeyVal('r_id', 'iTitle', 'az');						$this->category = new ModelSelectField("category", $messages['category'], $categories, true, false);			$this->category->multiLang = false;					}				public static $multiLang = false;				public static function initialize() {			self::$displayFields = Array('smallImage', 'category');			self::$title = Application::$messages['model_ph_gallery_photo']['title'];			self::$iconPath = 'category-icons.png';			self::$multiLang = false;			self::$viewUrl = 'news';			self::$useOwnViewUrl = false;		}			}?>