<?php	class ServicesNetworkModel extends CRUDModel {				public $r_id;		public $lang_id;		public $name;		public $address;		public $phone;		public $fax;		public $postalCode;		public $licenseNumber;		public $startTime;		public $workTime;		public $map;				public function __construct() {						$messages = Application::$messages['common_messages'];					$this->address = new ModelTextField("address", $messages['address'], true, true);			$this->address->required = false;						$this->name = new ModelTextField("name", $messages['name'], true, true);			$this->name->required = false;						$this->phone = new ModelTextField("phone", $messages['phones'], true, true);			$this->phone->common = true;			$this->phone->required = false;						$this->fax = new ModelTextField("fax", $messages['fax'], true, true);			$this->fax->common = true;			$this->fax->required = false;						$this->postalCode = new ModelTextField("postalCode", $messages['postalCode'], true, true);			$this->postalCode->common = true;			$this->postalCode->required = false;						$this->licenseNumber = new ModelTextField("licenseNumber", $messages['licenseNumber'], true, true);			$this->licenseNumber->common = true;			$this->licenseNumber->required = false;						$this->startTime = new ModelTextField("startTime", $messages['startTime'], true, true);			$this->startTime->common = true;			$this->startTime->required = false;						$this->workTime = new ModelTextField("workTime", $messages['workTime'], true, true);			$this->workTime->required = false;			//$this->workTime->common = true;						$this->map = new ModelTextArea("map", $messages['map'], true, true);			$this->map->required = false;			$this->map->common = true;					}				public static function initialize() {			self::$title = Application::$messages['model_serv_net']['title'];			self::$iconPath = 'category-icons.png';			self::$multiLang = true;			self::$viewUrl = 'services-network';			self::$displayFields = Array('name','address', 'phone', 'fax', 'postalCode', 'licenseNumber', 'startTime', 'workTime');		}			}?>