<?php	class ProductModel extends CRUDModel {		public $categoryId;		public $productTitle;		public $description;		public $image;		public $price;				public static $multiLang = false;				public function __construct() {			$categories = CategoryModel::getOnlyLastCategories(3, 'itemTitle');			$this->categoryId = new ModelSelectField("categoryId", Application::$messages['model_products']['field_categoryId'], $categories, true, false);			$this->productTitle = new ModelTextField("productTitle", Application::$messages['model_products']['field_productTitle'], true, false);			$this->description = new ModelTextArea("description", Application::$messages['model_products']['field_description'], true, false);			$this->image = new ModelFMImageField("image", Application::$messages['model_products']['field_image'], false, true, false);			$this->price = new ModelIntegerField("price", Application::$messages['model_products']['field_price'], true, false);		}				public static function initialize() {			self::$title = Application::$messages['model_products']['title'];			self::$iconPath = 'products.png';			self::$displayFields = Array('image','productTitle','categoryId','price');			self::$useOwnViewUrl = false;		}				public static function getByCategory($categoryId, $langId = false) {			return ($langId && self::$multiLang) ? self::find(" WHERE `lang_id` = '{#1}' AND `categoryId` = '{#2}'", Array($langId, $categoryId)) : self::find(" WHERE `categoryId` = '{#1}'", Array($categoryId));		}	}?>