<?php	class VideoGalleryVideoModel extends CRUDModel {				public $iTitle;		public $image;		public $iframe;		public $category;		public $showInMain;				public function __construct() {			$messages = Application::$messages['common_messages'];						$this->iTitle = new ModelTextField("iTitle", $messages['field_itemTitle'], true, true);						$this->image = new ModelFMImageField("image", $messages['image'], false, false);						$this->iframe = new ModelTextArea("iframe", $messages['field_video_i'], true, true);						$categories = VideoGalleryCategoryModel::getAsKeyVal('r_id', 'iTitle', 'az');						$this->category = new ModelSelectField("category", $messages['category'], $categories, true, false);			$this->category->multiLang = false;						$this->showInMain = new ModelSelectField("showInMain", $messages['showInMain'], Array(				0 => $messages['nno'],				1 => $messages['yyes']			), true, false);			$this->showInMain->multiLang = false;					}				public static $multiLang = false;				public static function initialize() {			self::$displayFields = Array('image', 'iTitle', 'category', 'showInMain');			self::$title = Application::$messages['model_v_gallery_video']['title'];			self::$iconPath = 'category-icons.png';			self::$multiLang = false;			self::$viewUrl = 'news';			self::$useOwnViewUrl = false;		}				public static function getVideoToMain() {			$r = self::find(" WHERE `showInMain` = '1' LIMIT 1");			if (count($r)) return $r[0];			else return false;		}			}?>