<?php	class ImageBannerModel extends CRUDModel {				public $r_id;		public $lang_id;		public $iTitle;		public $image;				public function __construct() {					$messages = Application::$messages['common_messages'];					$this->iTitle = new ModelTextField("iTitle", $messages['field_itemTitle'], true, true);			$this->iTitle->common = true;					$this->image = new ModelFMImageField("image", $messages['image'], true, true);				}				public static function initialize() {			self::$multiLang = true;			self::$title = Application::$messages['model_image_banner']['title'];			self::$iconPath = 'category-icons.png';			self::$useOwnViewUrl = false;			self::$displayFields = Array('image', 'iTitle');		}			}?>