<?php	class FeedbackModel extends CRUDModel {				public $name;		public $text;		//public $address;		public $email;		public $date;		public $answered;				public function __construct() {			$messages = Application::$messages['model_feedback'];			$commonMessages = Application::$messages['common_messages'];					$this->name = new ModelTextField("name", $messages['field_subject'], true, false);						$this->text = new ModelTextArea("text", $messages['field_text'], true, false);						$this->address = new ModelTextField("address", $messages['field_address'], true, false);						$this->email = new ModelTextField("email", $messages['field_email'], true, false);						$this->date = new ModelDateField("date", $messages['field_date'], true, false);						$this->answered = new ModelSelectField("answered", $messages['field_answered'], Array(				0 => $commonMessages['nno'],				1 => $commonMessages['yyes']			), true, false);		}				public static function initialize() {			self::$displayFields = Array('name', 'email', 'date', 'answered');			self::$title = Application::$messages['model_feedback']['title'];			self::$iconPath = 'feedback-icon.png';			self::$multiLang = false;			self::$viewUrl = 'feedback';		}			}?>