<?php	class NewsCommentsModel extends CRUDModel {				public $userId;		public $newsId;		public $commentText;		public $date;				public static $multiLang = false;				public function __construct() {			$messages = Application::$messages['model_news_comments'];			$lang = Application::$settings['default_language'];			$news = NewsModel::getAsKeyVal('r_id', 'itemTitle', $lang);			$users = SiteUsersModel::getAsKeyVal('id', 'email', $lang);						$this->userId = new ModelSelectField("userId", $messages['field_userId'], $users, true, true);			$this->userId->common = true;						$this->newsId = new ModelSelectField("newsId", $messages['field_newsId'], $news, true, true);			$this->newsId->common = true;						$this->commentText = new ModelTextArea("commentText", $messages['field_commentText'], true, true);						$this->date = new ModelDateField("date", $messages['field_date'], true, true);			$this->date->common = true;		}				public static function initialize() {			self::$title = Application::$messages['model_news_comments']['title'];			self::$displayFields = Array('newsId', 'userId', 'date');			self::$iconPath = 'comments-icon.png';			self::$useOwnViewUrl = false;		}			}?>