(1, 'en', '', '', '2015-09-01', 2131, '/QisaXeberler/siu_middle.jpg', '', NULL),
(2132, 'az', 'İnsult məktəbi Azərbaycanda', 'AZƏRBAYCAN RESPUBLİKASI PREZİDENT Yanında Qeyri-Hökümət Taşkilatlarına Dövlət Dəstəyi Şurasının 2013-cü il üçün elan edilən qrant hesabınaa ATU TTK – nın nəzdində 2013-cü ildən Azərbaycanda “İNSULT MƏKTƏBİ” yaradılıb. İnsult dünya üzrə xəstələnmə və ölüm hallarının', '2015-09-02', 2132, '/QisaXeberler/im_middle.jpg', 'page/59', NULL),
(1, 'az', 'Sağlam insan uğrunda', 'Hər birimiz cəmiyyətimizə xeyir verib, nəslimizi yaşadıb, arzularımızı həyyata keçirmək üçün ən azı sağlamlığımızı qoruyub saxlamaq üçün müəyyən biliklərə malik olmaq haqda tüşünürük. Söhbət yalnız rasional qıdalanmadan getmir. Hal-hazırda sağlamlığa iqtisadi kateqoriya kimi', '2015-09-01', 2129, '/QisaXeberler/siu_middle.jpg', 'page/58', NULL),
(2135, 'ru', '', '', '2015-09-03', 2136, '/QisaXeberler/dm_middle.jpg', '', NULL),
(2132, 'ru', '', '', '2015-09-02', 2133, '/QisaXeberler/im_middle.jpg', '', NULL),
(2132, 'en', '', '', '2015-09-02', 2134, '/QisaXeberler/im_middle.jpg', '', NULL),
(2135, 'az', 'Dərmansız müalicə', 'Tarixən belə hesab olunurdu ki, sağlamlıq haqqında bütün biliklər yalnız tibbə məxsusdur və burda istifadə olunur. Bu səbəbdən də sağlamlığı xəstəliyin olmaması ilə əlaqələndirirdilər. Amma canlı materiya haqda müasir təsəvvürlər bizim sağlamlığa aid biliklərimizi daha da genişləndirib.', '2015-09-03', 2135, '/QisaXeberler/dm_middle.jpg', 'page/60', NULL),
(2135, 'en', '', '', '2015-09-03', 2137, '/QisaXeberler/dm_middle.jpg', '', NULL);

CREATE TABLE IF NOT EXISTS `vl1_avtoadvicesmodel` (
