) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=34 ;

INSERT INTO `vl1_avtoadvicesmodel` (`r_id`, `lang_id`, `itemTitle`, `content`, `date`, `id`, `smallImage`, `forLoggedIn`, `type`) VALUES
(1, 'az', 'Sürücülük vəsiqəsinin dəyişdirilmə qaydaları', '<p>Hər birimiz cəmiyyətimizə xeyir verib, nəslimizi yaşadıb, arzularımızı həyyata ke&ccedil;irmək &uuml;&ccedil;&uuml;n ən azı sağlamlığımızı qoruyub saxlamaq &uuml;&ccedil;&uuml;n m&uuml;əyyən biliklərə malik olmaq haqda t&uuml;ş&uuml;n&uuml;r&uuml;k. S&ouml;hbət yalnız rasional qıdalanmadan getmir<strong>.&nbsp;</strong>Hal-hazırda<strong>&nbsp;</strong>sağlamlığa iqtisadi kateqoriya kimi baxılması təklif olunmuşdur ki&nbsp;bunun&nbsp;&nbsp;əsasında İNSAN&nbsp;&nbsp;&nbsp;KAPİTALI&nbsp;&nbsp;durur.<span class="s2">&nbsp;. İNSAN&nbsp;&nbsp;&nbsp;KAPİTALI&nbsp;&nbsp;fiziki,&nbsp;&nbsp;psixi və&nbsp;&nbsp;mənəvi sağlamlıqdır. Mənəvi sağlamlıq &ndash; bilik, bacarıq,&nbsp;&nbsp;qabiliyyət, təcr&uuml;bə, mədəni səviyəyə qoyulan investisiya nəticəsində əldə edilir.</span></p>', '2015-09-01', 1, NULL, NULL, 0),
