CREATE TABLE IF NOT EXISTS `vl1_avtoadvicesmodel1` (
  `r_id` int(32) NOT NULL,
  `lang_id` char(2) NOT NULL,
  `itemTitle` varchar(255) DEFAULT NULL,
  `content` longtext,
  `date` date DEFAULT NULL,
  `id` int(32) NOT NULL AUTO_INCREMENT,
  `image` varchar(150) DEFAULT NULL,
  `smallImage` varchar(150) DEFAULT NULL,
  `forLoggedIn` int(1) DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `Id` (`id`),
  KEY `R_id` (`r_id`),
  KEY `Lang id` (`lang_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=2162 ;

INSERT INTO `vl1_avtoadvicesmodel1` (`r_id`, `lang_id`, `itemTitle`, `content`, `date`, `id`, `image`, `smallImage`, `forLoggedIn`) VALUES
(2156, 'az', 'İcbari məcburidirmi?', '<p>2 il bundan &ouml;ncə "İcbari <a href="http://www.bakisigorta.az" target="_blank">Sığorta</a> Haqqında" Qanun dəyişdikdən sonra sığorta bazarında canlanma yarandı. Həm&ccedil;inin icbari <a href="http://www.bakisigorta.az">sığorta</a> olmadan texniki baxışdan ke&ccedil;mək qadağan olunduğundan sığorta şirkətləri m&uuml;ştəri axını ilə qarşılaşdılar. İcbari sığorta nə olduğunu bilmədən hamı bu sığorta n&ouml;v&uuml;nə m&uuml;raciət etdi. Və zaman-zaman <span style="color: #ff0000;"><strong>"mənim <a href="http://www.bakisigorta.az">kaskom</a> var icbari lazımdırmı?"</strong></span> , <span style="color: #ff0000;"><strong>"avtomobilimi tam sığortalamışam dəyən ziyanı &ouml;dəyəcəm icbari <a href="http://www.bakisigorta.az">sığorta</a> lazımdırmı?"</strong></span> , <span style="color: #ff0000;"><strong>"icbari sığorta məcburidirmi ?"</strong></span> sualları ilə qarşılaşırıq. Yol hərəkəti qaydalarını bilməyən, təbii, icbari sığortanı da bilməyəcək. Bu məqalədə icbari sığortanı sizə a&ccedil;ıqlayacağıq. Tam olaraq "Avtomobil sahiblərinin 3 şəxs qarşısında m&uuml;lki məsuliyyətinin icbari sığortası" adlanır.</p>\r\n<p>&nbsp;</p>\r\n<p>Bu icbari sığorta n&ouml;v&uuml; <strong><span style="color: #ff0000;">nəqliyyat vasitəsinin sahibinin qəza&nbsp;zamanı &uuml;&ccedil;&uuml;nc&uuml; şəxslərin əmlakına və səhhətinə vurduğu zərərə təminat verir</span>.</strong> Məsələn, Siz qəzaya səbəb olub başqasının avtomobilini əzsəniz, t&ouml;rətdiyiniz zərərin həcmi &nbsp;<a href="http://www.bakisigorta.az">5000 manatdan &nbsp;az olarsa</a>, zərərin həcmi qədər, 5000 manatdan &ccedil;ox olarsa, 5000 manat həcmində &ouml;dəniş ediləcəkdir. G&ouml;stərilən məbləğ <span style="color: #ff0000;"><strong>Sizə yox, maşınını əzdiyiniz şəxsə verilir.</strong></span></p>\r\n<p>Minik avtomobilləri &uuml;&ccedil;&uuml;n illik sığorta haqqı nəqliyyat vasitəsinin m&uuml;hərrik həcmindən asılıdır.Bu minik avtomobillərinə aiddir. Nəqliyyat vasitənizin icbari sığorta haqqını <a href="http://www.avtostop.tv">www.avtostop.tv</a>&nbsp; saytındakı kalkulyator vasitəsi ilə hesablaya bilərsiniz. D&ouml;vlət Yol Polisi bu sığortanın olmadığı halda 10 manat məbləğində cərimə edir. &nbsp;Məncə bu cərimə azdır.</p>\r\n<p>&nbsp;</p>\r\n<p>Avtonəqliyyat vasitəsi sahiblərinin m&uuml;lki məsuliyyətinin<span style="color: #ff0000;"><strong> icbari sığorta şəhadətnaməsinin kimin adına verilməsindən asılı olmayaraq</strong></span> həmin şəhadətnamədə qeyd olunan avtonəqliyyat vasitəsini qanuni əsaslarla istifadə edən istənilən şəxs &uuml;&ccedil;&uuml;nc&uuml; şəxslər qarşısında m&uuml;lki məsuliyyətinə g&ouml;rə əmlak mənafeyi <a href="http://www.bakisigorta.az">sığorta</a>lanmış sığorta olunan hesab edilir.</p>\r\n<p>&nbsp;</p>\r\n<p>&Uuml;&ccedil;&uuml;nc&uuml; şəxslərin əmlakı dedikdə aşağıdakılar nəzərdə tutulur:</p>\r\n<ul>\r\n<li>Başqa nəqliyyat vasitəsinin zədələnməsi və ya məhv edilməsi;</li>\r\n<li>Yolun və yol qurğularının zədələnməsi və ya məhv edilməsi;</li>\r\n<li>Hər hansı digər maddi sərvətlərin və əşyaların zədələnməsi və ya məhv edilməsi.</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p>&Uuml;&ccedil;&uuml;nc&uuml; şəxslərin səhhətinə vurulan zərər aşağıdakılar hallarda &ouml;dənilir:</p>\r\n<ul>\r\n<li>xəsarət alma;</li>\r\n<li>əmək qabiliyyətinin tam və ya qismən itirilməsi (əlillik, 18 yaşınadək sağlamlıq imkanlarının məhdudluğu);</li>\r\n<li>&uuml;&ccedil;&uuml;nc&uuml; şəxsin &ouml;l&uuml;m&uuml;</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p>Həm&ccedil;inin <a href="http://www.bakisigorta.az">sığorta</a> &ouml;dənişinin &ouml;dənilməmə halları da var.</p>\r\n<p>&nbsp;</p>\r\n<p>Hərbi əməliyyatlar, terror&ccedil;uluq, k&uuml;tləvi iğtişaşlarla&hellip;əlaqədar olduqda, bundan başqa sığorta tələbi &uuml;&ccedil;&uuml;nc&uuml; şəxsin pul, qiymətli kağızlar, qiymətli metallar və qiymətli daşlar, sənət əsərləri, o c&uuml;mlədən rəsm əsərləri, qrav&uuml;ralar, heykəllər, həm&ccedil;inin modellər, plan və eskizlər, ixtira patentləri, sənədlər, kitablar və yazılar şəklində olan əmlakına dəymiş zərərlə, habelə mənəvi zərərlə və ya gəlir itkisi ilə, həm&ccedil;inin &hellip;sığortalının və ya sığorta olunanın cərimə, dəbbə pulu, peniya &ouml;dəməsi &uuml;zrə əmlak mənafeləri ilə bağlı olduqda, sığorta &ouml;dənişi verilmir.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Nəqliyyat vasitəsi sahibinin m&uuml;lki məsuliyyəti <a href="http://www.bakisigorta.az">sığortası</a> Azərbaycan Respublikasının ərazisində q&uuml;vvədədir. Azərbaycan Respublikasının h&uuml;dudlarından kənarda yol-nəqliyyat hadisəsi baş verdikdə&nbsp; nəqliyyat vasitəsi sahibinin m&uuml;lki məsuliyyəti sığortası &uuml;zrə sığorta &ouml;dənişinin verilməsi yerli sığorta&ccedil;ının m&uuml;vafiq xarici &ouml;lkənin sığorta&ccedil;ısı ilə bağladığı m&uuml;qavilə, yaxud Azərbaycan Respublikasının beynəlxalq sazişləri əsasında tənzimlənir.</p>\r\n<p>&nbsp;</p>\r\n<p>Paslanmadan &ccedil;&uuml;r&uuml;mə, təbii fəlakətlər, hərbi əməliyyat, yaxud hərbi təlim, oğurlanma, m&uuml;əssisə, idarə və təşkilatların daxili ərazisində hərəkət, y&uuml;klənmə və boşaltma, habelə yol-nəqliyyat hadisəsi ilə bağlı olmayan hər hansı digər hallar nəticəsində nəqliyyat vasitəsinə vurulan zərər bu Qanuna g&ouml;rə sığorta hadisəsi sayılmır.</p>\r\n<p>&nbsp;</p>\r\n<p>Həm&ccedil;inin, bəzi s&uuml;r&uuml;c&uuml;lər hesab edir ki, o , hadisə t&ouml;rədir və icbari sığorta olduğuna g&ouml;rə dəymiş ziyan &ouml;dənilibsə, ondan he&ccedil; kim pul ala bilməz. Əslində bu belə deyil.</p>\r\n<p>&nbsp;</p>\r\n<p>Hadisə, &hellip;<a href="http://www.bakisigorta.az">sığortalının</a> və ya sığorta olunanın həmin hadisənin baş verməsinə y&ouml;nəldilmiş <span style="color: #ff0000;"><strong>qəsdən etdiyi qanunazidd hərəkətlərinin nəticəsi olduqda, avtonəqliyyat vasitəsinin sərxoş vəziyyətdə istismar edilməsi nəticəsində və ya onu idarə etməyə h&uuml;ququ olmayan şəxs tərəfindən idarə edilməsi, yaxud texniki təyinatına uyğun olmayan məqsədlərlə istifadə edilməsi zamanı baş verdikdə,</strong></span> sığorta&ccedil;ı &uuml;&ccedil;&uuml;nc&uuml; şəxsə verdiyi <span style="color: #ff0000;"><strong>sığorta &ouml;dənişini subroqasiya h&uuml;ququ əsasında sığorta olunandan geri almaq h&uuml;ququna malikdir.</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p>Və son olaraq yuxarıda verilmiş sualların cavabları :</p>\r\n<p>&nbsp;</p>\r\n<ul>\r\n<li><strong><span style="color: #ff0000;"><a href="http://www.bakisigorta.az">Kasko sığortanızın</a> olması sizi icbari sığorta etdirməkdən azad etmir</span></strong></li>\r\n<li><strong><span style="color: #ff0000;">Avtomobilinizin tam olaraq hətta qarşı tərəfə vurulan ziyanı belə &ouml;dənilməsini nəzərdə tutan sığortanız olsa belə icbari sığortanız olmaldır.</span></strong></li>\r\n<li><strong><span style="color: #ff0000;">Bankda ipotekada olan avtomobil bank tərəfindən sığortalansa belə siz yenə də icbari sığorta etdirməlisiniz.</span></strong></li>\r\n<li><strong><span style="color: #ff0000;">İcbari məcburi s&ouml;z&uuml;n&uuml;n sinonimidir.</span></strong></li>\r\n</ul>', '2014-02-28', 2159, '/Avtofayda/Icon_8-64.png', NULL, NULL),
(1, 'az', '"Sığorta nə vaxt pul ödəmir?"', '<h1 dir="ltr">Bu g&uuml;n s&uuml;r&uuml;c&uuml;lərin bir &ccedil;oxunu "hadisə baş verdikdə <a href="http://www.bakisigorta.az">sığorta</a> &ouml;dənişi olunmaya bilərmi?" sualı maraqlandırır. Bir &ccedil;ox sığorta şirkətləri qəza baş verdikdə sığorta &ouml;dənişindən imtina edir və belə hal olduqda <a href="http://www.bakisigorta.az">sığorta</a> bizim pulumuzu vermir ittihamları ilə qarşılaşırıq. Bu isə sığorta şirkətlərinə qarşı anti-reklam kompaniyasına belə &ccedil;evirlə bilir. Problem d&uuml;zg&uuml;n məlumatlandırılmama və sığorta m&uuml;qaviləsini oxumamaqdan irəli gəlir.</h1>\r\n<h1 dir="ltr">Avtomobilini <a href="http://www.bakisigorta.az">sığortalamış</a> şəxs qəza zamanı nə edəcəyini bilmir. Əslində sığortalının bilməli olduğu &ccedil;ox şey var. İlk olaraq qəza baş verdikdə s&uuml;r&uuml;c&uuml;n&uuml;n vəzifələrinə diqqət edək.</h1>\r\n<h1 dir="ltr">&nbsp;</h1>\r\n<h1 dir="ltr">Yol Hərəkəti Haqqında" Qanun 37-ci maddə :</h1>\r\n<h1 dir="ltr"><span>IV. Mexaniki nəqliyyat vasitəsinin s&uuml;r&uuml;c&uuml;s&uuml; yol-nəqliyyat hadisəsi zamanı:</span></h1>\r\n<h1 dir="ltr"><br />1) nəqliyyat vasitəsini <strong>yerindən tərpətməməli</strong>, <strong>qəza dayanma nişanını qurmalı, qəza işıq siqnalını yandırmalı, ya da yanıb-s&ouml;nən qırmızı işıq fənərini qoymalı, hadisəyə aidiyyatı olan əşyaların yerini dəyişməməlidir;</strong><br />2) zərər &ccedil;əkənlərə ilk tibbi yardım g&ouml;stərilməsi &uuml;&ccedil;&uuml;n <strong>m&uuml;mk&uuml;n olan tədbirləri g&ouml;rməli, &laquo;Təcili tibbi yardım&raquo; maşını &ccedil;ağırmalı, zərər &ccedil;əkənləri həyat &uuml;&ccedil;&uuml;n təhl&uuml;kəli xəsarət aldıqları hallarda isə, onu səmt &uuml;zrə gedən nəqliyyat vasitəsi ilə yaxınlıqdakı tibbi m&uuml;əssisəyə g&ouml;ndərməli, bu m&uuml;mk&uuml;n olmadıqda isə, &ouml;z&uuml;n&uuml;n nəqliyyat vasitəsi ilə aparmalı,</strong> orada <strong>şəxsiyyətini təstiq edən sənəd və ya vəsiqə, nəqliyyat vasitəsinin qeydiyyatı sənədini təqdim etməklə &ouml;z&uuml;n&uuml;n soyadını, nəqliyyat vasitəsinin qeydiyyat n&ouml;mrə nişanını bildirməli, sonra hadisə yerinə qayıtmalıdır;</strong><br />3) başqa nəqliyyat vasitələrinin <strong>hərəkəti qeyri-m&uuml;mk&uuml;n olduqda, yolun hərəkət hissəsini boşaltmalıdır.</strong> Yolun hərəkət hissəsini <strong>boşaltmaq və ya zərər &ccedil;əkənləri &ouml;z nəqliyyatında m&uuml;alicəxanaya aparmaq zərurəti olduqda, şahidlərin iştirakı ilə əvvəlcə nəqliyyat vasitəsinin vəziyyətini, izləri və hadisəyə aidiyyəti olan əşyaları qeyd etməli və onların qorunması, nəqliyyat vasitələrinin hadisə yerinin yanından ke&ccedil;məsini təşkil etmək &uuml;&ccedil;&uuml;n m&uuml;mk&uuml;n olan b&uuml;t&uuml;n tədbirləri g&ouml;rməlidir;</strong><br />4) hadisə haqqında Azərbaycan Respublikasının m&uuml;vafiq icra hakimiyyəti orqanına xəbər verməli, <strong>hadisəni g&ouml;rm&uuml;ş adamların soyadlarını, &uuml;nvanlarını yazmalı və Azərbaycan Respublikasının m&uuml;vafiq icra hakimiyyəti orqanı əməkdaşlarının gəlməsini g&ouml;zləməlidir;</strong><br />5) əgər yol-nəqliyyat hadisəsi nəticəsində he&ccedil; kim xəsarət almamışdırsa, maddi zərər c&uuml;zidirsə, nəqliyyat vasitələrində onların hərəkət etməsinə mane ola biləcək nasazlıq yoxdursa, və yol-nəqliyyat hadisəsini t&ouml;rədənlər hadisənin baş verdiyi şəraiti qiymətləndirməkdə razılığa gələrlərsə, <strong>hadisənin sxemini tərtib edib imzaladıqdan sonra, hadisəni m&uuml;əyyən olunmuş qaydada sənədləşdirmək &uuml;&ccedil;&uuml;n Azərbaycan Respublikasının m&uuml;vafiq icra hakimiyyəti orqanına gəlməlidirlər.</strong></h1>\r\n<h1 dir="ltr">&nbsp;</h1>\r\n<h1 dir="ltr">37-ci maddəyə əsasən qəza baş verirsə, avtomobilləri yerindən tərpətmək olmaz. Lakin, hərəkətin qarşısını alırsa, yolun hərəkət hissəsindən avtiomobil, m&uuml;tləq, kənara &ccedil;əkilməlidir. Bəzi sığorta şirkətləri &ouml;dənişi etməmək məqsədi ilə yolu boşaldan sığortalıya &ouml;dənişdən imtina edirlər. Bu k&ouml;k&uuml;ndən yanlışdır. İlk olaraq yolun hərəkət hissəsinin boşaldılma səbəbi aydınlaşdırılmalı, daha sonra qərar verilməlidir. 37-ci maddəyə uyğundursa, &ouml;dənişdən imtina oluna bilməz.</h1>\r\n<h1 dir="ltr"><a href="http://www.bakisigorta.az">Sığorta</a> hadisəsi baş verdikdə&nbsp;sığortalının&nbsp;aşağıdakılara &nbsp;riayət etməsi vacibdir:</h1>\r\n<h1 dir="ltr"><br class="kix-line-break" />Qəza baş verdikdə dərhal avtomobilinizi sığorta etdirdiyiniz sığorta şirkətinin n&ouml;vbət&ccedil;i telefon n&ouml;mrələrinə zəng edərək hadisə barədə ətraflı məlumat verin. Bundan başqa, m&uuml;vafiq h&uuml;quq &mdash; m&uuml;hafizə orqanına (102 - polis xidməti)&nbsp; hadisə barədə məlumat verməlisiniz.&nbsp;<br class="kix-line-break" /><br class="kix-line-break" />Əgər <a href="http://www.bakisigorta.az">sığorta</a> hadisəsi barədə məlumat sığorta&ccedil;ıya dərhal verilməzsə və sığortalı hadisə barədə dərhal məlumat vermək iqtidarında olmadığını s&uuml;but edə bilməzsə, <a href="http://www.bakisigorta.az">sığorta&ccedil;ı</a> <span style="text-decoration: underline;"><span style="color: #ff0000; text-decoration: underline;"><strong>sığorta &ouml;dənişindən imtina edə bilər.</strong> </span></span>Sığorta şirkətinin qəza eksperti hadisə yerinə gələnədək sığortalı nəqliyyat vasitəsinin vəziyyətində və hadisə yerində he&ccedil; bir dəyişiklik etməməlidir. &nbsp;<a href="http://www.bakisigorta.az">Sığorta</a> hadisəsi baş verdikdə &nbsp;qeyd olunanlardan hər hansı birinə riayət etməsəniz, <a href="http://www.bakisigorta.az">sığorta şirkətiniz</a>&nbsp; sizə sığorta &ouml;dənişinin verilməsindən imtina edə bilər.&nbsp;<br class="kix-line-break" /><br class="kix-line-break" />Bu halda sığorta şirkətləri <a href="http://www.bakisigorta.az">sığorta</a> &ouml;dənişinin verilməsindən imtina edirlər:&nbsp;<br class="kix-line-break" /><br class="kix-line-break" />&mdash; Sığortalı nəqliyyat vasitəsi sığorta şəhadətnaməsinə və ya qanunvericiliyə əsasən səlahiyyəti olmayan şəxs tərəfindən idarə edildikdə;&nbsp;<br class="kix-line-break" />&mdash; Sığorta etdirənin (Sığortalının) və ya nəqliyyat vasitəsini idarə etmək h&uuml;ququ olan şəxsin kobud ehtiyatsızlığı nəticəsində nəqliyyat vasitəsinə dəyən zərərlər.&nbsp;</h1>\r\n<h1 dir="ltr"><br />Kobud ehtiyatsızlıq dedikdə aşağıdakı hallar nəzərdə tutulur:&nbsp;<br class="kix-line-break" />&mdash; svetoforun və ya nizamlayıcının qadağanedici işarəsində hərəkətin davam etdirilməsi;&nbsp;<br class="kix-line-break" />&mdash; hərəkət istiqamətlərinə aid olan tələblərin pozulması;&nbsp;<br class="kix-line-break" />&mdash; yolda m&uuml;əyyən edilmiş hərəkət s&uuml;rətinin yaşayış məntəqələrində 20 km/saatdan, magistral yollarda 30 km/saatdan artıq aşılması;&nbsp;<br class="kix-line-break" />&mdash; nəqliyyat vasitəsi spirtli i&ccedil;ki və narkotik maddə qəbul etmiş şəxs tərəfindən idarə olunan zaman dəyən zərərlər və s.&nbsp;<br class="kix-line-break" /><br /></h1>\r\n<h1 dir="ltr"><br class="kix-line-break" /><a href="http://www.bakisigorta.az">Sığorta</a> &ouml;dənişinin verilməsindən imtinanın əsaslarının tam siyahısı sığorta şirkəti ilə bağlanan sığorta m&uuml;qaviləsinə əlavə edilmiş Nəqliyyat vasitələrinin <a href="http://www.bakisigorta.az">kasko sığortasının</a> qaydalarında g&ouml;stərilir. Həmin qaydalar ilə tanış olmağınız &uuml;&ccedil;&uuml;n m&uuml;qaviləni başdan-ayağa diqqətlə oxumağınız lazımdır. &nbsp;Sonra sığorta şirkətinin sizə &ouml;dəniş etməməsini &ldquo;bizdə <a href="http://www.bakisigorta.az">sığortanın</a> olmaması&rdquo; ilə bağlamayın. Bəlkə, &ouml;z&uuml;n&uuml;z qanunlarımızın və m&uuml;qavilənin şərtlərini pozursunuz? Oxuyun və yolda diqqətli olun!</h1>\r\n<p>&nbsp;</p>', '2014-02-18', 2155, '/Sigortalan/car-insurance-1.jpg', NULL, NULL),
(2160, 'az', '"Qəzaya düşdükdə nə etməli ?"', '<p>Bir &ccedil;ox s&uuml;r&uuml;c&uuml;lər qəza baş verdikdə nə etməli olduqlarını bilmirlər. Biz bu video-məqaləmizdə qəza baş verən zaman etməli olduqlarınızı deyəcəyik. Siz isə &ouml;z n&ouml;vbənizdə &ccedil;alışın qəzaya d&uuml;şməyəsiniz. D&uuml;şsəniz belə, t&ouml;vsiyyələrimizdən istifadə edin!</p>\r\n<p>&nbsp;</p>\r\n<p><iframe src="http://www.youtube.com/embed/RXTwwsqtXnk" frameborder="0" width="530" height="350"></iframe></p>', '2014-05-05', 2160, '/Avtofayda/insurance-10-64.png', NULL, NULL),
(2161, 'az', '1+1 kampaniyası', '<p>Dostumuz "Bakı Sığorta"-dan sizə təklif var. &Ouml;z əmlakını adi şərtlərlə, Duplex sığorta şərtləri ilə, eyni zamanda avtomobilini adi kasko və Turbo kasko şərtləri ilə sığortalayan b&uuml;t&uuml;n fiziki şəxslərə, n&ouml;vbəti ilin sığortası şirkət tərəfindən hədiyyə olunur. Yerdə qalan b&uuml;t&uuml;n qaydalar: 5000 manata qədər kredit, hadisə zamanı evakuasiya, ƏVƏZEDİCİ avtomobil, DYP-dən sənədlərin toplanması və h&uuml;quqi məsləhət eyni qaydada qalır.</p>\r\n<p>&nbsp;</p>\r\n<p><iframe src="http://www.youtube.com/embed/zPru8Tfb0pI" frameborder="0" width="513" height="350"></iframe></p>', '2014-08-22', 2161, '/Avtofayda/insurance-10-64.png', NULL, NULL);

