CREATE TABLE IF NOT EXISTS `vl1_competitionquestionsmodel` (
  `id` int(15) NOT NULL AUTO_INCREMENT,
  `question` text NOT NULL,
  `var1` text NOT NULL,
  `var2` text NOT NULL,
  `answer` text NOT NULL,
  `true_var` varchar(255) NOT NULL,
  `id_1` int(5) NOT NULL,
  `id_2` int(5) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=1214 ;

INSERT INTO `vl1_competitionquestionsmodel` (`id`, `question`, `var1`, `var2`, `answer`, `true_var`, `id_1`, `id_2`) VALUES
(1191, 'Əgər hərəkət zamanı daşın dəyməsindən qabaq süşə tam şəkildə sıradan çıxıbsa, Siz nə etməlisiniz?', 'Hərəkəti xüsusi ehtiyatla davam etdirməlisiniz', 'Xüsusi ehtiyatla təmir və ya durma yerinə getməlisiniz.', 'Xüsusi ehtiyatla təmir və ya durma yerinə getməlisiniz.', 'var2', 83605, 18886),
(1192, 'Aşağıdakı hansı nasazlıqlar olduqda belə yük avtomobilinin istismarına icazə verilir?', 'Spidometr işləmir.', 'Soyutma sisteminin temperatur göstəricisi işləmir.', 'Soyutma sisteminin temperatur göstəricisi işləmir.', 'var2', 49743, 57567),
(1193, '"Sürücünün reaksiya müddəti" dedikdə nə başa düşülür?', 'Sürücünün təhlükəni gördüyü andan nəqliyyat vasitəsini tam dayandırmağa kimi sərf olunan vaxt.', 'Sürücünün təhlükəni gördüyü andan onun bu təhlükəni sovuşdurmaq məqsədi ilə gördüyü tədbirə qədər sərf olunan vaxt.', 'Sürücünün təhlükəni gördüyü andan onun bu təhlükəni sovuşdurmaq məqsədi ilə gördüyü tədbirə qədər sərf olunan vaxt.', 'var2', 79646, 42902),
(1194, 'Yük avtomobilinin bort platformasında hərəkət edən sərnişinlərə ayaq üstə durmağa bortun və ya bortdan yuxarı səviyyədə olan yükün üzərində oturmağa icazə verilir?', 'İcazə verilmir', 'Avtomobilin sürəti saatda 30 km-dən aşağıdırsa icazə verilir.', 'İcazə verilmir', 'var1', 58863, 71877),
(1195, 'Əgər yol-nəqliyyat hadisəsi nəticəsində heç kim xəsarət almayıbsa sürücülər əraziyə DYP inspektoru çağırmaq məcburiyyətindədirlərmi?', 'Xeyr.Əgər YNH-ini törədənlər bu şəraiti qiymətləndirmədə razılığa gəliblərsə ,hadisənin sxemini tərtib edib imzalayıblarsa;', 'Bəli. İstənilən halda çağırılmalıdır.', 'Xeyr.Əgər YNH-ini törədənlər bu şəraiti qiymətləndirmədə razılığa gəliblərsə ,hadisənin sxemini tərtib edib imzalayıblarsa;', 'var1', 67269, 13172),
(1196, 'Aşağıda sadalanan hansı hala görə avtomobil yol hərəkətində iştirakdan kənarlaşdırıla və DİN-nin mühafizə olunan duracağında saxlanmaq üçün göndərilə bilər?', 'Toy karvanında iştirak etmək üçün qapıların şüşələrinə pərdə çəkildiyinə görə.', 'Toy karvanında hərəkət edərək əks istiqamət üçün nəzərdə tutulmuş hərəkət zolagına çıxıb orada hərəkət etdiyinə görə.', 'Toy karvanında iştirak etmək üçün qapıların şüşələrinə pərdə çəkildiyinə görə.', 'var1', 19609, 56649),
(1197, 'Avtobusun pəncərələrindən pərdə asmağa icazə verilir?', 'İcazə verilir, ancaq hər iki tərəfdə arxanı göstərən xarici güzgülər olduqda.', 'Qadağandır.', 'İcazə verilir, ancaq hər iki tərəfdə arxanı göstərən xarici güzgülər olduqda.', 'var1', 11049, 64561),
(1198, 'Sürücünün son dərəcə yorğunluğunun tipik əlamətləri hansılardır?', 'Mürgüləmək, əzginlik, diqqətin kütləşməsi (yayınıq olması).', 'Başgicəllənmə, gözlərin göynəməsi, yüksək tərləmə.', 'Mürgüləmək, əzginlik, diqqətin kütləşməsi (yayınıq olması).', 'var1', 11984, 95206),
(1199, 'Sürücü polis əməkdaşının  tələbi ilə təcili tibbi yardıma ehtiyacı olan şəxsləri yaxında olan xəstəxanalara aparmaq üçün öz nəqliyyat vasitəsini təqdim etməlidirmi?;', 'Bəli. Ancaq eyni səmtə getdiyi zaman.', 'Bəli.Hərəkət istiqamətindən asılı olmayaraq.', 'Bəli. Ancaq eyni səmtə getdiyi zaman.', 'var1', 11563, 60524),
(1200, 'Atmosfer təzyiqi avtomobilin yanacaq sərfinə necə təsir göstərir?', 'Atmosfer təzyiqi aşağı düşdükdə yanacaq sərfi artır..', 'Atmosfer təzyiqi aşağı düşdükdə yanacaq sərfi azalır.', 'Atmosfer təzyiqi aşağı düşdükdə yanacaq sərfi artır..', 'var1', 62073, 67030),
(1201, 'Siz sahibi olduğunuz çəkisi 3.5 tondan artıq olan nəqliyyat vasitəsini hansı halda yanında olmaqla digər şəxcsə verə bilərsiniz ?', '"B" kateqoriyalı sürücülük vəsiqəsi varsa ', '"C" kateqoriyalı sürücülük vəsiqəsdi varsa', '"B" kateqoriyalı sürücülük vəsiqəsi varsa ', 'var2', 46540, 68016),
(1202, 'Minik avtomobilində əlavə tormozlama siqnallarını quraşdırmağa icazə verilir?', 'İcazə verilir, 2 ədəddən artıq olmamaqla.', 'İcazə verilir, 1 ədəddən artıq olmamaqla.', 'İcazə verilir, 1 ədəddən artıq olmamaqla.', 'var2', 17663, 56154),
(1203, 'Bir dəfəyə  bir neçə nəqliyyat vasitəsini ötməyə icazə verilir?', 'İcazə verilmir', 'İcazə verilir o halda ki, nəqliyyat vasitələrinin sürəti saatda 30 km-dən aşağı olsun.', 'İcazə verilmir.', 'var1', 69397, 73877),
(1204, 'Yolda müəyyən edilmiş hərəkət sürətini 20-40 km aşılmasına görə cərimə və cərimə balı nə qədərdir ?', '50 azn 2 bal', '60 azn 3 bal', '50 azn 2 bal', 'var1', 97220, 98854),
(1205, 'Yaşayiş məntəqələrində hansı hallarda saatda 60 kilometrdən artıq sürətlə hərəkət etməyə icazə verilir?', 'Ancaq saatda 60 kilometrdən artıq sürətlə hərəkətə icazə verən yol nişanı quraşdırıldığı halda', 'Ötmə zamanı', 'Ancaq saatda 60 kilometrdən artıq sürətlə hərəkətə icazə verən yol nişanı quraşdırıldığı halda', 'var1', 38192, 88223),
(1206, 'Svetoforun sarı rəngli işığının yanıb-sönməsi nəyi bildirir?', 'Svetoforun nasaz olmasını', 'Hərəkətə icazə verir və nizamlanmayan yolayrıcının və ya piyada keçidinin olduğunu bildirir', 'Hərəkətə icazə verir və nizamlanmayan yolayrıcının və ya piyada keçidinin olduğunu bildirir', 'var2', 21150, 21798),
(1207, 'Nişanlanma xətlərinin və nişanların olmadığı halda hərəkət hissəsində olan zolaqların sayını təyin etmək üçün sürücü nəyi nəzərə almalıdır?', 'Ancaq hərəkət hissəsinin enini.', 'Hərəkət hissəsinin enini .avtomobiliərin qabaritlərini və onlar arasındakı intervalları.', 'Hərəkət hissəsinin enini .avtomobiliərin qabaritlərini və onlar arasındakı intervalları.', 'var2', 97110, 60893),
(1208, 'Dəmir yol keçidlərində və onların yaxınlığında ötməyə aid hansı məhdudiyyətlər mövcuddur?', ' Ancaq dəmir yol keçidlərində ötmə qadağandır.', 'Dəmir yol keçidlərində və onlara 100 metrdən az qaldıqda ötmə qadağandır.', 'Dəmir yol keçidlərində və onlara 100 metrdən az qaldıqda ötmə qadağandır.', 'var2', 69365, 76756),
(1209, 'Real sürətin nisbətən az olması təəssüratını hansı rənglər yaradır?', 'Al rəngli nəqliyyat vasitələri - qırmızı, narıncı.', 'Tünd rəngli nəqliyyat vasitələri - qara, yaşıl.', 'Tünd rəngli nəqliyyat vasitələri - qara, yaşıl.', 'var2', 93795, 28228),
(1210, 'Minik avtomobilinin arxa şüşəsindən jalüz və ya pərdə asmağa icazə verilir?', 'İcazə verilir, ancaq hər iki tərəfdə arxanı göstərən xarici güzgülər olduqda.', 'Qadağandır.', 'Qadağandır.', 'var2', 48633, 61065),
(1211, 'Dəmir yol keçidində quraşdırılmış svetoforun qırmızı rəngli işığının yanıb-sönməsi və ya növbə ilə iki yanıb-sönən qırmızı işıq nəyi bildirir?', 'Hərəkəti qadağan edir.', 'Hərəkətə xüsusi ehtiyatlıqla icazə verir.', 'Hərəkəti qadağan edir.', 'var1', 31401, 58243),
(1212, 'Aşağıdakı hansı hallarda avtomobilin istismarına icazə verilir?', 'Amortizatorda kənar səslər olduqda.', 'Palçıqdan qoruyan fartuklar və brızqoviklər olmadıqda.', 'Amortizatorda kənar səslər olduqda.', 'var1', 17714, 32451),
(1213, 'Ümumi istifadədə olan nəqliyyat vasitələrinin, habelə adamlar daşınan yük avtomobillərinin sərnişinlərlə yanacaqdoldurma məntəqəsinə daxil olmasına görə sürücülərə qarşı hansı inzibati cəzalar tətbiq edilə bilər?', '60 manat miqdarda cərimə və 3 bal.', '10-20 manatadək miqdarda cərimə.', '60 manat miqdarda cərimə və 3 bal.', 'var1', 22804, 19699);

