CREATE TABLE IF NOT EXISTS `vl1_menumodel` (
  `type` int(1) DEFAULT NULL,
  `menuItemTitle` varchar(150) DEFAULT NULL,
  `id` int(32) NOT NULL AUTO_INCREMENT,
  `link` varchar(150) DEFAULT NULL,
  `modelId` int(3) DEFAULT NULL,
  `treeItemId` int(32) DEFAULT NULL,
  `content` longtext,
  `r_id` int(50) DEFAULT NULL,
  `lang_id` char(2) DEFAULT NULL,
  `pageId` int(5) DEFAULT NULL,
  `visible` smallint(1) DEFAULT NULL,
  `forLoggedIn` int(1) DEFAULT NULL,
  `pageType` int(2) DEFAULT NULL,
  `image` varchar(200) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `R id` (`r_id`),
  KEY `Lang id` (`lang_id`),
  KEY `For logged in` (`forLoggedIn`),
  KEY `Visible` (`visible`),
  KEY `Tree item id` (`treeItemId`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=663 ;

INSERT INTO `vl1_menumodel` (`type`, `menuItemTitle`, `id`, `link`, `modelId`, `treeItemId`, `content`, `r_id`, `lang_id`, `pageId`, `visible`, `forLoggedIn`, `pageType`, `image`) VALUES
(3, 'Sağlam insan uğrunda', 640, 'ru/for/human', 10, 269, NULL, 58, 'ru', 4, 1, NULL, 0, ''),
(3, 'Sağlam insan uğrunda', 641, 'en/for/human', 10, 269, NULL, 58, 'en', 4, 1, NULL, 0, ''),
(3, 'İnsult məktəbi Azərbaycanda', 642, 'az/insult/school', 0, 270, '<p>AZƏRBAYCAN RESPUBLİKASI PREZİDENT Yanında Qeyri-H&ouml;k&uuml;mət Taşkilatlarına</p>\r\n<p>D&ouml;vlət Dəstəyi Şurasının 2013-c&uuml; il &uuml;&ccedil;&uuml;n elan edilən qrant hesabınaa ATU TTK &ndash; nın nəzdində</p>\r\n<p>2013-c&uuml; ildən Azərbaycanda &ldquo;İNSULT MƏKTƏBİ&rdquo; yaradılıb.</p>\r\n<p>İnsult d&uuml;nya &uuml;zrə xəstələnmə və &ouml;l&uuml;m hallarının ən &ouml;ndə gedən səbəblərindən biridir.</p>\r\n<p>İnsultun yayılmasında, xəstələnmə və &ouml;l&uuml;m hallarında Şərq &Ouml;lkələri və Qərbi Avropa &ouml;lkələri</p>\r\n<p>arasında b&ouml;y&uuml;k fərqlər m&ouml;vcuddur. Xəstələnmə və uzun m&uuml;ddətli əmək qabiliyyətinin itirilməsi</p>\r\n<p>səbəbindən yayılma dərəcəsinə g&ouml;rə insult daha b&ouml;y&uuml;k əhəmiyyət kəsb edir. İnsulta dair b&uuml;t&uuml;n</p>\r\n<p>t&ouml;vsiyyələrin kordinasiyası Avropa İnsult Təşkilatı (ESO) və digər milli və regional cəmiyyətlərin</p>\r\n<p>dəstəyi ilə &Uuml;mumd&uuml;nya İnsult Təşkilatı (WSO) tərəfindən həyata ke&ccedil;irilir. İnsultun əlamət və</p>\r\n<p>simptom-larının pasientin &ouml;z&uuml; və ya qohumları və ətrafdakılar tərəfindən duyulması, ilkin tibbi</p>\r\n<p>kontaktın xarakteri və stasionara g&ouml;ndərilməsi &uuml;sulu nəticədə b&ouml;y&uuml;k əhəmiyyət kəsb edir, belə ki,</p>\r\n<p>b&ouml;y&uuml;k miqdarda vaxt stasionardan kənarda itirilir. İlk simptomlar meydana &ccedil;ıxdığı andan təcili</p>\r\n<p>yardım xidmətinə zəng edilən ana kimi olan zaman kəsiyi, xəstəxanaya gədərki mərhələdə</p>\r\n<p>ləngimələrin əhəmiyyətli amillərindən biridir. Tibbi yardım &uuml;&ccedil;&uuml;n m&uuml;raciətlərin ləngiməsinin</p>\r\n<p>başlıca səbəb-ləri yalnız insultun simptomlarından məlumatı olmamaq və onların ciddiliyini dərk</p>\r\n<p>etməmək deyil, həm də xəstəliyin inkar olunması və simptomların &ouml;z-oz&uuml;nə ke&ccedil;ib getməsi</p>\r\n<p>&uuml;midinin olma-sıdır. Bu o deməkdir ki, insultun simptomlarının duyulmasının əhaliyə</p>\r\n<p>&ouml;yrədilməsi və insanların kəskin insulta m&uuml;nasibətinin dəyişməsi, simptomların meydana</p>\r\n<p>&ccedil;ıxmasının başlanmasından təcili yardımla kontakta kimi olan m&uuml;ddətin azalmasına səbəb ola</p>\r\n<p>&ldquo;İnsult Məktəbinin&rdquo; MƏŞĞƏLƏ PROQRAMI</p>\r\n<p>1. INSULTUN RİSK AMİLLƏRİ</p>\r\n<p>2. İNSULTUN ERKƏN DİAQNOSTİKASI</p>\r\n<p>3. XƏSTƏXANAYAGƏDƏRKİ TİBBİ YARDIMIN X&Uuml;SUSİYYƏTLƏRİ</p>\r\n<p>4. PARAKLİNİK İNSTRUMENTAL M&Uuml;AYİNƏ &Uuml;SULLARIN İNTERPRETASİYASI</p>\r\n<p>5. NEYROVİZUALİZASİYA N&Ouml;VLƏRİN SE&Ccedil;İM QAYDALARI</p>\r\n<p>6. QOSPİTAL MƏRHƏLƏDƏ M&Uuml;ALİCƏ</p>\r\n<p>7. İNSULT XƏSTƏLƏRİN BƏRPA D&Ouml;VR&Uuml;NƏ AİD TƏDBİRLƏR</p>', 59, 'az', 4, 1, NULL, 0, ''),
(3, 'İnsult məktəbi Azərbaycanda', 643, 'ru/insult/school', 0, 270, NULL, 59, 'ru', 4, 1, NULL, 0, ''),
(3, 'İnsult məktəbi Azərbaycanda', 644, 'en/insult/school', 0, 270, NULL, 59, 'en', 4, 1, NULL, 0, ''),
(3, 'Dərmansız müalicə', 645, 'az/drug/treatment', 0, 271, '<p>Tarixən belə hesab olunurdu ki, sağlamlıq haqqında b&uuml;t&uuml;n biliklər yalnız tibbə məxsusdur və</p>\r\n<p>burda istifadə olunur. Bu səbəbdən də sağlamlığı xəstəliyin olmaması ilə əlaqələndirirdilər.</p>\r\n<p>Amma canlı materiya haqda m&uuml;asir təsəvv&uuml;rlər bizim sağlamlığa aid biliklərimizi daha da</p>\r\n<p>genişləndirib. İnsa-nın bədənində daimi bərpa prosessləri gedir. Orqanizm yaşayır və daimi</p>\r\n<p>ətraf m&uuml;hidin təsirinə məruz olur &ndash; insan yaşadığı m&uuml;hitdən ayrılmazdır. Bəs nə &uuml;&ccedil;&uuml;n eyni</p>\r\n<p>m&uuml;hitdə bəzi insanlar &ouml;z bacarıqlarını, potensialını reallaşdıra bilirlər, ancaq başqaları</p>\r\n<p>g&uuml;cl&uuml; immuniteta, m&uuml;qavimətə malik deyirlər? İnsan bədəni k&uuml;lli miqdarda və n&ouml;vdə olan</p>\r\n<p>h&uuml;ceyrələrdən ibarətdir. H&uuml;ceyrə orqanizmin mikrokopiyasıdır. Bildiyimiz kimi h&uuml;ceyrədə</p>\r\n<p>daim biokimiyəvi prosesslər m&ouml;vc&uuml;ddur. Ancaq biokim-iyəvi və biofiziki prosesslər biri</p>\r\n<p>birindən ayrılmaz gedir. Hələ 20-ci əsrin 2-ci yarısında, uzun m&uuml;ddət Almaniyada calışan,</p>\r\n<p>məşhur fizik Gerbert Fr&ouml;lix ilk dəfə subuta yetirib ki hər bir h&uuml;ceyrənin elektromaqnit</p>\r\n<p>sahəsi var. Beləki, h&uuml;ceyrədə gedən canlı prosesslərin biofiziki əksi millimetr diapazonlu</p>\r\n<p>elektromaqnit sahədir, başqa s&ouml;zlə hər canlı h&uuml;ceyrə millimetr diapazonunda dalğa</p>\r\n<p>şualandırır. Məhs bu diapazonda h&uuml;ceyrələr fəaliyyət g&ouml;stərirlər və biri birini</p>\r\n<p>məlumatlandırırlar. Hər toxumanın h&uuml;ceyrələri &ouml;z&uuml;nə məxsus elektromaqnit tezlikdə şua</p>\r\n<p>verirlər. H&uuml;ceyrə membranın reseptorları hər bir qıcığı qəbul edərkən dəyişməyə məruz</p>\r\n<p>qalırlar, bunun nəticəsində z&uuml;lal strukturu dəyişilir və h&uuml;ceyrənin elektromaqnit sahəsi</p>\r\n<p>dəyişməli olur. Beləliklə, h&uuml;ceyrələr millimetr dalğa vasitəsilə daim məlumat &ouml;t&uuml;rullər sinir</p>\r\n<p>sisteminə, baş beynə &ndash; bu bizim orqanizmin əsas idarəedən mərkəzidir. Bundan başqa,</p>\r\n<p>millimetr dalğası bu və ya digər patogen prosess nəticəsində dəyişilən h&uuml;ceyrənin</p>\r\n<p>membranın bərpasını təmin etməyə qadirdir. Əgər h&uuml;ceyrə xəstəlik, intoksikasiya, yaş və</p>\r\n<p>başqa səbəblərdən deformasiyaya uğrayıbsa, millimetr dalğası vasitəsilə onu bərpa etmək</p>\r\n<p>iqtidarındadır. Hər bir &uuml;s&uuml;lun başinda m&uuml;təxəssis dəyanır: m&uuml;asir texnologiyalar yaradan</p>\r\n<p>cihazların d&uuml;zg&uuml;n istifadəsindən hər şey asılıdır! Sağlam həyat yaşamaq &uuml;&ccedil;&uuml;n insanı əhatə</p>\r\n<p>edən təbiət, təmiz hava, su, d&uuml;zg&uuml;n qidalanma, ətrafdaki insanlar və sağlam həyat tərzi</p>\r\n<p>vacibdir. Ancaq, biz 21-ci əsrin ritmində yaşayırıq: &Ccedil;ox zaman g&uuml;n ərzində işlətdiyimiz</p>\r\n<p>enerji və beynimizin aktivliyi tam bərpa olmur. Ona g&ouml;rə hər birimiz reallığa tabe olub</p>\r\n<p>profilaktik m&uuml;alicə haqda d&uuml;ş&uuml;nməliyik. Təqdim etdiyimiz dərmansız m&uuml;alicə &uuml;sulu bu</p>\r\n<p>məqamda əvəzsizdir.</p>', 60, 'az', 59, 1, NULL, 0, ''),
