INSERT INTO `vl1_menumodel` (`type`, `menuItemTitle`, `id`, `link`, `modelId`, `treeItemId`, `content`, `r_id`, `lang_id`, `pageId`, `visible`, `forLoggedIn`, `pageType`, `image`) VALUES
(1, 'Azərbaycan Respublikasının Cəzaların İcrası Məcəlləsi', 529, NULL, 0, 203, '<p style="text-align: left;" align="center"><strong>Azərbaycan Respublikasının Cəzaların İcrası Məcəlləsi</strong></p>\r\n<p style="text-align: left;" align="center"><strong><em>(2012-ci il mayın 12-dək edilmiş əlavə və dəyişikliklərlə)</em></strong></p>\r\n<p style="text-align: left;" align="center"><strong>(Bu Məcəllə Azərbaycan Respublikasının 14 iyul 2000-ci il tarixli 908-IQ n&ouml;mrəli Qanunu ilə təsdiq edilmişdir)</strong></p>\r\n<p style="text-align: left;"><strong><em>&Uuml;mumi hissə</em></strong></p>\r\n<p style="text-align: left;"><strong><em>I b&ouml;lmə. Cəzaların icrası qanunvericiliyinin əsas m&uuml;ddəaları</em></strong></p>\r\n<p style="text-align: left;"><strong>I fəsil. &Uuml;mumi m&uuml;ddəalar</strong></p>\r\n<p style="text-align: left;"><strong><em>Maddə 1. Cəzaların icrası qanunvericiliyi</em></strong></p>\r\n<p style="text-align: left;">Məhkəmə tərəfindən təyin edilən cəzaların icrası və &ccedil;əkilməsi qaydalarını və şərtlərini m&uuml;əyyən edən cəzaların icrası qanunvericiliyi bu Məcəllədən, Azərbaycan Respublikasının digər normativ h&uuml;quqi aktlarından və Azərbaycan Respublikasının tərəfdar &ccedil;ıxdığı beynəlxalq m&uuml;qavilələrdən ibarətdir.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 2. Cəzaların İcrası Məcəlləsinin məqsəd və vəzifələri</em></strong></p>\r\n<p style="text-align: left;">2.1. Bu Məcəllənin məqsədi məhkumları islah etmək, həm məhkumlar, həm də başqa şəxslər tərəfindən yeni cinayətlər t&ouml;rədilməsinin qarşısını almaqdır.</p>\r\n<p style="text-align: left;">2.2. Bu Məcəllənin vəzifəsi cəzaların icrası və &ccedil;əkilməsi qaydalarını və şərtlərini tənzimləməkdən, məhkumların islah edilmə vasitələrini m&uuml;əyyən etməkdən, məhkumların h&uuml;quq və azadlıqlarını, qanuni mənafelərini qorumaqdan ibarətdir.</p>\r\n<p style="text-align: left;">2.3. Bu Məcəllə qarşısına qoyulmuş vəzifələrə uyğun olaraq <a href="file:///C:/Users/Rena.HQ/AppData/Local/Microsoft/Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/%D0%90%D0%B4%D0%BC%D0%B8%D0%BD%D0%B8%D1%81%D1%82%D1%80%D0%B0%D1%82%D0%BE%D1%80/Application%20Data/1446">Azərbaycan Respublikasının Cinayət Məcəlləsində</a> nəzərdə tutulmuş cəzaların icrasının &uuml;mumi m&uuml;ddəaları və prinsiplərini, digər cinayət-h&uuml;quqi xarakterli tədbirləri, məhkumlara islah edilmə vasitələrinin tətbiqini, cəzaların icrası və &ccedil;əkilməsi qaydalarını və şərtlərini, məhkumların h&uuml;quqi vəziyyətini, cəzanı icra edən m&uuml;əssisə və orqanların fəaliyyət qaydasını, məhkumların islah edilməsində <a title="&ouml;z\r\n\r\n\r\n\r\n\r\n  səlahiyyəti daxilində Azərbaycan Respublikasının Ədliyyə Nazirliyi və intizam xarakterli hərbi hissədə saxlama cəza n&ouml;v&uuml;nə aid hissədə Azərbaycan Respublikasının M&uuml;dafiə Nazirliyi; Nax&ccedil;ıvan Muxtar Respublikası ərazisində Nax&ccedil;ıvan Muxtar Resp" href="file:///C:/Users/Rena.HQ/AppData/Local/Microsoft/Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/%D0%90%D0%B4%D0%BC%D0%B8%D0%BD%D0%B8%D1%81%D1%82%D1%80%D0%B0%D1%82%D0%BE%D1%80/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanlarının</a>, digər m&uuml;əssisə, idarə və ya təşkilatların ictimai birliklərin və vətəndaşların iştirakını, məhkumların cəza &ccedil;əkməkdən azad edilməsi və cəza &ccedil;əkməkdən azad edilmiş şəxslərə yardım edilməsi qaydalarını m&uuml;əyyənləşdirir.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 3. Cəzaların İcrası Məcəlləsi və beynəlxalq h&uuml;quqi aktlar</em></strong></p>\r\n<p style="text-align: left;">3.1. Bu Məcəllə cəzaların icrasına və məhkumlarla rəftar qaydalarına dair Azərbaycan Respublikasının tərəfdar &ccedil;ıxdığı beynəlxalq h&uuml;quqi aktları nəzərə alır.</p>\r\n<p style="text-align: left;">3.2. Azərbaycan Respublikasının tərəfdar &ccedil;ıxdığı beynəlxalq m&uuml;qavilələrdə bu Məcəllənin m&uuml;ddəalarından fərqli normalar m&uuml;əyyən edilərsə, beynəlxalq m&uuml;qavilələrin qaydaları tətbiq edilir.</p>\r\n<p style="text-align: left;">3.3. Bu Məcəllə <a href="file:///C:/Users/Rena.HQ/AppData/Local/Microsoft/Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/%D0%90%D0%B4%D0%BC%D0%B8%D0%BD%D0%B8%D1%81%D1%82%D1%80%D0%B0%D1%82%D0%BE%D1%80/Application%20Data/271">Azərbaycan Respublikasının Konstitusiyasına</a>, m&uuml;vafiq qanunlarına və beynəlxalq h&uuml;ququn prinsip və normalarına uyğun olaraq məhkumlarla rəftarda işgəncələrə, digər qeyri-insani hərəkətlərə və şəxsiyyətin al&ccedil;aldılmasına yol verilməməsinə əsaslanır.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 4. Cəzaların icrasına dair normativ h&uuml;quqi aktlar</em></strong></p>\r\n<p style="text-align: left;"><a title="&ouml;z\r\n\r\n\r\n\r\n\r\n  səlahiyyəti daxilində Azərbaycan Respublikasının Nazirlər Kabineti, Azərbaycan Respublikasının Ədliyyə Nazirliyi və Azərbaycan Respublikasının M&uuml;dafiə Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="file:///C:/Users/Rena.HQ/AppData/Local/Microsoft/Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/%D0%90%D0%B4%D0%BC%D0%B8%D0%BD%D0%B8%D1%81%D1%82%D1%80%D0%B0%D1%82%D0%BE%D1%80/Application%20Data/4044">M&uuml;vafiq icra hakimiyyəti orqanları</a> cəzaların icrası məsələlərinə dair qanunvericiliyə əsaslanan normativ h&uuml;quqi aktlar qəbul edə bilərlər.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 5. Cəzaların İcrası Məcəlləsinin məkana və zamana g&ouml;rə q&uuml;vvəsi</em></strong></p>\r\n<p style="text-align: left;">5.1. Bu Məcəllə Azərbaycan Respublikasının b&uuml;t&uuml;n ərazisində tətbiq edilir.</p>\r\n<p style="text-align: left;">5.2. Cəzaların icrası, habelə məhkumlara islah edilmə vasitələrinin tətbiqi və cəza &ccedil;əkməkdən azad edilmiş şəxslərə yardım edilməsi onların icrası zamanı q&uuml;vvədə olan qanunvericiliyə uyğun həyata ke&ccedil;irilir.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 6. Cəzaların icrasının əsasları</em></strong></p>\r\n<p style="text-align: left;">Cəzaların icrasının və məhkumlara cinayət-h&uuml;quqi xarakterli digər tədbirlərin tətbiqinin əsaslarını məhkəmənin qanuni q&uuml;vvəyə minmiş h&ouml;km&uuml;, yaxud məhkəmənin bu h&ouml;km&uuml; dəyişən qərarları, habelə amnistiya və ya əfvetmə aktları təşkil edir.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 7. Cəzaların icrası qanunvericiliyinin prinsipləri</em></strong></p>\r\n<p style="text-align: left;">Cəzaların icrası qanunvericiliyi qanun&ccedil;uluq, humanizm, demokratizm, məhkumların qanun qarşısında bərabərliyi, cəzaların icrasının fərdiləşdirilməsi, məcburetmə tədbirlərindən və məhkumların islah edilmə vasitələrindən məqsədəuyğun istifadə edilməsi, onlarda qanuna itaətin həvəsləndirilməsi və cəzanın islahedici təsirinin artırılması prinsiplərinə əsaslanır.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 8. Məhkumların islah edilməsi və onun əsas vasitələri</em></strong></p>\r\n<p style="text-align: left;">8.1. Məhkumların islah edilməsi&mdash;onlarda qanuna itaətin həvəsləndirilməsi, insana, cəmiyyətə, əməyə, birgəyaşayış qayda və ənənələrinə h&ouml;rmət və ehtiramın formalaşdırılmasıdır.</p>\r\n<p style="text-align: left;">8.2. Məhkumların islah edilməsinin əsas vasitələri aşağıdakılardır:</p>\r\n<p style="text-align: left;">8.2.1. cəzanın icrasının və &ccedil;əkilməsinin m&uuml;əyyən edilmiş qaydası (rejim);</p>\r\n<p style="text-align: left;">8.2.2. tərbiyə işi;</p>\r\n<p style="text-align: left;">8.2.3. ictimai faydalı əmək;</p>\r\n<p style="text-align: left;">8.2.4. &uuml;mumi təhsil;</p>\r\n<p style="text-align: left;">8.2.5.<em> ilk peşə-ixtisas təhsili</em> və peşə hazırlığı; (20)</p>\r\n<p style="text-align: left;">8.2.6. ictimai təsir.</p>\r\n<p style="text-align: left;">8.3. Məhkumlara islah edilmə vasitələri cəzanın n&ouml;v&uuml;, t&ouml;rədilmiş cinayətin ictimai təhl&uuml;kəlilik dərəcəsi və xarakteri, məhkumun şəxsiyyəti və davranışı nəzərə alınmaqla tətbiq edilir.</p>\r\n<p style="text-align: left;"><strong>II fəsil. Məhkumların h&uuml;quqi vəziyyəti</strong></p>\r\n<p style="text-align: left;"><strong><em>Maddə 9. Məhkumların h&uuml;quqi vəziyyətinin əsasları</em></strong></p>\r\n<p style="text-align: left;">9.1. Azərbaycan Respublikası məhkumların h&uuml;quq və azadlıqlarına, qanuni mənafelərinə h&ouml;rmət edir və qoruyur, məhkumların islah edilmə vasitələrinin tətbiq edilməsinin qanuniliyini, cəzaların icrası zamanı onların h&uuml;quqi m&uuml;dafiəsini və şəxsi təhl&uuml;kəsizliyini təmin edir.</p>\r\n<p style="text-align: left;">9.2. Cəzaların icrası zamanı məhkumlara dair Azərbaycan Respublikasının Cinayət, Cinayət-Prosessual məcəllələri, bu Məcəllə və digər qanunvericilik aktları ilə m&uuml;əyyən edilmiş məhdudlaşdırmalar və istisnalarla onların Azərbaycan Respublikası vətəndaşlarına mənsub olan h&uuml;quq və azadlıqlarına təminat verilir. Məhkumlar qanunvericiliklə m&uuml;əyyən edilmiş hallar istisna olmaqla, h&uuml;quq və azadlıqlardan irəli gələn vəzifələr daşıyırlar.</p>\r\n<p style="text-align: left;">9.3. Əcnəbi və ya vətəndaşlığı olmayan məhkum şəxslər Azərbaycan Respublikasının Cinayət, Cinayət-Prosessual məcəllələri, bu Məcəllə və digər qanunvericilik aktları ilə m&uuml;əyyən edilmiş məhdudlaşdırmalar istisna edilməklə, əcnəbilərin və vətəndaşlığı olmayan şəxslərin h&uuml;quqi vəziyyətinə dair qanunvericilikdə və Azərbaycan Respublikasının tərəfdar &ccedil;ıxdığı beynəlxalq m&uuml;qavilələrdə m&uuml;əyyən edilmiş h&uuml;quqlardan istifadə edir və vəzifələr daşıyırlar.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n<p style="text-align: left;">9.4. Məhkumların h&uuml;quq və vəzifələri, habelə bu h&uuml;quq və vəzifələrin məhdudlaşdırılması cəza n&ouml;v&uuml;n&uuml;n icrası və &ccedil;əkilməsi qaydaları və şərtləri nəzərə alınmaqla bu Məcəllə və digər normativ h&uuml;quqi aktlarla m&uuml;əyyənləşdirilir.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 10. Məhkumların əsas h&uuml;quqları</em></strong></p>\r\n<p style="text-align: left;">10.1. Cəzaların icrası və &ccedil;əkilməsi zamanı məhkumların qanunvericiliklə nəzərdə tutulmuş h&uuml;quq və azadlıqları təmin olunur. Məhkumların h&uuml;quq və azadlıqları yalnız qanunvericiliklə m&uuml;əyyən olunmuş qaydada məhdudlaşdırıla bilər.</p>\r\n<p style="text-align: left;">10.2. Cəzaların icrası və &ccedil;əkilməsi zamanı məhkumların aşağıdakı h&uuml;quqları vardır:</p>\r\n<p style="text-align: left;">10.2.1. cəzanı insan şəxsiyyətinə h&ouml;rməti təmin edən şəraitdə &ccedil;əkmək;</p>\r\n<p style="text-align: left;">10.2.2. ictimai faydalı əməklə məşğul olmaq;</p>\r\n<p style="text-align: left;"><em>10.2.2-1. bədən tərbiyəsi və idmanla məşğul olmaq;</em></p>\r\n<p style="text-align: left;">10.2.3. istirahət etmək;</p>\r\n<p style="text-align: left;">10.2.4. pensiya və ya sosial m&uuml;avinət almaq;</p>\r\n<p style="text-align: left;">10.2.5. İlk tibbi yardım daxil olmaqla, tibbi rəydən asılı olaraq ambulator və ya stasionar şəraitdə tibbi yardım almaq;</p>\r\n<p style="text-align: left;">10.2.6. &ouml;z h&uuml;quq və vəzifələri barədə, o c&uuml;mlədən məhkəmə tərəfindən təyin edilmiş cəzanın icrası qaydası və şərtləri barəsində məlumat almaq;</p>\r\n<p style="text-align: left;">10.2.7. h&uuml;quq və azadlıqlarının m&uuml;dafiəsi &uuml;zrə Azərbaycan Respublikasının qanunvericiliyi ilə m&uuml;əyyən edilmiş qaydada təklif, ərizə və şikayətlə d&ouml;vlət dilində və ya başqa dildə m&uuml;raciət etmək, zəruri hallarda isə bu məqsədlər &uuml;&ccedil;&uuml;n cəzanı icra edən m&uuml;əssisə və ya orqan tərəfindən təqdim edilən tərc&uuml;mə&ccedil;i xidmətindən istifadə etmək;</p>\r\n<p style="text-align: left;"><em>10.2.7-1. cəzanın icrası və &ccedil;əkilməsi ilə əlaqədar barəsində qəbul edilən qərarlara dair &ouml;z rəyini bildirmək və ya onlardan şikayət etmək;</em></p>\r\n<p style="text-align: left;">10.2.8. dini ayinlərin icra edilməsində iştirak etmək;</p>\r\n<p style="text-align: left;">10.2.9. h&uuml;quqi yardım almaq;</p>\r\n<p style="text-align: left;"><em>10.2.9-1. psixoloji yardım almaq;</em></p>\r\n<p style="text-align: left;">10.2.10. təhsil almaq və peşə hazırlığı ke&ccedil;mək;</p>\r\n<p style="text-align: left;">10.2.11. əcnəbi, vətəndaşlığı olmayan və ya qa&ccedil;qın statusuna malik olan məhkumların qısa m&uuml;ddətdə şəxsi məsələlər &uuml;zrə &ouml;z d&ouml;vlətlərinin Azərbaycan Respublikasındakı diplomatik və konsulluq n&uuml;mayəndəlikləri ilə və ya onlara himayədarlığı &ouml;z &uuml;zərinə g&ouml;t&uuml;rm&uuml;ş milli, yaxud beynəlxalq təşkilatlarla əlaqəyə girmək;</p>\r\n<p style="text-align: left;">10.2.12. cəzanın &ccedil;əkilməsinin təxirə salınması və ya onun ləğv edilməsi, xəstəliyə g&ouml;rə cəzanı &ccedil;əkməkdən azad edilməsi, ittiham h&ouml;km&uuml;n&uuml;n icrası m&uuml;ddəti ilə əlaqədar cəza &ccedil;əkməkdən azad edilməsi, cəzadan şərti olaraq vaxtından əvvəl azad edilməsi, cəzanın &ccedil;əkilməmiş hissəsinin daha y&uuml;ng&uuml;l cəza n&ouml;v&uuml; ilə əvəz edilməsi, cəza&ccedil;əkmə m&uuml;əssisəsinin n&ouml;v&uuml;n&uuml;n dəyişdirilməsi, amnistiyanın tətbiq edilməsi, m&uuml;alicə m&uuml;əssisəsində saxlanma vaxtının cəza&ccedil;əkmə m&uuml;ddətinə daxil edilməsi, məhkumluğun vaxtından əvvəl g&ouml;t&uuml;r&uuml;lməsi &uuml;&ccedil;&uuml;n ərizə ilə məhkəməyə, əfv edilmək &uuml;&ccedil;&uuml;n isə Azərbaycan Respublikasının Prezidentinə bilavasitə və ya qanuni n&uuml;mayəndələrinin vasitəsilə, habelə yetkinlik yaşına &ccedil;atmayan və ya fiziki q&uuml;surlardan, yaxud psixi xəstəlikdən əzab &ccedil;əkən məhkumlar barəsində isə onların m&uuml;dafiə&ccedil;ilərinin və qanuni n&uuml;mayəndələrinin vasitəsilə m&uuml;raciət etmək.</p>\r\n<p style="text-align: left;">10.3. Cəzaları icra edən m&uuml;əssisə və ya orqanın iş&ccedil;i heyəti məhkumlara qarşı m&uuml;nasibətdə nəzakətli olmalıdır. Məhkumlar qəddar və ya insan ləyaqətini al&ccedil;aldan rəftara məruz qalmamalıdırlar. Məhkumlara məcburetmə tədbirləri yalnız qanuna əsasən tətbiq edilə bilər.</p>\r\n<p style="text-align: left;">10.4. Məhkumlar onların həyat və sağlamlıqlarını təhl&uuml;kə altında qoya bilən tibbi və ya başqa təcr&uuml;bələrə məruz qala bilməzlər.</p>\r\n<p style="text-align: left;">10.5. Məhkumların h&uuml;quqlarının həyata ke&ccedil;irilməsi qaydası bu Məcəllə və digər normativ h&uuml;quqi aktlarla m&uuml;əyyən edilir.</p>\r\n<p style="text-align: left;">10.6. Məhkumların h&uuml;quqlarının həyata ke&ccedil;irilməsi zamanı normativ h&uuml;quqi aktların, fiziki və h&uuml;quqi şəxslərin h&uuml;quqlarının pozulmasına yol verilmir. (<a href="file:///C:/Users/Rena.HQ/AppData/Local/Microsoft/Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/%D0%90%D0%B4%D0%BC%D0%B8%D0%BD%D0%B8%D1%81%D1%82%D1%80%D0%B0%D1%82%D0%BE%D1%80/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p style="text-align: left;"><strong><em>Maddə 11. Məhkumların əsas vəzifələri</em></strong></p>\r\n<p style="text-align: left;">11.1. Məhkumlar Azərbaycan Respublikasının vətəndaşları &uuml;&ccedil;&uuml;n qanunvericiliklə m&uuml;əyyən olunmuş vəzifələri yerinə yetirməli və cəmiyyətdə qəbul edilmiş m&uuml;vafiq davranış normalarına riayət etməlidirlər.</p>\r\n<p style="text-align: left;">11.2. Məhkumlar bu Məcəllənin, habelə ona uyğun qəbul edilmiş digər normativ h&uuml;quqi aktların tələblərinə riayət etməlidirlər.</p>\r\n<p style="text-align: left;">11.3. Məhkumlar cəzanı icra edən m&uuml;əssisə və ya orqanın iş&ccedil;i heyətinin qanuni tələblərini yerinə yetirməlidirlər.</p>\r\n<p style="text-align: left;">11.4. Məhkumlar cəzanı icra edən m&uuml;əssisənin iş&ccedil;i heyətinə və cəza&ccedil;əkmə m&uuml;əssisəsinə baş &ccedil;əkən şəxslərə, habelə digər məhkumlara qarşı nəzakətlə davranmalıdırlar.</p>\r\n<p style="text-align: left;">11.5. Məhkumlar &uuml;zərinə d&uuml;şən vəzifələri, habelə cəzanı icra edən m&uuml;əssisə və ya orqanın iş&ccedil;i heyətinin qanuni tələblərini yerinə yetirmədikdə, qanunla m&uuml;əyyən edilmiş qaydada məsuliyyətə cəlb olunurlar.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 12. Məhkumların şəxsi təhl&uuml;kəsizlik h&uuml;ququ</em></strong></p>\r\n<p style="text-align: left;">12.1. Məhkumlar şəxsi təhl&uuml;kəsizlik h&uuml;ququna malikdirlər.</p>\r\n<p style="text-align: left;">12.2. <em>M&uuml;əyyən</em> m&uuml;ddətə azadlıqdan məhrum etmə və ya &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəza &ccedil;əkən məhkumun həyatına, sağlamlığına, yaxud şəxsiyyətinə qarşı digər məhkumlar tərəfindən və ya başqa şəxslər tərəfindən təhl&uuml;kə yaranarsa, o, x&uuml;susi m&uuml;əssisənin və ya cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyətinə təhl&uuml;kəsizliyini təmin etmək &uuml;&ccedil;&uuml;n ərizə və ya şifahi m&uuml;raciət etmək h&uuml;ququna malikdir.(22)</p>\r\n<p style="text-align: left;">12.3. Cəzanı icra edən cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti təhl&uuml;kənin aradan qaldırılması &uuml;&ccedil;&uuml;n məhkumun təhl&uuml;kəsiz yerə k&ouml;&ccedil;&uuml;r&uuml;lməsi və ya başqa tədbirlər g&ouml;r&uuml;lməsi barədə qərar qəbul edir. (22)</p>\r\n<p style="text-align: left;"><strong><em>Maddə 13. Məhkumların vicdan və dini etiqad azadlığının təmin edilməsi</em></strong></p>\r\n<p style="text-align: left;">13.1. Məhkumların vicdan və dini etiqad azadlığına təminat verilir. Onlar istədikləri dinə etiqad etmək və ya etməmək h&uuml;ququna malikdirlər.</p>\r\n<p style="text-align: left;">13.2. Məntəqə tipli cəza&ccedil;əkmə <em>m&uuml;əssisələrində</em> məhkumların xahişi ilə onlara cəzanın icra edildiyi <em>inzibati ərazi vahidi</em> h&uuml;dudları daxilində ibadət yerlərinə getməyə icazə verilə bilər. (22)</p>\r\n<p style="text-align: left;">13.3. M&uuml;əyyən edilmiş qaydada d&ouml;vlət qeydiyyatına alınmış dini birliklərin din xadimləri məhkumların xahişi ilə cəza&ccedil;əkmə m&uuml;əssisələrinə dəvət olunurlar. Məhkumlara cəza&ccedil;əkmə m&uuml;əssisələrində dini ayinləri yerinə yetirməyə, dini ləvazimat və ədəbiyyatdan istifadə etməyə icazə verilir. Bu məqsəd &uuml;&ccedil;&uuml;n cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti m&uuml;vafiq yer ayırır.</p>\r\n<p style="text-align: left;">13.4. Din xadimlərinin şəxsi təhl&uuml;kəsizliyi təmin olunmaqla, onlar x&uuml;susi rejimli cəza&ccedil;əkmə m&uuml;əssisələrinin birnəfərlik kameralarında, intizam və cərimə təcridxanalarında, kamera tipli otaqlarda saxlanılan məhkumların yanına buraxıla bilərlər.</p>\r\n<p style="text-align: left;">13.5. Ağır xəstəliyi ilə əlaqədar həyatı &uuml;&ccedil;&uuml;n təhl&uuml;kə yaranan məhkumların xahişi ilə din xadimləri zəruri dini ayinləri yerinə yetirmək &uuml;&ccedil;&uuml;n cəza&ccedil;əkmə m&uuml;əssisələrinə dəvət oluna bilərlər. <a href="file:///C:/Users/Rena.HQ/AppData/Local/Microsoft/Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/%D0%90%D0%B4%D0%BC%D0%B8%D0%BD%D0%B8%D1%81%D1%82%D1%80%D0%B0%D1%82%D0%BE%D1%80/Application%20Data/temp58772.htm#i3#i3">(3)</a></p>\r\n<p style="text-align: left;"><strong><em>Maddə 14. Məhkumların m&uuml;raciətləri və onlara baxılması qaydaları</em></strong></p>\r\n<p style="text-align: left;">14.1. Məhkumların təklif, ərizə və şikayətləri yazılı və şifahi verilə bilər.</p>\r\n<p style="text-align: left;">14.2. Azadlıqdan məhrum edilmiş şəxslərin bu Məcəllənin <em>10.2.12-ci</em> maddəsində g&ouml;stərilən təşkilatlara &uuml;nvanlanmış təklif, ərizə və şikayətləri cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti tərəfindən g&ouml;ndərilir. Digər cəza n&ouml;vlərinə məhkum edilmiş şəxslər təklif, ərizə və şikayətlərini sərbəst g&ouml;ndərirlər.</p>\r\n<p style="text-align: left;">14.3. Məhkumların cəzanı icra edən m&uuml;əssisə və ya orqanın vəzifəli şəxslərinin qərar və hərəkətlərinə dair təklif, ərizə və şikayətləri həmin qərarların və hərəkətlərin icrasını dayandırmır.</p>\r\n<p style="text-align: left;">14.4. Məhkumların təklif, ərizə və şikayət g&ouml;ndərdiyi orqan və ya vəzifəli şəxslər onlara qanunvericiliklə m&uuml;əyyən edilmiş m&uuml;ddətdə baxmalı və nəticəsi barədə məhkuma yazılı məlumat verməlidirlər.</p>\r\n<p style="text-align: left;">14.5. Cəzanı icra edən m&uuml;əssisə və ya orqanın vəzifəli şəxsləri məhkumları m&uuml;əyyən edilmiş və onlara bildirmiş g&uuml;nlərdə və saatlarda qəbul edir. <a href="file:///C:/Users/Rena.HQ/AppData/Local/Microsoft/Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/%D0%90%D0%B4%D0%BC%D0%B8%D0%BD%D0%B8%D1%81%D1%82%D1%80%D0%B0%D1%82%D0%BE%D1%80/Application%20Data/temp58772.htm#i1#i1">(1)</a></p>\r\n<p style="text-align: left;"><strong>III fəsil. Cəzanı icra edən m&uuml;əssisə və orqanlar, onların fəaliyyətinə nəzarət</strong></p>\r\n<p style="text-align: left;"><strong><em>Maddə 15. Cəzanı icra edən m&uuml;əssisə və orqanlar</em></strong></p>\r\n<p style="text-align: left;">15.1. İctimai işlər n&ouml;v&uuml;ndə cəza məhkumun yaşadığı, cərimə və əmlak m&uuml;sadirəsi n&ouml;v&uuml;ndə cəzalar isə h&ouml;km&uuml; &ccedil;ıxaran məhkəmənin, həm&ccedil;inin məhkumun işlədiyi və ya onun əmlakının olduğu yerin icra məmuru tərəfindən icra edilir.</p>\r\n<p style="text-align: left;">15.2. Azərbaycan Respublikasının h&uuml;dudlarından kənara məcburi &ccedil;ıxarma n&ouml;v&uuml;ndə cəza əsas cəzanı icra edən m&uuml;əssisə və ya orqanın təqdimatı ilə qanunvericilikdə nəzərdə tutulmuş qaydada <a title="Azərbaycan Respublikasının Daxili İşlər Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="file:///C:/Users/Rena.HQ/AppData/Local/Microsoft/Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/%D0%90%D0%B4%D0%BC%D0%B8%D0%BD%D0%B8%D1%81%D1%82%D1%80%D0%B0%D1%82%D0%BE%D1%80/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanı</a> tərəfindən icra edilir.</p>\r\n<p style="text-align: left;">15.3. M&uuml;əyyən vəzifə tutma və ya m&uuml;əyyən fəaliyyətlə məşğul olma h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəza məhkumun yaşayış yeri &uuml;zrə icra məmuru və ya cəza&ccedil;əkmə m&uuml;əssisələrinin m&uuml;diriyyəti tərəfindən icra edilir. M&uuml;əyyən vəzifə tutma və ya m&uuml;əyyən fəaliyyətlə məşğul olma h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəza barəsində h&ouml;km&uuml;n tələbləri məhkumun iş yeri &uuml;zrə, həm&ccedil;inin qanuna uyğun olaraq fəaliyyətin m&uuml;vafiq n&ouml;v&uuml; ilə məşğul olmağa icazəni ləğv etmək səlahiyyəti olan orqanlar həyata ke&ccedil;irir.</p>\r\n<p style="text-align: left;">15.4. Nəqliyyat vasitəsini idarəetmə h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəzanı nəqliyyat vasitəsini idarəetmə h&uuml;ququnu verən orqan icra edir.</p>\r\n<p style="text-align: left;">15.5. İslah işləri n&ouml;v&uuml;ndə cəzanı icra məmuru icra edir.</p>\r\n<p style="text-align: left;">15.6. <em>M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaların icrasını m&uuml;vafiq cəza&ccedil;əkmə m&uuml;əssisələri həyata ke&ccedil;irirlər</em>.(22)</p>\r\n<p style="text-align: left;">15.7. X&uuml;susi və ya hərbi r&uuml;tbədən, fəxri addan və d&ouml;vlət təltifindən məhrum etmə n&ouml;v&uuml;ndə cəza h&ouml;km&uuml; &ccedil;ıxaran məhkəmə tərəfindən icra edilir. X&uuml;susi və ya hərbi r&uuml;tbədən, fəxri addan və d&ouml;vlət təltifindən məhrum etmə barəsində h&ouml;km&uuml;n tələbləri həmin r&uuml;tbəni, fəxri adı və ya d&ouml;vlət təltifini verən orqan tərəfindən həyata ke&ccedil;irilir.</p>\r\n<p style="text-align: left;">15.8. Hərbi xidmət &uuml;zrə məhdudlaşdırma n&ouml;v&uuml;ndə cəza hərbi qulluq&ccedil;uların xidmət yeri &uuml;zrə hərbi hissə komandiri, intizam xarakterli hərbi hissədə saxlama n&ouml;v&uuml;ndə cəza isə bu məqsədlər &uuml;&ccedil;&uuml;n yaradılmış hərbi hissənin komandiri tərəfindən icra edilir.</p>\r\n<p style="text-align: left;">15.9. Şərti məhkum edilmiş məhkumlara nəzarəti icra məmurları həyata ke&ccedil;irir.</p>\r\n<p style="text-align: left;">15.10<em>. İntizam</em> xarakterli hərbi hissədə saxlama, m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum edilmiş şəxslərin cəzalarının icrası və &ccedil;əkilməsi qaydalarının həyata ke&ccedil;irilməsi mexanizmi, bu cəza n&ouml;vlərinin x&uuml;susiyyətlərinə uyğun olaraq <a title="&ouml;z səlahiyyəti daxilində Azərbaycan Respublikasının Ədliyyə Nazirliyi və intizam xarakterli hərbi hissədə saxlama cəza n&ouml;v&uuml;nə aid hissədə Azərbaycan Respublikasının M&uuml;dafiə Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="file:///C:/Users/Rena.HQ/AppData/Local/Microsoft/Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/%D0%90%D0%B4%D0%BC%D0%B8%D0%BD%D0%B8%D1%81%D1%82%D1%80%D0%B0%D1%82%D0%BE%D1%80/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanları</a> tərəfindən qəbul edilmiş Daxili İntizam Qaydaları ilə m&uuml;əyyən edilir. (22)</p>\r\n<p style="text-align: left;">15.11. Cəzaları icra edən m&uuml;əssisələr və ya orqanlar Azərbaycan Respublikasının qanunvericiliyi ilə m&uuml;əyyən edilmiş qaydada yaradılır və ya ləğv edilir.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 16. Cəzanın icra edildiyi yer barədə məlumat verilməsi</em></strong></p>\r\n<p style="text-align: left;">16.1. Cəzanı icra edən m&uuml;əssisə və ya orqanın vəzifəli şəxsləri məhkumun <em>razılığı ilə onun</em> ailəsinə və ya yaxın qohumlarından birinə<em>, yaxud məhkumun g&ouml;stərdiyi şəxsə</em> ona təyin olunmuş cəzanın icrası, cəzanın &ccedil;əkilmə yeri, məhkumun yerdəyişmələri və azad edilməsi barədə məlumat verməlidirlər.</p>\r\n<p style="text-align: left;">16.2. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəza &ccedil;əkən əcnəbi və ya vətəndaşlığı olmayan <em>şəxslərin razılığı ilə onlar</em> barəsində cəzanı icra edən m&uuml;əssisə və ya orqan onların d&ouml;vlətlərinin Azərbaycan Respublikasındakı diplomatik və ya konsulluq n&uuml;mayəndəliklərinə, habelə onlara himayədarlığı &ouml;z &uuml;zərinə g&ouml;t&uuml;rm&uuml;ş beynəlxalq təşkilatlara dərhal məlumat verir. (<a href="file:///C:/Users/Rena.HQ/AppData/Local/Microsoft/Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/%D0%90%D0%B4%D0%BC%D0%B8%D0%BD%D0%B8%D1%81%D1%82%D1%80%D0%B0%D1%82%D0%BE%D1%80/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p style="text-align: left;"><strong><em>Maddə 17. Məhkumlara tibbi xarakterli məcburi tədbirlərin tətbiq edilməsi</em></strong></p>\r\n<p style="text-align: left;">17.1. Məhkuma məhkəmə tərəfindən alkoqolizmə və ya narkomaniyaya, həm&ccedil;inin anlaqlılığı istisna etməyən psixi pozğuntuya g&ouml;rə tibbi xarakterli məcburi tədbirlər tətbiq edilərsə, bu tədbirlər cəza m&uuml;ddəti ərzində cəzanı icra edən m&uuml;əssisə və ya orqan tərəfindən həyata ke&ccedil;irilir.</p>\r\n<p style="text-align: left;">17.2. Məhkumun cəza m&uuml;ddəti ərzində alkoqolizmə və ya narkomaniyaya d&uuml;&ccedil;ar olması və ya anlaqlılığı istisna etməyən psixi pozuntusunun olması m&uuml;əyyən edildikdə, cəzanı icra edən m&uuml;əssisə və ya orqan həmin şəxs barəsində tibbi xarakterli məcburi tədbirlərin tətbiq edilməsi barədə məhkəməyə təqdimat verir.</p>\r\n<p style="text-align: left;">17.3. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum edilmiş şəxslərə tibbi xarakterli məcburi tədbirlər m&uuml;vafiq qanunvericiliyin və bu Məcəllənin tələbləri nəzərə alınmaqla tətbiq edilir.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 18. Cəzaların icrasına m&uuml;vafiq icra hakimiyyəti orqanlarının nəzarəti</em></strong></p>\r\n<p style="text-align: left;">Cəzaları icra edən m&uuml;əssisə və orqanın fəaliyyətinə nəzarəti <a title="&ouml;z səlahiyyəti daxilində Azərbaycan Respublikasının Ədliyyə Nazirliyi və intizam xarakterli hərbi hissədə saxlama cəza n&ouml;v&uuml;nə aid hissədə Azərbaycan Respublikasının M&uuml;dafiə Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="file:///C:/Users/Rena.HQ/AppData/Local/Microsoft/Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/%D0%90%D0%B4%D0%BC%D0%B8%D0%BD%D0%B8%D1%81%D1%82%D1%80%D0%B0%D1%82%D0%BE%D1%80/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanları</a> həyata ke&ccedil;irirlər. Bu nəzarətin həyata ke&ccedil;irilməsi qaydaları normativ h&uuml;quqi aktlarla m&uuml;əyyənləşdirilir.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 19. Məhkəmə nəzarəti</em></strong></p>\r\n<p style="text-align: left;">19.1. Cəzanı icra edən m&uuml;əssisə və ya orqanın vəzifəli şəxsləri məhkuma təyin olunmuş cəzanın icrası, cəzanın &ccedil;əkilmə yeri, məhkumun yerdəyişmələri və azad edilməsi barədə h&ouml;km &ccedil;ıxarmış və h&ouml;km&uuml;n icrası barədə g&ouml;stəriş vermiş hakimə dərhal məlumat verməlidirlər.</p>\r\n<p style="text-align: left;">19.2 Məhkəmə cinayət-prosessual qanunvericiliyi ilə m&uuml;əyyən edilmiş qaydada cəzanın &ccedil;əkilməsinin təxirə salınması və ya onun ləğv edilməsi, xəstəliyə g&ouml;rə cəzanı &ccedil;əkməkdən azad edilməsi, ittiham h&ouml;km&uuml;n&uuml;n icrası m&uuml;ddəti ilə əlaqədar cəza &ccedil;əkməkdən azad edilməsi, cəzanın &ccedil;əkilməmiş hissəsinin daha y&uuml;ng&uuml;l cəza n&ouml;v&uuml; ilə əvəz edilməsi, cəza&ccedil;əkmə m&uuml;əssisəsində saxlanılma şəraitinin dəyişdirilməsi, amnistiyanın tətbiq edilməsi, m&uuml;alicə m&uuml;əssisəsində saxlanma vaxtının cəza&ccedil;əkmə m&uuml;ddətinə daxil edilməsi, məhkumluğun vaxtından əvvəl g&ouml;t&uuml;r&uuml;lməsi məsələlərinin həlli zamanı cəzanın icrasına nəzarət edir.</p>\r\n<p style="text-align: left;">19.3. Məhkəmə cəzanı icra edən m&uuml;əssisə və ya orqanın fəaliyyəti ilə əlaqədar daxil olmuş şikayətlərə baxaraq m&uuml;vafiq qərar qəbul edir.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 20. Məhkumların islah edilməsində ictimai təşkilatların iştirakı</em></strong></p>\r\n<p style="text-align: left;">20.1. İctimai təşkilatlar məhkumların islah edilməsində iştirak edir və cəzanı icra edən m&uuml;əssisə və ya orqanların fəaliyyətinə ictimai nəzarəti həyata ke&ccedil;irirlər.</p>\r\n<p style="text-align: left;">20.2. İctimai təşkilatların məhkumların islah edilməsində iştirakı və cəzanı icra edən m&uuml;əssisə və ya orqanların fəaliyyətinə nəzarətin həyata ke&ccedil;irilməsi qaydaları normativ h&uuml;quqi aktlarla m&uuml;əyyənləşdirilir.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 21. Prokurorluğun cəzaların məqsədinə nail olunmasında iştirakı</em></strong></p>\r\n<p style="text-align: left;">Prokurorluğun məhkəmələr tərəfindən təyin edilmiş cəzaların məqsədinə nail olunmasında iştirakı h&ouml;km&uuml;n və ya məhkəmənin digər yekun qərarının icrası qaydasında məsələlərə dair m&uuml;raciətlərə məhkəmə iclasında baxılarkən cinayət-prosessual qanunvericiliyi ilə m&uuml;əyyən edilmiş qaydada həyata ke&ccedil;irilir.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 22. Cəza&ccedil;əkmə m&uuml;əssisələrinə baş &ccedil;əkilməsi</em></strong></p>\r\n<p style="text-align: left;">22.1. Qanunvericilik hakimiyyəti orqanı tərəfindən m&uuml;vəkkil edilmiş Azərbaycan Respublikası Milli Məclisinin deputatları, <a title="&ouml;z səlahiyyəti daxilində Azərbaycan Respublikasının Ədliyyə Nazirliyi və intizam xarakterli hərbi hissədə saxlama cəza n&ouml;v&uuml;nə aid hissədə Azərbaycan Respublikasının M&uuml;dafiə Nazirliyi; Nax&ccedil;ıvan Muxtar Respublikası ərazisində Nax&ccedil;ıvan Muxtar Respublikasının" href="file:///C:/Users/Rena.HQ/AppData/Local/Microsoft/Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/%D0%90%D0%B4%D0%BC%D0%B8%D0%BD%D0%B8%D1%81%D1%82%D1%80%D0%B0%D1%82%D0%BE%D1%80/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanlarının</a> icraatında olan cinayət işləri &uuml;zrə istintaq və digər prosessual hərəkətləri aparan təhqiqat&ccedil;ılar və ya m&uuml;stəntiqlər, təhqiqata və ibtidai istintaqa prosessual rəhbərliyi həyata ke&ccedil;irən prokurorlar, habelə məhkəmə nəzarəti funksiyalarını həyata ke&ccedil;irən hakimlər &ouml;z vəzifələrini yerinə yetirərkən, m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzanı icra edən m&uuml;əssisələrə baş &ccedil;əkə bilərlər. &nbsp;(25)</p>\r\n<p style="text-align: left;"><em>&nbsp;22.1-1. Azərbaycan Respublikası İnsan h&uuml;quqları &uuml;zrə m&uuml;vəkkilinin (ombudsmanın) və Milli preventiv qrup &uuml;zvlərinin istənilən vaxt, maneəsiz və əvvəlcədən xəbərdarlıq etmədən cəza&ccedil;əkmə m&uuml;əssisələrinə daxil olmaq, orada saxlanılan şəxslərlə, habelə m&uuml;vafiq məlumatı verə biləcək istənilən digər şəxslə təkbətək və ya zəruri saydığı halda m&uuml;təxəssisin və ya tərc&uuml;mə&ccedil;inin iştirakı ilə g&ouml;r&uuml;şmək və s&ouml;hbət etmək, onların həmin yerlərdə saxlanılmasının qanuniliyini təsdiq edən, eləcə də həmin şəxslərlə rəftara və onların saxlanma şəraitinə aid olan b&uuml;t&uuml;n sənədlərlə tanış olmaq və surətlərini almaq, akt tərtib etmək, həyata ke&ccedil;irdikləri tədbirlərin gedişini və nəticələrini protokollaşdırmaq, cəza&ccedil;əkmə m&uuml;əssisələrinin rəhbərliyi tərəfindən təxirə salınmadan qəbul edilmək, Azərbaycan Respublikası İnsan h&uuml;quqları &uuml;zrə m&uuml;vəkkilinin (ombudsmanın) isə həm&ccedil;inin cəza&ccedil;əkmə m&uuml;əssisələrinə m&uuml;vafiq t&ouml;vsiyələr vermək və həmin t&ouml;vsiyələrə m&uuml;əyyən edilmiş m&uuml;ddətdə cavablar almaq h&uuml;ququ vardır.</em><em> (25)</em></p>\r\n<p style="text-align: left;">22.2. K&uuml;tləvi informasiya vasitələrinin n&uuml;mayəndələri və bu Məcəllədə g&ouml;stərilən digər şəxslərin m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzanı icra edən m&uuml;əssisələrə baş &ccedil;əkmələri yalnız <a title="&ouml;z səlahiyyəti daxilində Azərbaycan Respublikasının Ədliyyə Nazirliyi və intizam xarakterli hərbi hissədə saxlama cəza n&ouml;v&uuml;nə aid hissədə Azərbaycan Respublikasının M&uuml;dafiə Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="file:///C:/Users/Rena.HQ/AppData/Local/Microsoft/Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/%D0%90%D0%B4%D0%BC%D0%B8%D0%BD%D0%B8%D1%81%D1%82%D1%80%D0%B0%D1%82%D0%BE%D1%80/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanı</a> tərəfindən m&uuml;əyyən edilmiş hallarda və qaydada həyata ke&ccedil;irilə bilər.</p>\r\n<p style="text-align: left;">22.3. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzanı icra edən m&uuml;əssisələrə daxilolma qaydası <a title="&ouml;z səlahiyyəti daxilində Azərbaycan Respublikasının Ədliyyə Nazirliyi və intizam xarakterli hərbi hissədə saxlama cəza n&ouml;v&uuml;nə aid hissədə Azərbaycan Respublikasının M&uuml;dafiə Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="file:///C:/Users/Rena.HQ/AppData/Local/Microsoft/Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/%D0%90%D0%B4%D0%BC%D0%B8%D0%BD%D0%B8%D1%81%D1%82%D1%80%D0%B0%D1%82%D0%BE%D1%80/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanı</a> tərəfindən m&uuml;əyyən edilir.</p>\r\n<p style="text-align: left;">22.4. Cəza&ccedil;əkmə m&uuml;əssisələrində məhkumlarla kino, foto, video &ccedil;əkilişlərinin aparılması, onlardan m&uuml;sahibə almaq, həm&ccedil;inin audio və video texniki vasitələrindən istifadə edilməsi onların razılığına əsasən həyata ke&ccedil;irilir.</p>\r\n<p style="text-align: left;">22.5. Məhkumların təhl&uuml;kəsizliyini və m&uuml;hafizəsini təmin edən obyektlərin kino, foto və video &ccedil;əkilişləri <a title="&ouml;z səlahiyyəti daxilində Azərbaycan Respublikasının Ədliyyə Nazirliyi və intizam xarakterli hərbi hissədə saxlama cəza n&ouml;v&uuml;nə aid hissədə Azərbaycan Respublikasının M&uuml;dafiə Nazirliyi; Nax&ccedil;ıvan Muxtar Respublikası ərazisində Nax&ccedil;ıvan Muxtar Respublikasının" href="file:///C:/Users/Rena.HQ/AppData/Local/Microsoft/Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/%D0%90%D0%B4%D0%BC%D0%B8%D0%BD%D0%B8%D1%81%D1%82%D1%80%D0%B0%D1%82%D0%BE%D1%80/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanının</a> icazəsi ilə həyata ke&ccedil;irilir. (<a href="file:///C:/Users/Rena.HQ/AppData/Local/Microsoft/Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/%D0%90%D0%B4%D0%BC%D0%B8%D0%BD%D0%B8%D1%81%D1%82%D1%80%D0%B0%D1%82%D0%BE%D1%80/Application%20Data/temp58772.htm#i6#i6">6</a>)</p>\r\n<p style="text-align: left;"><strong><em>X&uuml;susi hissə</em></strong></p>\r\n<p style="text-align: left;"><strong><em>II b&ouml;lmə. Məhkumun cəmiyyətdən təcrid edilməsi ilə əlaqədar olmayan cəzaların icrası</em></strong></p>\r\n<p style="text-align: left;"><strong>IV fəsil. Cərimə n&ouml;v&uuml;ndə cəzanın icrası</strong></p>\r\n<p style="text-align: left;"><strong><em>Maddə 23. Cərimə n&ouml;v&uuml;ndə cəzanın icrası qaydası</em></strong></p>\r\n<p style="text-align: left;">23.1. Məhkum cəriməni h&ouml;km qanuni q&uuml;vvəyə mindiyi g&uuml;ndən bir aydan gec olmayaraq m&uuml;vafiq bank hesabına k&ouml;&ccedil;&uuml;rməklə &ouml;dəməlidir. Icra məmuru cərimənin &ouml;dənilmədiyi halda məhkumu, ondan cərimənin məcburi tutulacağı barədə xəbərdar edir.</p>\r\n<p style="text-align: left;">23.2. Məhkum cəriməni &uuml;z&uuml;rl&uuml; səbəbdən bir ay m&uuml;ddətində &ouml;dəyə bilmədikdə, <a href="file:///C:/Users/Rena.HQ/AppData/Local/Microsoft/Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/%D0%90%D0%B4%D0%BC%D0%B8%D0%BD%D0%B8%D1%81%D1%82%D1%80%D0%B0%D1%82%D0%BE%D1%80/Application%20Data/4191">Azərbaycan Respublikasının Cinayət-Prosessual Məcəlləsinə</a> uyğun olaraq, məhkəmə məhkumun ərizəsinə əsasən cərimənin &ouml;dənilməsinin altı ayadək m&uuml;ddətə təxirə salınması və ya <em>m&uuml;ddəti g&ouml;stərilməklə</em> onun hissə-hissə &ouml;dənilməsi haqqında qərar qəbul edə bilər.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 24. Cərimənin məcburi qaydada tutulması</em></strong></p>\r\n<p style="text-align: left;">24.1. Məhkum cəriməni m&uuml;əyyən edilmiş m&uuml;ddətdə &ouml;dəmədikdə cərimə n&ouml;v&uuml;ndə cəzanın icrası Azərbaycan Respublikasının qanunvericiliyi ilə m&uuml;əyyən edilmiş qaydada h&ouml;km&uuml; &ccedil;ıxarmış məhkəmə tərəfindən məcburi qaydada, yəni məhkumun x&uuml;susi m&uuml;lkiyyətdə olan əmlakına və ya &uuml;mumi m&uuml;lkiyyətdə olan payına y&ouml;nəldilməklə həyata ke&ccedil;irilir.</p>\r\n<p style="text-align: left;">24.2. Cərimənin tutulması o halda məhkumun əmək haqqına və ya başqa qazancına y&ouml;nəldilir ki, onun əmlakı olmasın, yaxud bu əmlak cərimənin tamamilə &ouml;dənilməsi &uuml;&ccedil;&uuml;n kifayət etməsin.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 25. Cərimə n&ouml;v&uuml;ndə cəzanı icra edən orqan</em></strong></p>\r\n<p style="text-align: left;">Əsas cəza kimi və ya şərti məhkum etmə zamanı əlavə cəza kimi cərimə n&ouml;v&uuml;ndə cəzanın icrası məhkumun yaşadığı və ya işlədiyi yerin, yaxud onun əmlakının olduğu yerin icra məmuru, digər əsas cəza n&ouml;vlərinə əlavə cəza kimi təyin edilmiş cərimənin icrası isə məhkumun əsas cəzanı &ccedil;əkdiyi yerin icra məmuru tərəfindən həyata ke&ccedil;irilir.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 26. Cəriməni &ouml;dəməkdən boyun qa&ccedil;ırmanın nəticələri</em></strong></p>\r\n<p style="text-align: left;">Məhkum ona edilən rəsmi xəbərdarlıqdan sonra cəriməni &ouml;dəməkdən boyun qa&ccedil;ırarsa və onun x&uuml;susi m&uuml;lkiyyətdə əmlakı, &uuml;mumi m&uuml;lkiyyətdə payı və digər qazancı olmazsa məhkəmə əsas cəza kimi təyin edilmiş bu cəza n&ouml;v&uuml;n&uuml; icra məmurunın təqdimatına əsasən ictimai işlər, islah işləri və ya m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə cəzası ilə əvəz edə bilər.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 27. Cərimə n&ouml;v&uuml;ndə cəzanın icrasının qurtarması</em></strong></p>\r\n<p style="text-align: left;">Icra məmuru cərimənin tam &ouml;dənilməsi haqqında h&ouml;km&uuml; &ccedil;ıxarmış məhkəməyə məlumat verir.</p>\r\n<p style="text-align: left;"><strong>V fəsil. M&uuml;əyyən vəzifə tutma və ya m&uuml;əyyən fəaliyyətlə məşğul olma h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəzanın icrası</strong></p>\r\n<p style="text-align: left;"><strong><em>Maddə 28. M&uuml;əyyən vəzifə tutma və ya m&uuml;əyyən fəaliyyətlə məşğul olma h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəzanın icrası qaydası</em></strong></p>\r\n<p style="text-align: left;">28.1. M&uuml;əyyən vəzifə tutma və ya m&uuml;əyyən fəaliyyətlə məşğul olma h&uuml;ququndan məhrum etmə əsas cəza kimi və yaxud m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə ilə əlaqədar olmayan cəzaya əlavə cəza kimi təyin edildikdə, habelə şərti məhkum etmə zamanı məhkəmə qanuni q&uuml;vvəyə minmiş h&ouml;km&uuml;n surətini cəzanın icra edilməsi &uuml;&ccedil;&uuml;n məhkumun işlədiyi yerin işəg&ouml;t&uuml;rəninə, habelə bu cəzanın icrasına nəzarət edilməsi &uuml;&ccedil;&uuml;n icra məmuruna g&ouml;ndərir.</p>\r\n<p style="text-align: left;">28.2. M&uuml;əyyən vəzifə tutma və ya m&uuml;əyyən fəaliyyətlə məşğul olma h&uuml;ququndan məhrum etmə m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya əlavə cəza kimi təyin edildikdə, cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti əsas cəza m&uuml;ddəti qurtardıqdan sonra, yaxud məhkumun cəzadan şərti olaraq vaxtından əvvəl azad edildiyi və ya cəzanın &ccedil;əkilməmiş hissəsinin daha y&uuml;ng&uuml;l cəza n&ouml;v&uuml; ilə əvəz edildiyi hallarda h&ouml;km&uuml;n surətini cəzanın icrası &uuml;&ccedil;&uuml;n məhkumun yaşadığı yerin icra məmuruna g&ouml;ndərir.</p>\r\n<p style="text-align: left;">28.3. <em>&ccedil;ıxarılmışdır. </em>(22)</p>\r\n<p style="text-align: left;">28.4. Cəzanın icrası m&uuml;ddətində məhkum hərbi xidmətə qəbul edilərsə, icra məmurunı h&ouml;km&uuml;n surətini cəzanın icrası &uuml;&ccedil;&uuml;n məhkumun xidmət yerinə g&ouml;ndərir.<strong>(24)</strong></p>\r\n<p style="text-align: left;"><strong><em>Maddə 29. M&uuml;əyyən vəzifə tutma və ya m&uuml;əyyən fəaliyyətlə məşğul olma h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəzanın icrası zamanı işəg&ouml;t&uuml;rənin vəzifələri</em></strong></p>\r\n<p style="text-align: left;">29.1. İşəg&ouml;t&uuml;rən h&ouml;km&uuml;n surətini aldığı g&uuml;ndən sonra &uuml;&ccedil; g&uuml;n ərzində m&uuml;əyyən vəzifə tutma və ya m&uuml;əyyən fəaliyyətlə məşğul olma h&uuml;ququndan məhrum edilmiş məhkumla əmək m&uuml;qaviləsinə qanunvericilikdə nəzərdə tutulmuş qaydada xitam verir və məhkəmənin h&ouml;km&uuml;nə uyğun olaraq məhkumun əmək kitab&ccedil;asına onun hansı əsasla, hansı m&uuml;ddətə və hansı vəzifəni tutma h&uuml;ququndan məhrum edilməsi və ya hansı fəaliyyət n&ouml;v&uuml; ilə məşğul olma h&uuml;ququndan məhrum edilməsi barədə qeyd edir. H&ouml;km&uuml; &ccedil;ıxarmış məhkəməyə bu barədə dərhal məlumat verilir.</p>\r\n<p style="text-align: left;">29.2. İşəg&ouml;t&uuml;rən h&ouml;kmə zidd olmadıqda məhkumun razılığı ilə ona başqa vəzifə və ya başqa fəaliyyət n&ouml;v&uuml; ilə məşğul olmaq imkanı yarada bilər.</p>\r\n<p style="text-align: left;">29.3. M&uuml;əyyən vəzifə tutma və ya m&uuml;əyyən fəaliyyətlə məşğul olma h&uuml;ququndan məhrum edilməyə məhkum olunmuş şəxs əmək m&uuml;qaviləsi bağlayarkən onun əmək kitab&ccedil;asında m&uuml;vafiq qeyd olmadıqda işəg&ouml;t&uuml;rən bu cəzanın icrasına nəzarət edən m&uuml;vafiq icra məmurunın təqdimatı ilə məhkəmənin h&ouml;km&uuml;nə uyğun olaraq həmin əmək kitab&ccedil;asında onun hansı əsasla, hansı m&uuml;ddətə və hansı vəzifəni tutma h&uuml;ququndan məhrum edilməsi və ya hansı fəaliyyət n&ouml;v&uuml; ilə məşğul olma h&uuml;ququndan məhrum edilməsi barədə qeyd edir.</p>\r\n<p style="text-align: left;">29.4. M&uuml;əyyən vəzifə tutma və ya m&uuml;əyyən fəaliyyətlə məşğul olma h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəza &ccedil;əkmiş, yaxud qanunla m&uuml;əyyən edilmiş qaydada bu cəzadan azad edilmiş şəxsə onun xahişi ilə əmək kitab&ccedil;ası əvəzinə bu kitab&ccedil;anın cəza haqqında qeyd yazılmış dublikatı verilir.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 30. M&uuml;əyyən vəzifə tutma və ya m&uuml;əyyən fəaliyyətlə məşğul olma h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəzanın icrası zamanı cəzanı icra edən m&uuml;əssisə və ya orqanın vəzifələri</em></strong></p>\r\n<p style="text-align: left;">M&uuml;əyyən vəzifə tutma və ya m&uuml;əyyən fəaliyyətlə məşğul olma h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə əlavə cəzaya məhkum olunmuş şəxslər əsas cəzanı &ccedil;əkərkən, cəzanı icra edən m&uuml;əssisə və ya orqan bu şəxsləri h&ouml;kmdə qadağan edilmiş işlərə cəlb edə bilməz.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 31. M&uuml;əyyən vəzifə tutma və ya m&uuml;əyyən fəaliyyətlə məşğul olma h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəzanın icrası zamanı məhkəmə icrasının vəzifələri</em></strong></p>\r\n<p style="text-align: left;">31.0. Məhkumun yaşadığı yerin icra məmuru:</p>\r\n<p style="text-align: left;">31.0.1. m&uuml;əyyən vəzifə tutma və ya m&uuml;əyyən fəaliyyətlə məşğul olma h&uuml;ququndan məhrum etmə barədə məhkəmənin h&ouml;km&uuml;n&uuml;n tələblərinə məhkumun riayət etməsinə nəzarət edir;</p>\r\n<p style="text-align: left;">31.0.2. m&uuml;əyyən vəzifə tutma və ya m&uuml;əyyən fəaliyyətlə məşğul olma h&uuml;ququndan məhrum etmə barədə məhkəmənin h&ouml;km&uuml;ndə nəzərdə tutulan tələblərin işəg&ouml;t&uuml;rən tərəfindən yerinə yetirilməsini yoxlayır.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 32. M&uuml;əyyən vəzifə tutma və ya m&uuml;əyyən fəaliyyətlə məşğul olma h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəzanın icrası m&uuml;ddətinin hesablanması</em></strong></p>\r\n<p style="text-align: left;">M&uuml;əyyən vəzifə tutma və ya m&uuml;əyyən fəaliyyətlə məşğul olma h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəza ictimai işlərə, islah işlərinə, habelə şərti məhkum etməyə əlavə cəza kimi təyin edildiyi hallarda, əlavə cəzanın m&uuml;ddəti h&ouml;km&uuml;n qanuni q&uuml;vvəyə mindiyi andan hesablanır. Bu cəza n&ouml;v&uuml; intizam xarakterli hərbi hissədə saxlama və ya m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzalara əlavə cəza kimi təyin edildiyi hallarda, əlavə cəza əsas cəzanın &ccedil;əkildiyi b&uuml;t&uuml;n m&uuml;ddətə və bundan əlavə h&ouml;kmdə bu cəza n&ouml;v&uuml; &uuml;&ccedil;&uuml;n m&uuml;əyyən edilmiş m&uuml;ddətə şamil olunur.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 33. M&uuml;əyyən vəzifə tutma və ya m&uuml;əyyən fəaliyyətlə məşğul olma h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəzanın icra edilməməsinin nəticələri</em></strong></p>\r\n<p style="text-align: left;">33.1. M&uuml;əyyən vəzifə tutma və ya m&uuml;əyyən fəaliyyətlə məşğul olma h&uuml;ququndan məhrum edilməyə məhkum olunmuş şəxs h&ouml;kmdə g&ouml;r&uuml;lməsi ona qadağan edilmiş işə girdikdə, yaxud h&ouml;kmlə ona qadağan edilmiş fəaliyyətlə məşğul olduqda icra məmuru işəg&ouml;t&uuml;rənə məlumat verərək, məhkumun h&ouml;kmlə qadağan edilmiş fəaliyyətinə xitam verilməsi &uuml;&ccedil;&uuml;n &uuml;&ccedil; g&uuml;n m&uuml;ddətində tədbir g&ouml;r&uuml;lməsini təklif edir və onu h&ouml;km&uuml;n icra edilməməsinin məsuliyyətə səbəb olması barədə xəbərdar edir.</p>\r\n<p style="text-align: left;">33.2. M&uuml;əyyən vəzifə tutma və ya m&uuml;əyyən fəaliyyətlə məşğul olma h&uuml;ququndan məhrum edilmiş məhkum cəzanın &ccedil;əkilməsindən qərəzli boyun qa&ccedil;ırdıqda, habelə vəzifəli şəxs m&uuml;əyyən vəzifə tutma və ya m&uuml;əyyən fəaliyyətlə məşğul olma h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəzanı qərəzli icra etmədikdə onlar Azərbaycan Respublikasının qanunvericiliyində nəzərdə tutulmuş qaydada məsuliyyət daşıyırlar.</p>\r\n<p style="text-align: left;"><strong>VI fəsil. İctimai işlər n&ouml;v&uuml;ndə cəzanın icrası</strong></p>\r\n<p style="text-align: left;"><strong><em>Maddə 34. İctimai işlər n&ouml;v&uuml;ndə cəzanın icrası qaydası</em></strong></p>\r\n<p style="text-align: left;">34.1. İctimai işlər n&ouml;v&uuml;ndə cəzanı məhkumun yaşadığı ərazinin <a title="yerli icra hakimiyyəti orqanları / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="file:///C:/Users/Rena.HQ/AppData/Local/Microsoft/Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/%D0%90%D0%B4%D0%BC%D0%B8%D0%BD%D0%B8%D1%81%D1%82%D1%80%D0%B0%D1%82%D0%BE%D1%80/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanının</a> m&uuml;əyyən etdiyi ictimai yerlərdə icra məmuru icra edir.</p>\r\n<p style="text-align: left;">34.2. İctimai işlər n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər məhkəmənin qanuni q&uuml;vvəyə minmiş h&ouml;km&uuml;n&uuml;n icrası haqqında məhkəmənin h&ouml;km&uuml;n&uuml;n surətini və m&uuml;vafiq g&ouml;stərişinin icra məmuru tərəfindən alındığı g&uuml;ndən sonra on beş g&uuml;n m&uuml;ddətində cəzanın &ccedil;əkilməsinə cəlb olunurlar.</p>\r\n<p style="text-align: left;">34.3. Icra məmuru məhkumların qeydiyyatını aparır, onlara cəzanın icrasının qayda və şərtlərini izah edir, <a title="yerli icra hakimiyyəti orqanları / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="file:///C:/Users/Rena.HQ/AppData/Local/Microsoft/Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/%D0%90%D0%B4%D0%BC%D0%B8%D0%BD%D0%B8%D1%81%D1%82%D1%80%D0%B0%D1%82%D0%BE%D1%80/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanı</a> ilə ictimai işlərin həyata ke&ccedil;iriləcəyi yerlərin siyahısını razılaşdırır, məhkumların davranışına nəzarət edir, onların işləmiş olduğu vaxtın u&ccedil;otunu aparır.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 35. İctimai işlər n&ouml;v&uuml;ndə cəzanın &ccedil;əkilməsi və icrası şərtləri</em></strong></p>\r\n<p style="text-align: left;">35.1. Məhkumlər ictimai işləri yerinə yetirdikləri yerin m&uuml;əssisədaxili intizam qaydalarına riayət etməli, əməyə vicdanla yanaşmalı, onlar &uuml;&ccedil;&uuml;n m&uuml;əyyən olunmuş yerlərdə və h&ouml;kmdə m&uuml;əyyən olunmuş m&uuml;ddətdə işləməli və yaşayış yerini dəyişdikdə bu barədə icra məmuruna məlumat verməlidirlər.</p>\r\n<p style="text-align: left;">35.2. Əsas iş yerində məhkuma n&ouml;vbəti məzuniyyətin verilməsi ictimai işlər n&ouml;v&uuml;ndə cəzanın icrasını dayandırmır.</p>\r\n<p style="text-align: left;">35.3. İctimai işlər n&ouml;v&uuml;ndə cəza &ccedil;əkən məhkum və ya onun qanuni n&uuml;mayəndəsi qanunvericiliklə m&uuml;əyyən edilmiş qaydada cəzadan azad edilmə haqqında məhkəməyə ərizə ilə m&uuml;raciət edə bilər.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 36. İctimai işlər n&ouml;v&uuml;ndə cəzanın m&uuml;ddətinin hesablanması</em></strong></p>\r\n<p style="text-align: left;">36.1. İctimai işlər n&ouml;v&uuml;ndə cəzanın m&uuml;ddəti saatla hesablanır.</p>\r\n<p style="text-align: left;">36.2. İctimai işlər n&ouml;v&uuml;ndə cəzanın m&uuml;ddəti, bir qayda olaraq, həftə ərzində on iki saatdan az olmayaraq m&uuml;əyyən edilir. İctimai işlər n&ouml;v&uuml;ndə cəzanın m&uuml;ddəti <em>istirahət, səsvermə, iş g&uuml;n&uuml; hesab edilməyən bayram g&uuml;nləri, &uuml;mumxalq h&uuml;zn g&uuml;n&uuml;</em> və məhkumun əsas işindən və ya təhsilindən asudə vaxtlarında d&ouml;rd saatdan, iş g&uuml;nlərində isə iki saatdan, məhkumun razılığı ilə isə d&ouml;rd saatdan &ccedil;ox ola bilməz. Bu cəza n&ouml;v&uuml;n&uuml;n icra m&uuml;ddəti on beş yaşınadək məhkumlar &uuml;&ccedil;&uuml;n g&uuml;ndə iki saatdan, on beş yaşından on altı yaşınadək məhkumlar &uuml;&ccedil;&uuml;n isə &uuml;&ccedil; saatdan &ccedil;ox ola bilməz. &Uuml;z&uuml;rl&uuml; səbəblər olduqda icra məmuru məhkuma həftə ərzində m&uuml;əyyən olunmuş vaxtdan az işləməyə icazə verə bilər.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 37. İctimai işlər n&ouml;v&uuml;ndə cəzanın icrası zamanı işəg&ouml;t&uuml;rənin vəzifələri </em></strong></p>\r\n<p style="text-align: left;">İctimai işlər n&ouml;v&uuml;ndə cəzanın icrası zamanı işəg&ouml;t&uuml;rən məhkumlar &uuml;&ccedil;&uuml;n m&uuml;əyyən edilmiş işin yerinə yetirilməsinə nəzarət edir, onların işlədikləri saatların u&ccedil;otunu aparır və ictimai işlərin yerinə yetirilməsindən boyun qa&ccedil;ırma halları barədə icra məmuruna məlumat verir.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 38. İctimai işlər n&ouml;v&uuml;ndə cəza &ccedil;əkən şəxslərin məsuliyyəti</em></strong></p>\r\n<p style="text-align: left;">38.1. Məhkum ictimai işlər n&ouml;v&uuml;ndə cəzanın &ccedil;əkilməsi qaydalarını pozduqda, icra məmuru ona məsuliyyət daşıyacağı barədə rəsmi xəbərdarlıq edir.</p>\r\n<p style="text-align: left;">38.2. Məhkum ictimai işlər n&ouml;v&uuml;ndə cəzanın &ccedil;əkilməsindən qərəzli boyun qa&ccedil;ırdıqda, icra məmuru <a href="file:///C:/Users/Rena.HQ/AppData/Local/Microsoft/Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/%D0%90%D0%B4%D0%BC%D0%B8%D0%BD%D0%B8%D1%81%D1%82%D1%80%D0%B0%D1%82%D0%BE%D1%80/Application%20Data/4056">Azərbaycan Respublikasının Cinayət Məcəlləsinə</a> uyğun olaraq cəzanın <em>m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəza</em>ilə əvəz edilməsi &uuml;&ccedil;&uuml;n məhkəməyə təqdimat verir. (22)</p>\r\n<p style="text-align: left;"><strong><em>Maddə 39. İctimai işlər n&ouml;v&uuml;ndə cəzanın &ccedil;əkilməsindən qərəzli boyun qa&ccedil;ırma</em></strong></p>\r\n<p style="text-align: left;">39.0. İctimai işlər n&ouml;v&uuml;ndə cəzanın &ccedil;əkilməsindən qərəzli boyun qa&ccedil;ıran məhkumlar aşağıdakılar hesab olunurlar.</p>\r\n<p style="text-align: left;">39.0.1. &uuml;z&uuml;rs&uuml;z səbəbdən ayda iki dəfədən &ccedil;ox ictimai işə &ccedil;ıxmayanlar;</p>\r\n<p style="text-align: left;">39.0.2. ayda iki dəfədən &ccedil;ox cəzanın icrası yerlərində əmək intizamını pozanlar;</p>\r\n<p style="text-align: left;">39.0.3. cəzanı &ccedil;əkməmək məqsədilə gizlənənlər.</p>\r\n<p style="text-align: left;"><strong>VII fəsil. İslah işləri n&ouml;v&uuml;ndə cəzanın icrası</strong></p>\r\n<p style="text-align: left;"><strong><em>Maddə 40. İslah işləri n&ouml;v&uuml;ndə cəzanın icrası yeri</em></strong></p>\r\n<p style="text-align: left;">İslah işləri n&ouml;v&uuml;ndə cəza məhkumun əsas iş yeri &uuml;zrə &ccedil;əkilir.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 41. İslah işləri n&ouml;v&uuml;ndə cəzanın icrası qaydası</em></strong></p>\r\n<p style="text-align: left;">41.1. İslah işləri n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər məhkum edilməzdən əvvəl işlədikləri vəzifədə və ya işdə qalırlar. Bu şəxslər başqa vəzifəyə və ya işə yalnız əmək qanunvericiliyi ilə m&uuml;əyyən olunmuş qaydada və &uuml;mumi əsaslarla ke&ccedil;irilə bilərlər.</p>\r\n<p style="text-align: left;">41.2. H&ouml;km &ccedil;ıxarıldıqdan sonra ağır xəstəliyə d&uuml;&ccedil;ar olmuş məhkumlar və ya onların qanuni n&uuml;mayəndələri cəzanın bu n&ouml;v&uuml;n&uuml; daha y&uuml;ng&uuml;l cəza n&ouml;v&uuml; ilə əvəz etmək haqqında məhkəməyə ərizə ilə m&uuml;raciət edə bilərlər. Həmin m&uuml;raciət məhkəmə tərəfindən həll edilənədək g&ouml;stərilən şəxslər islah işlərini &ccedil;əkməyə cəlb edilmirlər.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 42. İslah işləri n&ouml;v&uuml;ndə cəzanın icrasının təşkili</em></strong></p>\r\n<p style="text-align: left;">42.1. İslah işləri n&ouml;v&uuml;ndə cəza &ccedil;əkən məhkumların islah edilməsi onların ictimai-faydalı əməkdə iştirakı əsasında həyata ke&ccedil;irilir. Cəzanın &ccedil;əkildiyi yer &uuml;zrə işəg&ouml;t&uuml;rən məhkumun davranışına nəzarət edir və onunla tərbiyə işinin aparılmasında icra məmuruna k&ouml;mək edir.</p>\r\n<p style="text-align: left;">42.2. İslah işləri n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər cəza &ccedil;əkmək &uuml;&ccedil;&uuml;n m&uuml;əyyən edilmiş qaydaya riayət etməli, cəzanın bu n&ouml;v&uuml;n&uuml; icra edən icra məmurunın &ccedil;ağırışına gəlməlidirlər. &Uuml;z&uuml;rl&uuml; səbəblər olmadan bu tələbə əməl edilmədikdə, məhkum məcburi gətirilə bilər. Məcburi gətirilmə məhkəmənin qərarı ilə həyata ke&ccedil;irilir.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 43. İslah işləri n&ouml;v&uuml;ndə cəzanın icra edilməsi </em></strong></p>\r\n<p style="text-align: left;">43.1. İslah işləri n&ouml;v&uuml;ndə cəzanın &ccedil;əkilməsi h&ouml;km qanuni q&uuml;vvəyə mindiyi g&uuml;ndən icraya y&ouml;nəldilir.</p>\r\n<p style="text-align: left;">43.2. Icra məmuru islah işləri n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxs barəsində h&ouml;km&uuml;n surətini və g&ouml;stərişi aldıqdan sonra &uuml;&ccedil; g&uuml;n m&uuml;ddətində h&ouml;km&uuml;n surətini və m&uuml;əyyən edilmiş formada bildirişi işəg&ouml;t&uuml;rənə g&ouml;ndərir. Məhkum əvvəlki iş yerindən getdikdə və he&ccedil; yerdə işləmədikdə, icra məmuru məhkuma &uuml;&ccedil; ay m&uuml;ddətində işə girməyi təklif edir və zəruri olduqda ona işə d&uuml;zəlməkdə k&ouml;mək edir. G&ouml;stərilən m&uuml;ddətdə məhkum &uuml;z&uuml;rl&uuml; səbəblər olmadan işə girmədikdə, onun barəsində bu Məcəllədə nəzərdə tutulmuş tədbirlər tətbiq edilir.</p>\r\n<p style="text-align: left;">43.3. İşsiz məhkum &ouml;z&uuml; işə d&uuml;zəlməli və ya icra məmurunın iştirakı ilə məşğulluq xidməti orqanında qeydiyyata durmalıdır. Məhkum məşğulluq xidməti orqanının ona təklif etdiyi işdən imtina edə bilməz.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 44. İslah işləri n&ouml;v&uuml;ndə cəzanın icrası şərtləri</em></strong></p>\r\n<p style="text-align: left;">44.1. İslah işləri n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslərin qazancından cəzanın icrası m&uuml;ddəti ərzində məhkəmənin h&ouml;km&uuml; ilə m&uuml;əyyən edilmiş məbləğdə və bu Məcəllədə nəzərdə tutulmuş qaydada d&ouml;vlət nəfinə pul tutulur.</p>\r\n<p style="text-align: left;">44.2. İslah işləri n&ouml;v&uuml;ndə cəzanın icrası m&uuml;ddəti məhkumun &uuml;mumi iş stajına daxil edilir.</p>\r\n<p style="text-align: left;">44.3. İslah işləri n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər əmək qanunvericiliyi ilə m&uuml;əyyən edilmiş məzuniyyətlərdən istifadə etmək h&uuml;ququna malikdirlər.</p>\r\n<p style="text-align: left;"><strong><em>Maddə 45. İslah işləri n&ouml;v&uuml;ndə cəzanın icrası m&uuml;ddətinin hesablanması</em></strong></p>\r\n<p style="text-align: left;">45.1. İslah işləri n&ouml;v&uuml;ndə cəzanın icrası m&uuml;ddəti məhkumun işlədiyi və onun qazancından tutulmalar aparıldığı aylar və g&uuml;nlərlə hesablanır.</p>\r\n<p style="text-align: left;">45.2. Məhkumun işlədiyi g&uuml;nlərin sayı məhkəmənin cəza &uuml;&ccedil;&uuml;n m&uuml;əyyən etdiyi təqvim ayına d&uuml;şən iş g&uuml;nlərinin sayına uyğun olmalıdır. Məhkum g&ouml;stərilən miqdarda iş g&uuml;nlərini işləmədikdə və işlənməmiş g&uuml;nlərin cəza m&uuml;ddətinə hesablanması &uuml;&ccedil;&uuml;n bu Məcəllə ilə m&uuml;əyyən edilmiş əsaslar olmadıqda, islah işləri n&ouml;v&uuml;ndə cəzanın icrası nəzərdə tutulmuş miqdarda iş g&uuml;nlərinin məhkum tərəfindən tamamilə işlənməsinədək davam edir.</p>\r\n<p style="text-align: left;">45.3. Məhkumun &uuml;z&uuml;rl&uuml; səbəblərə g&ouml;rə işləmədiyi və qanuna uyğun olaraq onun əmək haqqı saxlandığı vaxt cəzanın icrası m&uuml;ddətinə hesablanır. Tibbi sənədlərlə təsdiq edilmiş əmək qabiliyyətinin m&uuml;vəqqəti itirildiyi vaxt, habelə sosial məzuniyyətlərdə olma vaxtı bu m&uuml;ddətə daxil edilir.</p>\r\n<p style="text-align: left;">45.4. Kənd təsərr&uuml;fatı m&uuml;əssisələrində məhkumların işlədikləri g&uuml;nlərin &uuml;mumi sayı təsərr&uuml;fat &uuml;zrə iş&ccedil;ilər &uuml;&ccedil;&uuml;n m&uuml;əyyən edilmiş illik minimumdan və ya onun ayrı-ayrı d&ouml;vrləri &uuml;zrə minimumdan aşağı olmadıqda, obyektiv səbəblərə g&ouml;rə onlara iş verilməyən vaxt da cəzanın &ccedil;əkilməsi m&uuml;ddətinə hesablanır.</p>\r\n<p>45.5. İslah işləri n&ouml;v&uuml;ndə cəzanın icrası zamanı məhkuma inzibati tənbeh kimi verilmiş, yaxud başqa cinayət işi ilə əlaqədar qətimkan tədbiri kimi se&ccedil;ilmiş həbsin m&uuml;ddəti islah işləri n&ouml;v&uuml;ndə cəzanın icrası m&uuml;ddətinə hesablanmır.</p>\r\n<p><strong><em>Maddə 46. İslah işləri n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslərin qazancından tutulmalar</em></strong></p>\r\n<p>46.1. İslah işləri n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslərin qazancından tutulmalar b&uuml;t&uuml;n qazanc məbləğindən, vergilər və digər tədiyələr daxil olmaqla, habelə icra sənədlərindən asılı olmayaraq aparılır.</p>\r\n<p>46.2. Qazancdan tutulmalar əmək haqqı verilərkən hər işlənmiş ay &uuml;&ccedil;&uuml;n, məhkum işdən &ccedil;ıxdıqda isə ayın işlənmiş hissəsi &uuml;&ccedil;&uuml;n aparılır. Əvəz&ccedil;ilik &uuml;zrə işləyən şəxslərin iş yerlərindəki qazancından pul məbləği tutulur.</p>\r\n<p>46.3. Qazancdan tutulmalar pensiyalardan və m&uuml;avinətdən, birdəfəlik verilən və əmək haqqı sistemində nəzərdə tutulmayan &ouml;dəmələrdən, ezamiyyətlərlə əlaqədar xərclər &uuml;&ccedil;&uuml;n əvəz kimi verilən məbləğlərdən və başqa &ouml;dəmələrdən aparılmır.</p>\r\n<p>46.4. Məhkumların qazancından tutulmalara onların qazancının həm pul, həm də natura hissəsi cəlb edilir. Qazancın məhkumlardan tutulmuş natura hissəsi işəg&ouml;t&uuml;rənin sərəncamında qalır, onun dəyəri isə d&ouml;vlətin nəfinə ke&ccedil;irilir. Tutulmuş pul məbləği hər ay əmək haqqı verilən g&uuml;n d&ouml;vlətin nəfinə ke&ccedil;irilir.</p>\r\n<p>46.5. Tutulmalar qazancın natura hissəsi daxil olduqca təsərr&uuml;fat ilinin yekunlarına g&ouml;rə haqq-hesab &ccedil;əkilərkən aparılır.</p>\r\n<p>46.6. İşə xitam verilməklə məhkəmənin h&ouml;km&uuml; ləğv olunduqda və ya dəyişdirildikdə məhkumun qazancından tutulmuş məbləğlər tamamilə və ya artıq tutulmuş məbləğlər ona qaytarılır.</p>\r\n<p><strong><em>Maddə 47. İslah işləri n&ouml;v&uuml;ndə cəzanın icrası yeri &uuml;zrə işəg&ouml;t&uuml;rənin vəzifələri</em></strong></p>\r\n<p>47.0. İslah işləri n&ouml;v&uuml;ndə cəzanın icrası yeri &uuml;zrə işəg&ouml;t&uuml;rən:</p>\r\n<p>47.0.1. məhkəmə h&ouml;km&uuml;n&uuml;n məzmununu əmək kollektivinin nəzərinə &ccedil;atdırır;</p>\r\n<p>47.0.2. iş yeri &uuml;zrə məhkumların davranışına nəzarət edir və onlarla tərbiyə işinin aparılmasında icra məmuruna k&ouml;mək edir;</p>\r\n<p>47.0.3. məhkumların əmək intizamına riayət etməsinə nəzarət edir, onlara ixtisaslarının artırılmasında k&ouml;mək edir;</p>\r\n<p>47.0.4. məhkum barəsində həvəsləndirmə və tənbeh tədbirlərinin tətbiq edilməsi və məhkumun cəza &ccedil;əkməkdən boyun qa&ccedil;ırması, başqa işə ke&ccedil;məsi və ya işdən azad olması haqqında əvvəlcədən icra məmuruna məlumat verir;</p>\r\n<p>47.0.5. məhkumun qazancından d&ouml;vlət nəfinə tutulmaları d&uuml;zg&uuml;n və vaxtında aparır və tutulmuş məbləğləri m&uuml;əyyən edilmiş qaydada d&ouml;vlət nəfinə ke&ccedil;irir;</p>\r\n<p>47.0.6. islah işləri n&ouml;v&uuml;ndə cəzanın icrası &uuml;&ccedil;&uuml;n bu Məcəllədə nəzərdə tutulmuş şərtləri yerinə yetirir.</p>\r\n<p><strong><em>Maddə 48. İslah işləri n&ouml;v&uuml;ndə cəzanı icra edən icra məmurunın vəzifələri</em></strong></p>\r\n<p>48.0. İslah işləri n&ouml;v&uuml;ndə cəzanı icra edən icra məmuru:</p>\r\n<p>48.0.1. islah işləri n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslərə zəruri hallarda işə d&uuml;zəlməkdə k&ouml;məklik g&ouml;stərir;</p>\r\n<p>48.0.2. məhkumların qazancından tutulmaların d&uuml;zg&uuml;n aparılmasına və cəzanın icrası &uuml;&ccedil;&uuml;n bu Məcəllədə m&uuml;əyyən edilmiş şərtlərə işəg&ouml;t&uuml;rən tərəfindən riayət olunmasına nəzarət edir;</p>\r\n<p>48.0.3. məhkumlarla tərbiyə işini aparır;</p>\r\n<p>48.0.4. məhkumlar barəsində bu Məcəllədə nəzərdə tutulmuş həvəsləndirmə və tənbeh tədbirlərini tətbiq edir;</p>\r\n<p>48.0.5. məhkum olunmuş şəxslərin fərdi qeydiyyatını aparır;</p>\r\n<p>48.0.6. yeri məlum olmayan məhkumların m&uuml;əyyən olunmuş qaydada axtarışını təşkil edir.</p>\r\n<p><strong><em>Maddə 49. İslah işləri n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər barəsində işəg&ouml;t&uuml;rənin tətbiq etdiyi m&uuml;kafatlandırma və intizam tənbehi tədbirləri</em></strong></p>\r\n<p>İslah işləri n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər barəsində işəg&ouml;t&uuml;rən tərəfindən əmək qanunvericiliyi ilə m&uuml;əyyən edilmiş qaydada m&uuml;kafatlandırma və intizam tənbehi tədbirləri tətbiq edilir.</p>\r\n<p><strong><em>Maddə 50. İslah işləri n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər barəsində icra məmurları tərəfindən tətbiq edilən həvəsləndirmə tədbirləri</em></strong></p>\r\n<p>N&uuml;munəvi davranışına g&ouml;rə məhkumə icra məmuru tərəfindən təşəkk&uuml;r elan edilə və ya əvvəllər verilmiş tənbeh g&ouml;t&uuml;r&uuml;lə bilər.</p>\r\n<p><strong><em>Maddə 51. İslah işləri n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər barəsində icra məmurları tərəfindən tətbiq edilən tənbeh tədbirləri</em></strong></p>\r\n<p>51.1. İslah işləri n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər cəzanın icrası qaydalarını pozduqda, icra məmuru məhkuma xəbərdarlıq və ya t&ouml;hmət n&ouml;v&uuml;ndə tənbeh verə bilər.</p>\r\n<p>51.2. Tənbeh verilməzdən əvvəl məhkumdan yazılı izahat alınır. Xətanın aşkara &ccedil;ıxarıldığı g&uuml;ndən bir ay ke&ccedil;dikdən sonra məhkuma tənbeh verilə bilməz. Məhkum ona verilmiş tənbehdən yuxarı vəzifəli şəxsə şikayət edə bilər.</p>\r\n<p>51.3. İslah işləri n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər cəzanı &ccedil;əkməkdən boyun qa&ccedil;ırdıqda, icra məmuru onlara rəsmi xəbərdarlıq edir.</p>\r\n<p>51.4. İslah işləri n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər rəsmi xəbərdarlıqdan sonra cəzanı &ccedil;əkməkdən qəsdən boyun qa&ccedil;ırdıqda, icra məmuru <a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4056">Azərbaycan Respublikası Cinayət Məcəlləsinə</a> uyğun olaraq islah işlərinin &ccedil;əkilməmiş hissəsinin m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəza ilə əvəz edilməsi haqqında məhkəməyə təqdimat verir. (22)</p>\r\n<p><strong>VIII fəsil. <em>&Ccedil;ıxarılmışdır. </em>(22) </strong></p>\r\n<p><strong><em>III b&ouml;lmə. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaların icrası</em></strong></p>\r\n<p><strong>IX fəsil. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaların icrasının &uuml;mumi m&uuml;ddəaları</strong></p>\r\n<p><strong><em>Maddə 65. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaların icrası yeri</em></strong></p>\r\n<p>65.1. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər cəzalarını bir qayda olaraq yaşayış yerinə yaxın ərazidə yerləşən cəza&ccedil;əkmə m&uuml;əssisələrində &ccedil;əkirlər. Sağlamlıq vəziyyətinə g&ouml;rə, şəxsi təhl&uuml;kəsizliyini təmin etmək məqsədi ilə və digər m&uuml;stəsna hallara g&ouml;rə məhkum cəzasını &ccedil;əkmək &uuml;&ccedil;&uuml;n başqa ərazidə yerləşən cəza&ccedil;əkmə m&uuml;əssisələrinə g&ouml;ndərilə bilər.</p>\r\n<p>65.2. Cinayətlərin x&uuml;susi təhl&uuml;kəli residivinə g&ouml;rə, &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya və ya cəzalarının bir hissəsini həbsxanada &ccedil;əkməyə məhkum olunanlar, həm&ccedil;inin əcnəbilər və ya vətəndaşlığı olmayan şəxslər cəzalarını &ccedil;əkmək &uuml;&ccedil;&uuml;n yaşadıqları yerdən asılı olmayaraq, m&uuml;vafiq cəza&ccedil;əkmə m&uuml;əssisələrinə g&ouml;ndərilirlər.</p>\r\n<p><strong><em>Maddə 66. Cəza&ccedil;əkmə m&uuml;əssisələrinin n&ouml;vləri</em></strong></p>\r\n<p>66.1. Cəza&ccedil;əkmə m&uuml;əssisələri yetkinlik yaşına &ccedil;atmayan şəxslər &uuml;&ccedil;&uuml;n tərbiyə m&uuml;əssisələrindən, yetkinlik yaşına &ccedil;atmış şəxslər &uuml;&ccedil;&uuml;n m&uuml;xtəlif rejimli cəza&ccedil;əkmə m&uuml;əssisələrindən, həbsxanalardan və azadlıqdan məhrum edilmiş şəxslər &uuml;&ccedil;&uuml;n nəzərdə tutulmuş m&uuml;alicə m&uuml;əssisələrindən ibarətdir. İstintaq təcridxanaları <em>penitensiar m&uuml;əssisə olmaqla</em> təsərr&uuml;fat və məişət xidməti işlərinə g&ouml;rə bu m&uuml;əssisədə saxlanılan məhkumlar &uuml;&ccedil;&uuml;n cəza&ccedil;əkmə m&uuml;əssisələrinin vəzifələrini yerinə yetirirlər.</p>\r\n<p>66.2. Cəza&ccedil;əkmə m&uuml;əssisələri məntəqə tipli, &uuml;mumi, ciddi, x&uuml;susi rejimli cəza&ccedil;əkmə m&uuml;əssisələrinə, tərbiyə m&uuml;əssisələri isə &uuml;mumi və m&ouml;hkəm rejimli m&uuml;əssisələrə b&ouml;l&uuml;n&uuml;rlər.</p>\r\n<p>66.3. M&uuml;alicə m&uuml;əssisələrində stasionar şəraitdə <em>m&uuml;ayinə və</em> m&uuml;alicəyə ehtiyacı olan məhkumlar cəza &ccedil;əkirlər. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 67. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslərin cəzanın icrası yerinə g&ouml;ndərilməsi</em></strong></p>\r\n<p>67.1. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər istintaq təcridxanasının m&uuml;diriyyəti tərəfindən h&ouml;km&uuml;n surətini və onun icrası barədə məhkəmənin g&ouml;stərişi alındığı vaxtdan on g&uuml;ndən gec olmayaraq cəzalarını &ccedil;əkmək &uuml;&ccedil;&uuml;n cəza&ccedil;əkmə m&uuml;əssisələrinə g&ouml;ndərilirlər. Məhkum bu m&uuml;ddət ərzində yaxın qohumları ilə bir qısam&uuml;ddətli g&ouml;r&uuml;ş h&uuml;ququna malikdir.</p>\r\n<p>67.2. Cəza&ccedil;əkmə m&uuml;əssisəsindən ke&ccedil;irilmiş məhkumlar istisna olmaqla cəzanın icrası m&uuml;ddəti məhkumun məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisəsində qeydiyyata alındığı vaxtdan hesablanır. Məhkumun qətimkan tədbiri kimi həbsdə saxlanma vaxtı, cəza&ccedil;əkmə m&uuml;əssisələrindən gətirilmə vaxtı cəzanın icrası m&uuml;ddətinə hesablanır. Məhkumun bir g&uuml;ndən artıq icazəsiz məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisəsinin ərazisində olmaması cəzanın icrası m&uuml;ddətinə hesablanmır.</p>\r\n<p><strong><em>Maddə 68. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslərin cəzanın icrası yerinə g&ouml;ndərilməsi qaydaları</em></strong></p>\r\n<p>68.1. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər cəzanın icrası yerlərinə və bir cəza&ccedil;əkmə m&uuml;əssisəsindən digərinə m&uuml;şayiətlə g&ouml;ndərilirlər. <em>Məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisəsinə g&ouml;ndərilən və h&ouml;km q&uuml;vvəyə mindiyi zaman həbsdə olmayan məhkum icra məmurunın təqdim etdiyi m&uuml;vafiq g&ouml;stərişə əsasən m&uuml;əyyən edilən vaxtda məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisəsinə şəxsən gəlməlidir. Məhkum &uuml;zrl&uuml; səbəb olmadan cəzanın icrası yerinə gəlmədikdə, cəzanı icra edən m&uuml;əssisənin və ya orqanın vəsatəti və m&uuml;vafiq prokurorun təqdimatı əsasında məhkəmə onun tutulması barədə qərar qəbul edə bilər.</em></p>\r\n<p>68.2. Məhkumların m&uuml;şayiətlə g&ouml;ndərilməsi ayrılıqda saxlanma qaydalarına riayət olunmaqla həyata ke&ccedil;irilir. Kişilər qadınlardan, yetkinlik yaşına &ccedil;atmayanlar yetkinlik yaşına &ccedil;atanlardan, eyni cinayət işi &uuml;zrə məhkum olunmuşlar ayrılıqda, vərəm xəstəliyinə tutulmuş (vərəm &ccedil;&ouml;pləri ifraz edənlər), z&ouml;hrəvi xəstəlikdən m&uuml;alicə kursunu tam ke&ccedil;məmiş şəxslər və psixi xəstələr bir-birindən ayrı-ayrılıqda və sağlam məhkumlardan təcrid olunmaqla tibb iş&ccedil;ilərinin m&uuml;şayiəti ilə g&ouml;ndərilirlər. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 69. Məhkumların təsərr&uuml;fat və məişət xidməti işi &uuml;&ccedil;&uuml;n istintaq təcridxanasında, m&uuml;alicə m&uuml;əssisəsində və ya həbsxanada saxlanması</em></strong></p>\r\n<p>69.1. İlk dəfə beş ildən artıq olmayan m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş və cəzasını &uuml;mumi rejimli cəza&ccedil;əkmə m&uuml;əssisələrində &ccedil;əkən şəxslər <span style="text-decoration: line-through;">m&uuml;stəsna hallarda</span> onların razılığı ilə təsərr&uuml;fat və məişət xidməti işi &uuml;&ccedil;&uuml;n istintaq təcridxanasında, m&uuml;alicə m&uuml;əssisəsində və ya həbsxanada saxlanıla bilərlər. <em>Əvvəllər məhkum edilmiş və yenidən yeddi ildən artıq olmayan m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər m&uuml;stəsna hallarda onların razılığı ilə təsərr&uuml;fat və məişət xidməti işi &uuml;&ccedil;&uuml;n m&uuml;alicə m&uuml;əssisələrində saxlanıla bilərlər.</em></p>\r\n<p>69.2. Məhkumun təsərr&uuml;fat və məişət xidməti işi &uuml;&ccedil;&uuml;n saxlanması istintaq təcridxanası, m&uuml;alicə m&uuml;əssisəsi və ya həbsxana m&uuml;diriyyətinin qərarı ilə həyata ke&ccedil;irilir.</p>\r\n<p>69.3. Təsərr&uuml;fat və məişət xidməti işi &uuml;&ccedil;&uuml;n istintaq təcridxanasında, m&uuml;alicə m&uuml;əssisəsində və ya həbsxanada saxlanılan məhkumlar, &uuml;mumi rejimli cəza&ccedil;əkmə m&uuml;əssisələri &uuml;&ccedil;&uuml;n bu Məcəllədə nəzərdə tutulan şərtlərlə başqa şəxslərdən təcrid olunmaqla saxlanılırlar. Bu məhkumlar bağlı bina və ya otaqlarda işlədikləri hallarda g&uuml;ndə iki saat m&uuml;ddətinə gəzinti h&uuml;ququna malikdirlər. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 69-1. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və ya &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslərin istintaq təcridxanalarına ke&ccedil;irilməsi </em></strong></p>\r\n<p><em>69-1.1. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və ya &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzalara məhkum olunmuş şəxslər digər şəxslər tərəfindən t&ouml;rədilmiş cinayət işləri &uuml;zrə istintaq hərəkətlərinin həyata ke&ccedil;irilməsi və ya digər şəxslər tərəfindən t&ouml;rədilmiş cinayət işlərinə məhkəmədə baxılması ilə əlaqədar məhkəmənin qərarına əsasən iki aydan &ccedil;ox olmayan m&uuml;ddətə cəza&ccedil;əkmə m&uuml;əssisəsindən istintaq təcridxanalarına ke&ccedil;irilə bilərlər. Zəruri hallarda bu m&uuml;ddət məhkəmənin qərarı ilə d&ouml;rd ayadək uzadıla bilər. Məhkəməyədək icraat zamanı g&ouml;stərilən məsələlərə ibtidai araşdırmaya prosessual rəhbərliyi həyata ke&ccedil;irən prokurorun təqdimatı əsasında məhkumun cəza &ccedil;əkdiyi yer &uuml;zrə birinci instansiya məhkəməsi tərəfindən <a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4191">Azərbaycan Respublikası Cinayət-Prosessual Məcəlləsinin</a></em><em> 520-ci maddəsi ilə nəzərdə tutulmuş qaydada baxılır.</em></p>\r\n<p><em>69-1.2. Bu Məcəllənin 69-1.1-ci maddəsinə əsasən cəza&ccedil;əkmə m&uuml;əssisələrindən istintaq təcridxanalarına ke&ccedil;irilmiş şəxslərin saxlanılma şərtləri həmin yerlərin saxlanma rejimi və onlara məhkəmənin h&ouml;km&uuml; ilə təyin edilmiş cəza&ccedil;əkmə m&uuml;əssisəsinin n&ouml;v&uuml; və rejimi nəzərə alınmaqla m&uuml;əyyən edilir. Belə halda məhkumlara bu Məcəllədə nəzərdə tutulmuş uzun m&uuml;ddətli g&ouml;r&uuml;şlər onların razılığı ilə qısa m&uuml;ddətli g&ouml;r&uuml;ş və ya telefon danışıqları ilə əvəz oluna bilər.</em></p>\r\n<p><em>69-1.3. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və ya &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzalara məhkum olunmuş şəxslər digər cinayət işləri &uuml;zrə ş&uuml;bhəli və ya təqsirləndirilən şəxs qismində cəlb olunduqda, onların cəza&ccedil;əkmə m&uuml;əssisələrindən istintaq təcridxanalarına ke&ccedil;irilməsi ibtidai araşdırmaya prosessual rəhbərliyi həyata ke&ccedil;irən prokurorun təqdimatı əsasında məhkəmənin qərarı ilə həyata ke&ccedil;irilir. Bu şəxslərin istintaq təcridxanalarında saxlanma m&uuml;ddəti <a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4191">Azərbaycan Respublikasının Cinayət-Prosessual Məcəlləsində</a></em><em> nəzərdə tutulmuş həbsdə saxlanma m&uuml;ddətləri daxilində m&uuml;əyyən edilir.</em> <a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i1#i1">(1)</a></p>\r\n<p><strong><em>Maddə 70. Cəza&ccedil;əkmə m&uuml;əssisəsinin n&ouml;v&uuml;n&uuml;n dəyişdirilməsi</em></strong></p>\r\n<p>70.1. &Ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər istisna olmaqla, davranışından və əməyə m&uuml;nasibətindən asılı olaraq məhkumlar cəza&ccedil;əkmə m&uuml;əssisəsinin bir n&ouml;v&uuml;ndən digərinə ke&ccedil;irilə bilərlər.</p>\r\n<p>70.2. M&uuml;sbət xarakterizə edilən məhkumlar cəzanın qalan hissəsini &ccedil;əkmək &uuml;&ccedil;&uuml;n cəza&ccedil;əkmə m&uuml;əssisəsinin bir n&ouml;v&uuml;ndən digərinə aşağıdakı hallarda ke&ccedil;irilə bilərlər:</p>\r\n<p>70.2.1. həbsxanadan cəza&ccedil;əkmə m&uuml;əssisələrinə&mdash;məhkəmənin h&ouml;km&uuml; ilə m&uuml;əyyən olunmuş həbs cəzasının ən azı yarısını &ccedil;əkdikdə;</p>\r\n<p><em>70.2.2. ciddi rejimli cəza&ccedil;əkmə m&uuml;əssisələrindən - &uuml;mumi rejimli cəza&ccedil;əkmə m&uuml;əssisələrinə - cəza m&uuml;ddətinin ən azı &uuml;&ccedil;də iki hissəsini &ccedil;əkdikdə</em><em>;(22)</em></p>\r\n<p><em>70.2.3. ilk dəfə ağır cinayət t&ouml;rətmiş məhkumlar &uuml;mumi rejimli cəza&ccedil;əkmə m&uuml;əssisələrindən - məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisələrinə - cəza m&uuml;ddətinin ən azı &uuml;&ccedil;də iki hissəsini &ccedil;əkdikdə; (22)<br /> 70.2.4. az ağır və ya b&ouml;y&uuml;k ictimai təhl&uuml;kə t&ouml;rətməyən cinayət t&ouml;rətmiş məhkumlar &uuml;mumi rejimli cəza&ccedil;əkmə m&uuml;əssisələrindən - məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisələrinə - cəza m&uuml;ddətinin ən azı &uuml;&ccedil;də birini &ccedil;əkdikdə. (22)</em></p>\r\n<p>70.3. Aşağıdakı məhkumlar məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisəsinə ke&ccedil;irilmirlər:</p>\r\n<p>70.3.1. cinayətlərin x&uuml;susi təhl&uuml;kəli residivinə g&ouml;rə məhkum olunmuş şəxslər;</p>\r\n<p>70.3.2. &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzası m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəza ilə əvəz olunan şəxslər;</p>\r\n<p>70.3.3. tibbi xarakterli məcburi tədbirlər ke&ccedil;məli olan şəxslər;</p>\r\n<p><em>70.3.4. ciddi rejimli cəza&ccedil;əkmə m&uuml;əssisələrindən &uuml;mumi rejimli cəza&ccedil;əkmə m&uuml;əssisələrinə ke&ccedil;irilmiş şəxslər.</em> (22)</p>\r\n<p>70.4. Cəzanın icrası qaydalarını qərəzli pozan məhkumlar:</p>\r\n<p>70.4.1. məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisəsindən məhkəmənin h&ouml;kmdə m&uuml;əyyən etdiyi rejim n&ouml;v&uuml;ndəki cəza&ccedil;əkmə m&uuml;əssisəsinə;</p>\r\n<p>70.4.2. məhkəmənin h&ouml;km&uuml; ilə məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisəsinə g&ouml;ndərilən şəxslər &uuml;mumi rejimli cəza&ccedil;əkmə m&uuml;əssisəsinə;</p>\r\n<p>70.4.3. &uuml;mumi, ciddi və x&uuml;susi rejimli cəza&ccedil;əkmə m&uuml;əssisələrindən &uuml;&ccedil; ildən &ccedil;ox olmayan m&uuml;ddətə həbsxanaya ke&ccedil;irilə bilərlər.</p>\r\n<p>70.5. Cəza&ccedil;əkmə m&uuml;əssisələrinin n&ouml;v&uuml;n&uuml;n dəyişdirilməsi məhkəmə tərəfindən həyata ke&ccedil;irilir. <a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i2#i2">(2)</a></p>\r\n<p><strong><em>Maddə 71. Məhkumların cəza&ccedil;əkmə m&uuml;əssisələrinə qəbul edilməsi qaydası</em></strong></p>\r\n<p>71.1. Məhkumlar cəza&ccedil;əkmə m&uuml;əssisələrinə cəza&ccedil;əkmə m&uuml;əssisələrinin Daxili İntizam Qaydalarına uyğun m&uuml;diriyyət tərəfindən qəbul edilirlər.</p>\r\n<p>71.2. Cəza&ccedil;əkmə m&uuml;əssisələrinə yeni qəbul edilmiş məhkumlar iki həftəyədək m&uuml;ddətə başqa məhkumlardan ayrı saxlanılırlar.</p>\r\n<p>71.3. Məhkumların ayrı saxlandığı m&uuml;ddətdə cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti tərəfindən onların <em>ana dilində və ya bildikləri başqa dildə</em> h&uuml;quq və vəzifələri, qanunla nəzərdə tutulmuş əmək və istirahət şəraiti izah olunmalı, cəzanın icrasını tənzimləyən qanunvericilik aktları və cəza&ccedil;əkmə m&uuml;əssisələrinin Daxili İntizam Qaydaları ilə tanış olmaları &uuml;&ccedil;&uuml;n şərait yaradılmalıdır. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 72. Məhkumların cəza&ccedil;əkmə m&uuml;əssisələrində ayrılıqda saxlanması</em></strong></p>\r\n<p>72.1. Cəza&ccedil;əkmə m&uuml;əssisələrində yetkinlik yaşına &ccedil;atmayanlar yetkinlik yaşına &ccedil;atanlardan, kişilər qadınlardan ayrılıqda saxlanılırlar.</p>\r\n<p>72.2. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya ilk dəfə məhkum olunmuş şəxslər əvvəllər məhkum olunmuş şəxslərdən ayrılıqda saxlanılırlar. Cinayətlərin x&uuml;susi təhl&uuml;kəli residivinə g&ouml;rə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər başqa məhkumlardan təcrid olunmaqla ayrılıqda saxlanılırlar.</p>\r\n<p>72.3. Məhkəmə və h&uuml;quq m&uuml;hafizə orqanlarının sabiq iş&ccedil;iləri x&uuml;susi cəza&ccedil;əkmə m&uuml;əssisəsində saxlanılırlar. Həmin m&uuml;əssisəyə bu Məcəllənin 12.3-c&uuml; maddəsinə uyğun olaraq başqa məhkumlar da g&ouml;ndərilə bilərlər.</p>\r\n<p>72.4. Məhkumların ayrılıqda saxlanmasının bu Məcəllənin 72.2-ci maddəsində m&uuml;əyyən edilmiş tələbləri azadlıqdan məhrum edilmiş şəxslər &uuml;&ccedil;&uuml;n nəzərdə tutulmuş m&uuml;alicə m&uuml;əssisəsinə, həm&ccedil;inin nəzdində uşaq evi olan cəza&ccedil;əkmə m&uuml;əssisəsinə aid edilmir. Qeyd edilən m&uuml;əssisələrə g&ouml;ndərilmiş şəxslər məhkəmənin təyin etdiyi və qanunla bu n&ouml;v cəza&ccedil;əkmə m&uuml;əssisəsi &uuml;&ccedil;&uuml;n m&uuml;əyyən edilmiş şərtlərə uyğun saxlanılırlar.</p>\r\n<p>72.5. M&uuml;xtəlif yoluxucu xəstəliklərə tutulmuş məhkumlar bir-birindən və sağlam məhkumlardan ayrılıqda saxlanılırlar.</p>\r\n<p>72.6. Cəzasını m&uuml;xtəlif rejimdə &ccedil;əkən məhkumlar bir cəza&ccedil;əkmə m&uuml;əssisəsində, lakin ayrılıqda və bir-birindən təcrid edilməklə saxlanıla bilərlər.</p>\r\n<p><strong><em>Maddə 73. Məhkumun cəzanı bir cəza&ccedil;əkmə m&uuml;əssisəsində &ccedil;əkməsi</em></strong></p>\r\n<p>73.1. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər bir qayda olaraq cəzalarının b&uuml;t&uuml;n m&uuml;ddətini bir cəza&ccedil;əkmə m&uuml;əssisəsində, həbsxanada və ya tərbiyə m&uuml;əssisəsində &ccedil;əkirlər.</p>\r\n<p>73.2. Məhkumun cəzanın &ccedil;əkilməmiş hissəsini &ccedil;əkmək &uuml;&ccedil;&uuml;n bir cəza&ccedil;əkmə m&uuml;əssisəsindən eyni n&ouml;vl&uuml; başqa cəza&ccedil;əkmə m&uuml;əssisəsinə və ya bir həbsxanadan digərinə ke&ccedil;irilməsinə məhkumun xəstəliyi ilə əlaqədar və ya onun təhl&uuml;kəsizliyini təmin etmək &uuml;&ccedil;&uuml;n, cəza&ccedil;əkmə m&uuml;əssisəsinin yenidən təşkili və ya ləğv edilməsi, həm&ccedil;inin məhkumun bu cəza&ccedil;əkmə m&uuml;əssisəsində qalmasına mane olan digər m&uuml;stəsna hallarda yol verilir. Məhkumların eyni rejimli bir cəza&ccedil;əkmə m&uuml;əssisəsindən digərinə ke&ccedil;irilmə qaydası <a title="Azərbaycan Respublikasının Ədliyyə Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanı</a> tərəfindən m&uuml;əyyən edilir.</p>\r\n<p><strong>X fəsil. Cəza&ccedil;əkmə m&uuml;əssisələrində rejim</strong></p>\r\n<p><strong><em>Maddə 74. Cəza&ccedil;əkmə m&uuml;əssisələrində rejim və onun əsas tələbləri</em></strong></p>\r\n<p>74.1. Cəza&ccedil;əkmə m&uuml;əssisələrində rejim qanunla və normativ h&uuml;quqi aktlarla m&uuml;əyyən olunmuş m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaların icrası və &ccedil;əkilməsi qaydasıdır. Rejim məhkumların m&uuml;hafizəsini və təcrid olunmasını, onlara daim nəzarət edilməsini, onların &uuml;zərinə qoyulmuş vəzifələrin yerinə yetirilməsini, onların h&uuml;quq və azadlıqlarının həyata ke&ccedil;irilməsini, iş&ccedil;i heyətinin və məhkumların şəxsi təhl&uuml;kəsizliyini, m&uuml;xtəlif kateqoriyalı məhkumların bir-birindən ayrı saxlanılmasını, cəza&ccedil;əkmə m&uuml;əssisəsinin məhkəmə tərəfindən təyin edilmiş n&ouml;v&uuml;ndən asılı olaraq, m&uuml;xtəlif saxlanılma şəraitini, cəzanın icrası şərtlərinin dəyişdirilməsini təmin edir.</p>\r\n<p>74.2. Cəza&ccedil;əkmə m&uuml;əssisələrində m&uuml;diriyyət tərəfindən cəza&ccedil;əkmə m&uuml;əssisələrinin Daxili İntizam Qaydalarında nəzərdə tutulmuş və məhkumlar &uuml;&ccedil;&uuml;n yerinə yetirilməsi məcburi olan <em>g&uuml;n b&ouml;lg&uuml;s&uuml;</em> m&uuml;əyyən edilir.</p>\r\n<p>74.3. Cəza&ccedil;əkmə m&uuml;əssisələrində məhkumlar m&uuml;əyyən olunmuş eyni formalı paltar geyinirlər. Geyim forması cəza&ccedil;əkmə m&uuml;əssisələrinin Daxili İntizam Qaydaları ilə m&uuml;əyyən edilir.</p>\r\n<p><em>74.4. Cəzanın icrası qaydalarını təmin etmək məqsədilə məhkumlara cəza&ccedil;əkmə m&uuml;əssisələrinin Daxili İntizam Qaydaları ilə m&uuml;əyyən edilmiş əşyaların verilməsi və ya &ouml;t&uuml;r&uuml;lməsi, habelə onlar tərəfindən belə əşyaların hazırlanması, saxlanılması, gəzdirilməsi, daşınması və ya istifadəsi qadağan olunur. Məhkumlarda, cəza&ccedil;əkmə m&uuml;əssisəsinə daxil olan və &ccedil;ıxan şəxslərdə, habelə məhkumların adına g&ouml;ndərilmiş bağlama və banderollarda belə əşyalar aşkar edildikdə, həmin əşyalar g&ouml;t&uuml;r&uuml;l&uuml;r və cinayət-prosessual qanunvericiliklə m&uuml;əyyən edilmiş qaydada m&uuml;vafiq araşdırma aparılır.</em></p>\r\n<p><em>74.5. Cəza&ccedil;əkmə m&uuml;əssisələrində məhkumlara &ouml;zlərində saxlamağa, əldə etməyə, habelə onlara bağlama və banderollarda g&ouml;ndərilməsinə, sovqatlarda gətirilməsinə icazə verilən əşya, mallar və ərzaq məhsullarının siyahısı cəza&ccedil;əkmə m&uuml;əssisələrinin Daxili İntizam Qaydaları ilə m&uuml;əyyən edilir.</em></p>\r\n<p><em>74.6. Bu Məcəllənin 74.5-ci maddəsində nəzərdə tutulanlar istisna olmaqla digər əşya, mallar və ərzaq məhsulları cəza&ccedil;əkmə m&uuml;əssisəsində aşkar edildikdə, onlar həmin m&uuml;əssisənin m&uuml;diriyyəti tərəfindən g&ouml;t&uuml;r&uuml;l&uuml;r, m&uuml;vafiq araşdırma aparılır və məhkumun hərəkətləri cəzaların icrası qaydalarının pozuntusu kimi qiymətləndirilə bilər. G&ouml;t&uuml;r&uuml;lm&uuml;ş əşya, mallar və ya ərzaq məhsulları məhkum azad olunanadək saxlanılması &uuml;&ccedil;&uuml;n anbara təhvil verilir və ya cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyətinin əsaslandırılmış qərarına əsasən məhv edilir. Bu barədə məhkuma məlumat verilməklə m&uuml;vafiq akt tərtib olunur. Cəza&ccedil;əkmə m&uuml;əssisəsinə daxil olan və ya &ccedil;ıxan şəxslərdə bu Məcəllənin 74.5-ci maddəsində nəzərdə tutulanlar istisna olmaqla digər əşya, mallar və ərzaq məhsulları aşkar edildikdə, qanunla bu əmələ g&ouml;rə məsuliyyət nəzərdə tutulmamışdırsa, belə əşyalar onları gətirmiş şəxslərə qaytarılır.</em> (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 75. Cəza&ccedil;əkmə m&uuml;əssisələrində texniki nəzarət vasitələri</em></strong></p>\r\n<p>75.1. Cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti məhkumlar tərəfindən cəzanın icrası yerindən qa&ccedil;manın və başqa cinayətlər t&ouml;rədilməsinin, cəzanın icrası qaydalarının pozulmasının qarşısını almaq, məhkumların davranışı haqqında məlumatlar əldə etmək &uuml;&ccedil;&uuml;n video və səsyazmadan, elektron və digər texniki vasitələrdən istifadə etmək h&uuml;ququna malikdir.</p>\r\n<p>75.2. Texniki nəzarət vasitələrinin tətbiq edilməsi, o c&uuml;mlədən bu vasitələrin onların həyat və sağlamlıqları &uuml;&ccedil;&uuml;n təhl&uuml;kə yaradacağı barədə, cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti məhkumları yazılı şəkildə xəbərdar edir.</p>\r\n<p>75.3. Texniki nəzarət vasitələrindən istifadə qaydası normativ h&uuml;quqi aktlarla m&uuml;əyyənləşdirilir.</p>\r\n<p>75.4. Cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti məhkumlara, onların &uuml;st-başına, əşya və geyimlərinə, həm&ccedil;inin cəza&ccedil;əkmə m&uuml;əssisəsinin ərazisindəki binalara baxış və onlarda axtarış ke&ccedil;irə bilər. Şəxsi axtarış məhkumlarla eyni cinsdən olan şəxs tərəfindən aparılır. Təxirəsalınmaz hallarda məhkumların yaşadığı binalarda gecə baxışı aparıla bilər.</p>\r\n<p>75.5. Cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti bu m&uuml;əssisəyə daxil olan və &ccedil;ıxan şəxslərə, onların əşyalarına, nəqliyyat vasitələrinə baxış ke&ccedil;irmək <span style="text-decoration: line-through;">və cəza&ccedil;əkmə m&uuml;əssisələrinin Daxili İntizam Qaydaları ilə m&uuml;əyyən edilmiş siyahı &uuml;zrə qadağan olunmuş əşya və sənədləri g&ouml;t&uuml;rmək</span> h&uuml;ququna malikdir.</p>\r\n<p>75.6. Baxış və axtarış tədbirlərinin ke&ccedil;irilməsi qaydası cəza&ccedil;əkmə m&uuml;əssisələrinin Daxili İntizam Qaydaları ilə m&uuml;əyyən edilir. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 76. Cəza&ccedil;əkmə m&uuml;əssisələrində əməliyyat-axtarış fəaliyyəti</em></strong></p>\r\n<p>76.1. Azərbaycan Respublikasının qanunvericiliyinə uyğun olaraq cəza&ccedil;əkmə m&uuml;əssisələrində əməliyyat-axtarış fəaliyyəti həyata ke&ccedil;irilir. Cəza&ccedil;əkmə m&uuml;əssisələrində əməliyyat-axtarış fəaliyyətinin vəzifələri aşağıdakılardır:</p>\r\n<p>76.1.1. iş&ccedil;i heyətinin, məhkumların və başqa şəxslərin təhl&uuml;kəsizliyini təmin etmək;</p>\r\n<p>76.1.2. cəzanın icrası qaydalarının pozulması hallarını aşkar etmək, cəza&ccedil;əkmə m&uuml;əssisələrində hazırlanan və t&ouml;rədilən cinayətlərin qarşısını almaq və onları a&ccedil;maq;</p>\r\n<p>76.1.3. cəza&ccedil;əkmə m&uuml;əssisələrindən qa&ccedil;mış, həm&ccedil;inin m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzanı &ccedil;əkməkdən boyun qa&ccedil;ıran şəxsləri m&uuml;əyyən edilmiş qaydada axtarmaq;</p>\r\n<p>75.1.4. məhkumların t&ouml;rətdikləri digər cinayətlərin aşkar edilməsi və a&ccedil;ılmasında h&uuml;quq-m&uuml;hafizə orqanlarına k&ouml;məklik g&ouml;stərmək.</p>\r\n<p>76.2. Cəza&ccedil;əkmə m&uuml;əssisələrində əməliyyat-axtarış fəaliyyətini cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti həyata ke&ccedil;irir. <a title="Azərbaycan Respublikasının Daxili İşlər Nazirliyi və Azərbaycan Respublikasının Milli Təhl&uuml;kəsizlik Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">M&uuml;vafiq icra hakimiyyəti orqanları</a> &ouml;z səlahiyyətlərinə aid edilmiş cinayət işlərinin təhqiqatı &uuml;zrə əməliyyat-axtarış fəaliyyətini cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti ilə birgə həyata ke&ccedil;irirlər.</p>\r\n<p>76.3. Cəza&ccedil;əkmə m&uuml;əssisəsində əməliyyat-axtarış fəaliyyətində qanunların icrasına nəzarəti Azərbaycan Respublikasının qanunvericiliyi ilə m&uuml;əyyən edilmiş qaydada Azərbaycan Respublikasının Baş prokuroru və onun tərəfindən m&uuml;vəkkil edilmiş prokurorlar həyata ke&ccedil;irirlər.</p>\r\n<p><strong><em>Maddə 77. Cəza&ccedil;əkmə m&uuml;əssisələrində f&ouml;vqəladə hallarda saxlanma şəraitinin x&uuml;susiyyətləri</em></strong></p>\r\n<p>77.1. Cəza&ccedil;əkmə m&uuml;əssisələrində m&uuml;diriyyətin, iş&ccedil;i heyətinin və ya məhkumların həyatı və sağlamlığı &uuml;&ccedil;&uuml;n təhl&uuml;kə yaradan k&uuml;tləvi iğtişaşlar, həm&ccedil;inin təbii fəlakət və ya digər f&ouml;vqəladə hadisə baş verdikdə <a title="Azərbaycan Respublikasının Ədliyyə Nazirliyi; Nax&ccedil;ıvan Muxtar Respublikası ərazisində Nax&ccedil;ıvan Muxtar Respublikasının Ədliyyə Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanının</a> qərarına əsasən x&uuml;susi saxlanma şəraiti tətbiq edilə bilər və ya məhkumlar eyni rejimli cəza&ccedil;əkmə m&uuml;əssisəsinə ke&ccedil;irilə bilər.</p>\r\n<p>77.2. Cəza&ccedil;əkmə m&uuml;əssisələrində x&uuml;susi saxlanma şəraitinin tətbiqi d&ouml;vr&uuml;ndə məhkumların bu Məcəllədə nəzərdə tutulan h&uuml;quqlarının həyata ke&ccedil;irilməsi, istehsal, kommunal-məişət, mədəni-maarif, tibbi-sanitar və başqa xidmətlərin fəaliyyəti bu Məcəllənin 77.1-ci maddəsində g&ouml;stərilən hallar aradan qaldırılanadək məhdudlaşdırıla bilər. Bu m&uuml;ddət ərzində məhkumlara g&ouml;r&uuml;ş verilməsi dayandırılır və cəza&ccedil;əkmə m&uuml;əssisəsinə buraxılış x&uuml;susi qaydada həyata ke&ccedil;irilir.</p>\r\n<p>77.3. Nax&ccedil;ıvan Muxtar Respublikasının ərazisində yerləşən cəza&ccedil;əkmə m&uuml;əssisələrində x&uuml;susi saxlanma şəraitinin tətbiqi <a title="Nax&ccedil;ıvan Muxtar Respublikasının Ədliyyə Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">Nax&ccedil;ıvan Muxtar Respublikasının m&uuml;vafiq icra hakimiyyəti orqanının</a> qərarı ilə də həyata ke&ccedil;irilə bilər. Bu halda qəbul edilmiş qərarın təsdiqi &uuml;&ccedil;&uuml;n dərhal <a title="Azərbaycan Respublikasının Ədliyyə Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">Azərbaycan Respublikasının m&uuml;vafiq icra hakimiyyəti orqanına</a> məlumat verilir.</p>\r\n<p>77.4. Təxirəsalınmaz hallarda cəza&ccedil;əkmə m&uuml;əssisələrinin m&uuml;diriyyəti bu Məcəllənin 77.2-ci maddəsində nəzərdə tutulan tədbirləri m&uuml;stəqil həyata ke&ccedil;irir və bu barədə dərhal <a title="Azərbaycan Respublikasının Ədliyyə Nazirliyi; Nax&ccedil;ıvan Muxtar Respublikası ərazisində Nax&ccedil;ıvan Muxtar Respublikasının Ədliyyə Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanına</a> məlumat verilir.</p>\r\n<p><strong><em>Maddə 78. Cəza&ccedil;əkmə m&uuml;əssisələrində təhl&uuml;kəsizlik tədbirləri və onların tətbiqinin əsasları</em></strong></p>\r\n<p>78.1. Cəza&ccedil;əkmə m&uuml;əssisələrinin iş&ccedil;i heyətinin qanuni tələblərinə tabe olmayan, onlara m&uuml;qavimət g&ouml;stərən, k&uuml;tləvi iğtişaşlarda, insanların girov g&ouml;t&uuml;r&uuml;lməsində iştirak edən, vətəndaşlara h&uuml;cum edən və ya digər ictimai təhl&uuml;kəli hərəkətlər t&ouml;rədən məhkumlara, həm&ccedil;inin məhkumların cəza&ccedil;əkmə m&uuml;əssisəsindən qa&ccedil;ması və ya qa&ccedil;anların tutulması zamanı g&ouml;stərilən h&uuml;quqazidd hərəkətlərin, habelə bu məhkumların ətrafdakı insanlara və ya &ouml;zlərinə ziyan vurmasının qarşısının alınması, hazırlanan və ya t&ouml;rədilən cinayətlərin qarşısının alınması məqsədilə təhl&uuml;kəsizlik tədbirləri (fiziki q&uuml;vvə, x&uuml;susi vasitələr, xidməti itlər və odlu silah) tətbiq edilə bilər.</p>\r\n<p>78.2. Cəza&ccedil;əkmə m&uuml;əssisələrində qalmaqal salan məhkumlara qarşı əl qandalı, iş&ccedil;i heyətinə fiziki m&uuml;qavimət g&ouml;stərən və ya digər zorakılıq hərəkətləri edən, habelə m&uuml;hafizə altından qa&ccedil;an məhkumlara qarşı isə fiziki q&uuml;vvə, x&uuml;susi vasitələr və xidməti itlər tətbiq edilə bilər.</p>\r\n<p>78.3. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər cəza&ccedil;əkmə m&uuml;əssisələrinin iş&ccedil;i heyətinin və ya başqa şəxslərin həyatı &uuml;&ccedil;&uuml;n bilavasitə təhl&uuml;kə t&ouml;rədən basqınlar, yaxud da qəsdən başqa hərəkətlər etdikdə, habelə m&uuml;hafizə altından qa&ccedil;dıqda, g&ouml;stərilən hərəkətlərin qarşısını başqa tədbirlərlə almaq m&uuml;mk&uuml;n olmadıqda, m&uuml;stəsna tədbir kimi odlu silah tətbiq edilməsinə yol verilir. Qadınlar və yetkinlik yaşına &ccedil;atmayan şəxslər cəza&ccedil;əkmə m&uuml;əssisəsindən qa&ccedil;dıqda, onlara qarşı odlu silah tətbiq edilməsinə yol verilmir.</p>\r\n<p>78.4. Odlu silah tətbiq edilməsi ilə əlaqədar cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti dərhal <a title="Azərbaycan Respublikasının Ədliyyə Nazirliyi; Nax&ccedil;ıvan Muxtar Respublikası ərazisində Nax&ccedil;ıvan Muxtar Respublikasının Ədliyyə Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanına</a> məlumat verməlidirlər.</p>\r\n<p>78.5. Cəza&ccedil;əkmə m&uuml;əssisəsində təhl&uuml;kəsizlik tədbirlərinin tətbiqi qaydası qanunvericiliklə m&uuml;əyyən edilir.</p>\r\n<p><strong>XI fəsil. Cəza&ccedil;əkmə m&uuml;əssisələrində cəzanın &ccedil;əkilməsi şərtləri </strong></p>\r\n<p><strong><em>Maddə 79. Məhkumların cəza &ccedil;əkmə şəraiti</em></strong></p>\r\n<p>79.1. Məhkumlar bir cəza&ccedil;əkmə m&uuml;əssisəsinin daxilində rejim n&ouml;v&uuml;ndən asılı olaraq adi və ya yaxşılaşdırılmış şəraitdə saxlanıla bilərlər.</p>\r\n<p>79.2. Məhkumlar bir saxlanılma şəraitindən digərinə bu Məcəllədə nəzərdə tutulmuş əsaslarla ke&ccedil;irilirlər.</p>\r\n<p><strong><em>Maddə 80. Məhkumların ərzaq məhsulları və ən zəruri mallar əldə etməsi</em></strong></p>\r\n<p>80.1. Cəza&ccedil;əkmə m&uuml;əssisələrində məhkumlar nağdsız haqq-hesabla şəxsi hesablarında olan vəsaitlə ərzaq məhsulları və ən zəruri mallar əldə edirlər.</p>\r\n<p>80.2. Cəza&ccedil;əkmə m&uuml;əssisələrində məhkumlara xərcləmək &uuml;&ccedil;&uuml;n icazə verilən vəsaitin məbləği bu Məcəllə ilə m&uuml;əyyən edilir.</p>\r\n<p>80.3. İş normalarını artıqlaması ilə yerinə yetirən və ya m&uuml;əyyən edilmiş tapşırıqları n&uuml;munəvi yerinə yetirən məhkumlara xərcləmək &uuml;&ccedil;&uuml;n icazə verilən vəsaitdən əlavə olaraq, ayda <em>on beş manat</em> miqdarında, ağır işlərdə, əmək şəraiti zərərli olan işlərdə, yaxud y&uuml;ksək dağlıq rayonlarda yerləşmiş cəza&ccedil;əkmə m&uuml;əssisələrində isə, əlavə olaraq ayda <em>otuz beş manat</em> miqdarında pul xərcləməyə icazə verilir.</p>\r\n<p>80.4. Birinci və ikinci qrup əlillərə<em>, sağlamlıq imkanları məhdud uşaqlara</em>, hamilə qadınlara, cəza&ccedil;əkmə m&uuml;əssisələrinin nəzdindəki uşaq evlərində uşaqları olan qadınlara, m&uuml;alicə m&uuml;əssisələrində və yoluxucu xəstəliyə tutulmuş şəxslərin saxlanılması və m&uuml;alicəsi &uuml;&ccedil;&uuml;n nəzərdə tutulan cəza&ccedil;əkmə m&uuml;əssisələrindəki məhkumlara əlavə olaraq ayda <em>on beş manat</em> miqdarında ərzaq məhsulları və ən zəruri mallar əldə etməyə icazə verilir.</p>\r\n<p>80.5. Cəza&ccedil;əkmə m&uuml;əssisələrində məhkumlara <em>ticarət şəbəkəsi vasitəsilə satışına icazə verilən</em> ərzaq məhsullarının və digər malların siyahısı cəza&ccedil;əkmə m&uuml;əssisələrinin Daxili İntizam Qaydaları ilə m&uuml;əyyən edilir. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i14#i14">14</a>, <a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i15#i15">15</a>, <a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 81. Məhkumların qohumları, vəkilləri və digər şəxslərlə g&ouml;r&uuml;ş&uuml;</em></strong></p>\r\n<p>81.1. Məhkumlara cəza&ccedil;əkmə m&uuml;əssisəsinin ərazisində d&ouml;rd saatadək qısam&uuml;ddətli və bir g&uuml;ndən &uuml;&ccedil; g&uuml;nədək uzunm&uuml;ddətli g&ouml;r&uuml;şlər verilir.</p>\r\n<p>81.2. Qısam&uuml;ddətli g&ouml;r&uuml;şlər qohumlarla və başqa şəxslərlə cəza&ccedil;əkmə m&uuml;əssisəsinin n&uuml;mayəndəsinin iştirakı ilə verilir.</p>\r\n<p>81.3. Məhkumlara uzunm&uuml;ddətli g&ouml;r&uuml;şlər birlikdə yaşamaq h&uuml;ququ ilə yaxın qohumlarla (arvadı, əri, valideynləri, babası, nənəsi, &ouml;vladları, &ouml;vladlığa g&ouml;t&uuml;rənləri və g&ouml;t&uuml;rd&uuml;kləri, nəvələri, qardaşları və bacıları) verilir. Məhkumların xahişi ilə uzun m&uuml;ddətli g&ouml;r&uuml;şlər qısa m&uuml;ddətli g&ouml;r&uuml;şlərlə əvəz edilə bilər.</p>\r\n<p>81.4. Məhkumlara m&uuml;sbət təsir g&ouml;stərə biləcək digər şəxslərlə g&ouml;r&uuml;ş &uuml;&ccedil;&uuml;n cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti tərəfindən şərait yaradılır.</p>\r\n<p>81.5. G&ouml;r&uuml;şlərin sayı və n&ouml;vləri bu Məcəllə ilə m&uuml;əyyən edilir.</p>\r\n<p>81.6. Ağır xəstəliyi ilə əlaqədar həyatı &uuml;&ccedil;&uuml;n təhl&uuml;kə yaranan məhkumun yaxın qohumlarına ona baş &ccedil;əkmək &uuml;&ccedil;&uuml;n cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti tərəfindən imkan yaradılır.</p>\r\n<p>81.7. H&uuml;quqi yardım almaq &uuml;&ccedil;&uuml;n məhkumların &ouml;zlərinin, yaxın qohumlarının və ya onların qanuni n&uuml;mayəndələrinin ərizəsinə əsasən məhkumlara vəkillərlə, habelə belə yardımı g&ouml;stərmək h&uuml;ququna malik olan digər şəxslərlə g&ouml;r&uuml;ş verilir.</p>\r\n<p>81.8. Məhkumlarla bu Məcəllənin 81.7-ci maddəsi ilə m&uuml;əyyən edilmiş g&ouml;r&uuml;şlərin sayı və m&uuml;ddəti məhdudlaşdırılmır və cəza&ccedil;əkmə m&uuml;əssisələrinin Daxili İntizam Qaydalarına riayət edilməklə həyata ke&ccedil;irilir. Belə g&ouml;r&uuml;şlər bu Məcəllə ilə m&uuml;əyyən edilmiş g&ouml;r&uuml;şlərin sayına daxil edilmir.</p>\r\n<p>81.9. Vəkil və ya h&uuml;quqi yardım g&ouml;stərmək h&uuml;ququna malik olan digər şəxs cəza&ccedil;əkmə m&uuml;əssisəsinə onun şəxsiyyətini və səlahiyyətlərini təsdiq edən sənədi təqdim etdikdə buraxılır. Belə g&ouml;r&uuml;şlər tərəflərin arzusu ilə təklikdə ke&ccedil;irilir.</p>\r\n<p><strong><em>Maddə 82. Məhkumların bağlama, sovqat və banderol alması</em></strong></p>\r\n<p>82.1. Cəza&ccedil;əkmə m&uuml;əssisələrində məhkumlar bu Məcəllə ilə m&uuml;əyyən edilmiş sayda bağlama, sovqat və ya banderol alırlar. Bir bağlama və ya banderolun yuxarı &ccedil;əkisi m&ouml;vcud po&ccedil;t g&ouml;ndərişləri qaydaları ilə m&uuml;əyyən edilir. Bir sovqatın &ccedil;əkisi bir bağlama &uuml;&ccedil;&uuml;n m&uuml;əyyən olunmuş &ccedil;əkidən artıq olmamalıdır.</p>\r\n<p>82.2. Ağır xəstə, hamilə və uşaq evlərində uşaqları olan qadın məhkumların, birinci və ikinci qrup əlilliyi<em>, 18 yaşınadək sağlamlıq imkanlarının məhdudluğu</em> olan məhkumların bağlama, sovqat və ya banderol almalarına məhdudiyyət qoyulmur.</p>\r\n<p>82.3. Tibbi rəyə əsasən məhkumların aldığı dərman preparatları və tibbi məmulatlar bu Məcəllənin 82.1-ci maddəsi ilə m&uuml;əyyən olunmuş bağlama, sovqat və ya banderolların sayına daxil edilmir. Belə dərman preparatları və tibbi məmulatlar cəza&ccedil;əkmə m&uuml;əssisələrinin tibb-sanitar hissəsinə məhkumların m&uuml;alicəsi &uuml;&ccedil;&uuml;n verilir.</p>\r\n<p>82.4. Bağlama, sovqat və banderollara m&uuml;diriyyət tərəfindən baxış ke&ccedil;irilir.</p>\r\n<p>82.5. Məhkumların bağlama, sovqat və ya banderol alması və onlara baxış ke&ccedil;irilməsi qaydası cəza&ccedil;əkmə m&uuml;əssisələrinin Daxili İntizam Qaydaları ilə m&uuml;əyyən edilir. <em>Məhkumların adına g&ouml;ndərilmiş bağlama və banderollarda bu Məcəllənin 74.5-ci maddəsində nəzərdə tutulanlar istisna olmaqla digər əşya, mallar və ərzaq məhsulları aşkar edildikdə onlar g&ouml;t&uuml;r&uuml;l&uuml;r. Bu əşya, mallar və ya ərzaq məhsulları məhkum azad olunanadək saxlanılması &uuml;&ccedil;&uuml;n anbara təhvil verilir və ya bağlama və banderolu g&ouml;ndərmiş şəxsin hesabına geri qaytarılır, yaxud cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyətinin əsaslandırılmış qərarına əsasən məhv edilir.</em></p>\r\n<p>82.6. Cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyətinin razılığı ilə məhkumlar yaxın qohumlarına bağlama və ya banderol g&ouml;ndərə bilərlər. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i15#i15">15</a>, <a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 83. Məhkumların yazışması, pul baratlarının alınması və g&ouml;ndərilməsi </em></strong></p>\r\n<p>83.1. Bu Məcəllənin 83.3-c&uuml; maddəsində nəzərdə tutulan hallardan başqa məhkumlara məhdudiyyətsiz sayda məktub, teleqram almağa və g&ouml;ndərməyə icazə verilir. Məhkumlar məktub və teleqramları &ouml;z hesablarına g&ouml;ndərirlər.</p>\r\n<p><em>83.2. Məhkumların m&uuml;dafiə&ccedil;i və ya qanuni əsaslarla h&uuml;quqi yardım g&ouml;stərən digər şəxslərlə yazışmaları istisna olmaqla, onlar tərəfindən alınan və g&ouml;ndərilən yazışmalar hazırlanan cinayətlərin qarşısını almaq, cinayət təqibini, cəzanın &ccedil;əkilməsi qaydasını və şəxslərin həyat və təhl&uuml;kəsizliyini təmin etmək məqsədilə cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti tərəfindən senzuradan ke&ccedil;irilə bilər. Yazışmadakı məlumatların cinayətlərin t&ouml;rədilməsinə y&ouml;nəlməsi barədə cəza&ccedil;əkmə m&uuml;əssisəsi m&uuml;diriyyətinin əsaslı məlumata malik olduğu hallarda məhkumların yazışmaları &uuml;zərində nəzarət cəza&ccedil;əkmə m&uuml;əssisəsi m&uuml;diriyyətinin əsaslandırılmış qərarı ilə həyata ke&ccedil;irilir. Qanunvericiliklə qadağan olunmuş əşyaların &ouml;t&uuml;r&uuml;lməsinin qarşısını almaq məqsədilə məhkumların adına gələn və onlar tərəfindən g&ouml;ndərilən məktublar cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti tərəfindən a&ccedil;ıla bilər. Bu halda həmin məktubların oxunmasına yol verilmir.</em></p>\r\n<p>83.3. Cəza&ccedil;əkmə m&uuml;əssisələrində saxlanılan və qohum olmayan məhkumlar arasında yazışmalara cəza&ccedil;əkmə m&uuml;əssisəsi m&uuml;diriyyətinin razılığı ilə icazə verilir.</p>\r\n<p>83.4. Məhkumların adına gəlmiş məktubların verilməsi, habelə &uuml;nvan sahiblərinə məhkumların məktublarının g&ouml;ndərilməsi məktubun gəldiyi və ya məhkum tərəfindən verildiyi g&uuml;ndən ən geci &uuml;&ccedil; g&uuml;n m&uuml;ddətində cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti tərəfindən həyata ke&ccedil;irilir. Teleqramlar məhkuma cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti tərəfindən təxirə salınmadan verilir.</p>\r\n<p>83.5. Cəza&ccedil;əkmə m&uuml;əssisələrinin fəaliyyətinə nəzarəti həyata ke&ccedil;irən orqanlara<em>, Azərbaycan Respublikasının İnsan h&uuml;quqları &uuml;zrə m&uuml;vəkkilinə (ombudsmana)</em><em>, Milli preventiv qrupa və İnsan H&uuml;quqları &uuml;zrə Avropa Məhkəməsinə</em> &uuml;nvanlanmış təklif, ərizə və şikayətlər senzuradan ke&ccedil;irilmir və bir g&uuml;ndən gec olmayaraq (<em>istirahət, səsvermə, iş g&uuml;n&uuml; hesab edilməyən bayram g&uuml;nləri və &uuml;mumxalq h&uuml;zn g&uuml;n&uuml;</em> istisna olmaqla) aidiyyəti &uuml;zrə g&ouml;ndərilir. &nbsp;(25)</p>\r\n<p>83.6. Məhkumlar məhdudiyyətsiz pul baratları almaq, həm&ccedil;inin şəxsi hesablarından yaxın qohumlarına pul baratları g&ouml;ndərmək h&uuml;ququna malikdirlər. Başqa şəxslərə məhkumlar cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyətinin icazəsi ilə pul baratları g&ouml;ndərə bilərlər. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i6#i6">6</a>, <a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i7#i7">7</a>, <a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i10#i10">10</a>, <a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 84. Məhkumların telefon danışıqları</em></strong></p>\r\n<p>84.1. <em>M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum edilmiş şəxslər həftədə bir dəfə, &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum edilmiş şəxslər isə ayda iki dəfə m&uuml;ddəti on beş dəqiqə olmaqla telefonla danışmaq h&uuml;ququna malikdirlər.</em> Şəhərlərarası telefon danışıqlarının haqqı məhkumun şəxsi vəsaiti hesabına və ya yaxın qohumları tərəfindən &ouml;dənilir. Texniki imkanlar olmadıqda bir telefon danışığı bir qısa m&uuml;ddətli g&ouml;r&uuml;şlə əvəz edilə bilər.</p>\r\n<p>84.2. Məhkumun xahişi ilə onun yeni cəza&ccedil;əkmə m&uuml;əssisəsinə gəlməsi ilə əlaqədar, həm&ccedil;inin m&uuml;stəsna hallarda ona cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti tərəfindən əlavə telefon danışığına icazə verilə bilər.</p>\r\n<p>84.3. Tənbeh tədbiri kimi intizam və ya cərimə təcridxanalarında, kamera tipli otaqlarda və birnəfərlik kameralarda saxlanılan məhkumların telefon danışıqlarına yalnız m&uuml;stəsna hallarda icazə verilə bilər.</p>\r\n<p>84.4. Cəza&ccedil;əkmə m&uuml;əssisələrində saxlanılan məhkumlar arasında <em>yaxın qohumlar istisna olmaqla</em> telefon danışıqlarına icazə verilmir.</p>\r\n<p>84.5. Məhkumların telefon danışıqlarına cəza&ccedil;əkmə m&uuml;əssisəsinin iş&ccedil;i heyəti tərəfindən nəzarət edilir. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 85. Məhkumların gəzintisi</em></strong></p>\r\n<p>85.1. A&ccedil;ıq havada işləməyən, bağlı otaq və ya kamerada saxlanılan məhkumlar bir saatdan az olmayan m&uuml;ddətə hər g&uuml;n gəzinti h&uuml;ququna malikdirlər.</p>\r\n<p>85.2. Məhkumların gəzintisi g&uuml;nd&uuml;z vaxtı, cəza&ccedil;əkmə m&uuml;əssisəsi ərazisinin x&uuml;susi təchiz olunmuş hissəsində hər bağlı otaq və ya kamera &uuml;&ccedil;&uuml;n ayrılıqda ke&ccedil;irilir. Gəzinti zamanı m&uuml;vafiq qaydalar məhkum tərəfindən pozulduqda gəzinti vaxtından əvvəl dayandırıla bilər.</p>\r\n<p><strong><em>Maddə 86. Məhkumların ədəbiyyat və yazı ləvazimatı əldə etməsi və saxlaması</em></strong></p>\r\n<p>86.1. Məhkumlara bağlama, sovqat və ya banderol ilə, habelə şəxsi hesablarında olan vəsait hesabına məhdudiyyət qoyulmadan yazı ləvazimatı, ədəbiyyat almağa, qəzet və jurnallara abunə yazılmağa icazə verilir.</p>\r\n<p>86.2. Əldə olunmuş ədəbiyyat bu Məcəllə ilə m&uuml;əyyən edilmiş bağlama və banderolların sayına daxil edilmir. Məhkumlara on cildədək kitab (jurnal) saxlamağa icazə verilir.</p>\r\n<p>86.3. Məhkumların m&uuml;haribəni, zorakılığı<em>, ekstremizmi, terroru</em> və qəddarlığı təbliğ edən, <em>milli, dini və irqi</em> ədavəti qızışdıran, habelə pornoqrafiya xarakterli nəşrləri əldə etməsi, saxlaması, onlara abunə yazılması və onları yayması qadağandır. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 87. Məhkumların m&uuml;şayiətsiz gediş-gəlişi</em></strong></p>\r\n<p>87.1. Təsərr&uuml;fat və ya məişət xidməti &uuml;&ccedil;&uuml;n cəza&ccedil;əkmə m&uuml;əssisələrində m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəza &ccedil;əkən və m&uuml;sbət xarakterizə olunan məhkumlara cəza m&uuml;ddətinin ən azı &uuml;&ccedil;də bir hissəsini &ccedil;əkdikdən sonra onların g&ouml;rd&uuml;y&uuml; işin x&uuml;susiyyətinə g&ouml;rə zəruri hallarda m&uuml;hafizə olunan ərazidən kənarda m&uuml;şayiətsiz hərəkət etməyə icazə verilə bilər.</p>\r\n<p>87.2. Cinayətlrin x&uuml;susi təhl&uuml;kəli residivinə g&ouml;rə məhkum olunmuş şəxslərə, &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəza &ccedil;əkən şəxslərə, &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzası m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəza ilə əvəz olunmuş şəxslərə, tibbi xarakterli məcburi tədbirlər ke&ccedil;məli olan şəxslərə, bir cəza&ccedil;əkmə m&uuml;əssisəsində altı aydan az m&uuml;ddətə cəza &ccedil;əkən məhkumlara, tənbehi olan məhkumlara, cəzanın icrası d&ouml;vr&uuml;ndə qəsdən t&ouml;rədilmiş cinayətlərə g&ouml;rə yenidən məhkum olunmuşlara, vərəm xəstəliyinə tutulmuş (vərəm &ccedil;&ouml;pləri ifraz edənlər) və z&ouml;hrəvi xəstəliklərdən tam m&uuml;alicə kursu ke&ccedil;məyən məhkumlara m&uuml;hafizə olunan ərazidən kənarda m&uuml;şayiətsiz hərəkət etməyə icazə verilmir.</p>\r\n<p>87.3. Məhkuma m&uuml;hafizə olunan ərazidən kənarda m&uuml;şayiətsiz hərəkət etmək &uuml;&ccedil;&uuml;n icazə cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyətinin əsaslandırılmış və <a title="Azərbaycan Respublikasının Ədliyyə Nazirliyi; Nax&ccedil;ıvan Muxtar Respublikası ərazisində Nax&ccedil;ıvan Muxtar Respublikasının Ədliyyə Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanı</a> ilə razılaşdırılmış qərarına əsasən verilir.</p>\r\n<p>87.4. M&uuml;şayiətsiz hərəkət etməyə icazə verilmiş məhkumlar ayrı yaşayış binalarında yerləşdirilə bilərlər.</p>\r\n<p>87.5. M&uuml;hafizə olunan ərazidən kənarda m&uuml;şayiətsiz hərəkət etməyə icazə verilmiş məhkumların davranışı cəza&ccedil;əkmə m&uuml;əssisələrinin Daxili İntizam Qaydaları ilə tənzimlənir.</p>\r\n<p>87.6. Məhkum tərəfindən davranış qaydaları pozulduqda və ya işin x&uuml;susiyyəti dəyişdikdə m&uuml;şayiətsiz hərəkət etməyə icazə cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyətinin əsaslandırılmış və <a title="Azərbaycan Respublikasının Ədliyyə Nazirliyi; Nax&ccedil;ıvan Muxtar Respublikası ərazisində Nax&ccedil;ıvan Muxtar Respublikasının Ədliyyə Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanı</a> ilə razılaşdırılmış qərarı ilə ləğv olunur.</p>\r\n<p><strong><em>Maddə 88. Məhkum qadınların cəza&ccedil;əkmə m&uuml;əssisəsindən kənarda yaşaması</em></strong></p>\r\n<p>88.1. Əməyə vicdanlı m&uuml;nasibət bəsləyən və cəza&ccedil;əkmə rejiminin tələblərinə riayət edən, habelə cəza m&uuml;ddətinin ən azı &uuml;&ccedil;də birini &ccedil;əkmiş məhkum qadınlara cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyətinin əsaslandırılmış və <a title="Azərbaycan Respublikasının Ədliyyə Nazirliyi; Nax&ccedil;ıvan Muxtar Respublikası ərazisində Nax&ccedil;ıvan Muxtar Respublikasının Ədliyyə Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanı</a> ilə razılaşdırılmış qərarı ilə hamiləliyə və ya doğuşa g&ouml;rə qanunla m&uuml;əyyən edilmiş qaydada, işdən azad edildikləri vaxt ərzində, habelə uşaq &uuml;&ccedil; yaşına &ccedil;atanadək cəza&ccedil;əkmə m&uuml;əssisəsindən kənarda yaşamağa icazə verilə bilər.</p>\r\n<p>88.2. Cəza&ccedil;əkmə m&uuml;əssisəsindən kənarda yaşamağa icazə verilmiş məhkum qadınlar:</p>\r\n<p>88.2.1. cəza&ccedil;əkmə m&uuml;əssisəsinin ərazisi yaxınlığında yaşamağa yerləşdirilir və m&uuml;diriyyətin daimi m&uuml;şahidəsi altında olurlar;</p>\r\n<p>88.2.2. adi həyatda işlədilən paltar geyinə bilərlər;</p>\r\n<p>88.2.3. məhdudiyyətsiz halda yazışa bilər, pul baratları ala bilər və nağd puldan istifadə edə bilər, qohumları, həkimi ilə və başqa şəxslərlə g&ouml;r&uuml;şə bilər, bağlama, sovqat və ya banderol ala bilərlər;</p>\r\n<p>88.2.4. h&uuml;dudları cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti tərəfindən m&uuml;əyyən edilən ərazi daxilində sərbəst gediş-gəliş h&uuml;ququndan istifadə edə bilərlər.</p>\r\n<p>88.3. Cəza&ccedil;əkmə rejimi və ya davranış qaydaları məhkum qadın tərəfindən m&uuml;ntəzəm və ya qəsdən pozulduqda, cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyətinin əsaslandırılmış və <a title="Azərbaycan Respublikasının Ədliyyə Nazirliyi; Nax&ccedil;ıvan Muxtar Respublikası ərazisində Nax&ccedil;ıvan Muxtar Respublikasının Ədliyyə Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanı</a> ilə razılaşdırılmış qərarına əsasən cəza&ccedil;əkmə m&uuml;əssisəsindən kənarda yaşamaq &uuml;&ccedil;&uuml;n verilən icazə ləğv edilir, məhkum qadın isə cəzasını &ccedil;əkmək &uuml;&ccedil;&uuml;n cəza&ccedil;əkmə m&uuml;əssisəsinə qaytarılır.</p>\r\n<p><strong><em>Maddə 89. Cəza&ccedil;əkmə m&uuml;əssisəsinin h&uuml;dudlarından kənarda qısa m&uuml;ddətli səfərlər</em></strong></p>\r\n<p>89.1. Məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisələrində, ilk dəfə qəsdən t&ouml;rədilmiş b&ouml;y&uuml;k ictimai təhl&uuml;kə t&ouml;rətməyən və ya az ağır cinayətlərə g&ouml;rə, habelə ehtiyatsızlıqdan t&ouml;rədilmiş cinayətlərə g&ouml;rə məhkum olunmuş &uuml;mumi rejimli cəza&ccedil;əkmə m&uuml;əssisələrində və tərbiyə m&uuml;əssisələrində saxlanılan məhkumlara onların yaxın qohumlarının &ouml;l&uuml;m&uuml; və ya həyatı &uuml;&ccedil;&uuml;n təhl&uuml;kə yaradan ağır xəstəliyi ilə əlaqədar, məhkumlara və ya onların ailəsinə təbii fəlakətlə və digər f&ouml;vqəladə hadisə nəticəsində əhəmiyyətli maddi zərər vurulduqda cəza&ccedil;əkmə m&uuml;əssisələrinin h&uuml;dudlarından kənara (yalnız &ouml;lkə daxilində <em>və f&ouml;vqəladə vəziyyət ləğv edildikdən sonra</em>) yeddi g&uuml;n m&uuml;ddətinə qısa m&uuml;ddətli səfərə getməsinə icazə verilə bilər. Bu zaman gedib-gəlmək &uuml;&ccedil;&uuml;n sərf olunan vaxt, iki g&uuml;ndən &ccedil;ox olmamaq şərti ilə, nəzərə alınmır.</p>\r\n<p>89.2. Bu Məcəllənin 89.1-ci maddəsində nəzərdə tutulmuş hallarda məhkumun qısa m&uuml;ddətli səfər haqqındakı ərizəsinə təxirə salınmadan baxılmalıdır. Qısa m&uuml;ddətli səfərə cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti, <a title="&ouml;z səlahiyyəti daxilində Azərbaycan Respublikasının Ədliyyə Nazirliyi və intizam xarakterli hərbi hissədə saxlama cəza n&ouml;v&uuml;nə aid hissədə Azərbaycan Respublikasının M&uuml;dafiə Nazirliyi; Nax&ccedil;ıvan Muxtar Respublikası ərazisində Nax&ccedil;ıvan Muxtar Respublikasının" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanı</a> ilə razılaşdırıldıqdan sonra məhkumun şəxsiyyətini və davranışını nəzərə almaqla icazə verir. Qısa m&uuml;ddətli səfərə icazə verilməməsi əsaslandırılmalıdır. Məhkumun cəza&ccedil;əkmə m&uuml;əssisəsinin h&uuml;dudlarından kənarda olduğu vaxt cəzanın icrası m&uuml;ddətinə hesablanır. Məhkumun yol xərcini &ouml;z&uuml; və ya qohumları &ouml;dəyir. Məhkumun cəza&ccedil;əkmə m&uuml;əssisəsinin h&uuml;dudlarından kənarda olduğu vaxt ərzində ona əmək haqqı verilmir.</p>\r\n<p>89.3. Cəza&ccedil;əkmə m&uuml;əssisələrindəki uşaq evlərində uşaqları olan qadınlara &ouml;z məzuniyyətləri vaxtı &ouml;vladlarını qohumlarının, himayə&ccedil;ilərinin yanına və ya uşaq evlərinə d&uuml;zəltmək &uuml;&ccedil;&uuml;n cəza&ccedil;əkmə m&uuml;əssisələrinin h&uuml;dudlarından kənara (yalnız &ouml;lkə daxilində) on beş g&uuml;nədək qısa m&uuml;ddətli səfərə getməsinə icazə verilə bilər.</p>\r\n<p>89.4. İlk dəfə qəsdən t&ouml;rədilmiş b&ouml;y&uuml;k ictimai təhl&uuml;kə t&ouml;rətməyən və ya az ağır cinayətlərə g&ouml;rə, habelə ehtiyatsızlıqdan t&ouml;rədilmiş cinayətlərə g&ouml;rə məhkum olunmuş və &uuml;mumi rejimli cəza&ccedil;əkmə m&uuml;əssisələrində və ya &uuml;mumi rejimli tərbiyə m&uuml;əssisələrində bu Məcəllənin 113.3 və 124.3-c&uuml; maddələrinə əsasən saxlanılma şəraiti yaxşılaşdırılan məhkumlara ildə bir dəfə məzuniyyətləri vaxtı <a title="Azərbaycan Respublikasının Ədliyyə Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanının</a> m&uuml;əyyən etdiyi qaydada cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyətinin qərarı ilə cəza&ccedil;əkmə m&uuml;əssisələrinin h&uuml;dudlarından kənara (yalnız &ouml;lkə daxilində) yeddi g&uuml;nədək m&uuml;ddətə, yola sərf olunan iki g&uuml;nədək m&uuml;ddət nəzərə alınmamaqla, qısa m&uuml;ddətli səfər etməyə icazə verilə bilər.</p>\r\n<p><em>89.4-1. Məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisəsində cəza &ccedil;əkən və cəza&ccedil;əkmə m&uuml;əssisəsindən kənarda, yaxud cəza&ccedil;əkmə m&uuml;əssisəsinin istehsalat sahəsində əmək fəaliyyəti ilə məşğul olan məhkuma n&uuml;munəvi davranışına, əməyə və təhsilə vicdanlı m&uuml;nasibətinə, təlim-tərbiyə tədbirlərində fəal iştirakına g&ouml;rə cəza&ccedil;əkmə m&uuml;əssisəsi m&uuml;diriyyətinin qərarı ilə əmək məzuniyyətini cəza&ccedil;əkmə m&uuml;əssisəsinin h&uuml;dudlarından kənarda (yalnız Azərbaycan Respublikasının ərazisi h&uuml;dudlarında) ke&ccedil;irməyə icazə verilməlidir.</em> (22)</p>\r\n<p>89.5. Məhkumların cəza&ccedil;əkmə m&uuml;əssisələrinin h&uuml;dudlarından kənara qısa m&uuml;ddətli səfərə buraxılması qaydası <a title="&ouml;z səlahiyyəti daxilində Azərbaycan Respublikasının Ədliyyə Nazirliyi və intizam xarakterli hərbi hissədə saxlama cəza n&ouml;v&uuml;nə aid hissədə Azərbaycan Respublikasının M&uuml;dafiə Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanı</a> tərəfindən m&uuml;əyyən edilir. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i8#i8">8</a>)</p>\r\n<p><strong><em>Maddə 90. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslərin məcburi d&ouml;vlət sosial sığortası və pensiya təminatı</em></strong></p>\r\n<p>90.1. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş və işləyən şəxslər, d&ouml;vlət məcburi sığortalanmasına cəlb edilir, qadın məhkumlar isə bundan əlavə &uuml;mumi əsaslarla hamiləliyə və ya doğuşa g&ouml;rə m&uuml;avinətlə təmin olunurlar.</p>\r\n<p>90.2. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər &uuml;mumi əsaslarla qanunvericilikdə nəzərdə tutulmuş hallarda pensiya təminatı h&uuml;ququna malikdirlər.</p>\r\n<p>90.3. Məhkumların pensiyalarından qanunvericiliklə m&uuml;əyyən edilmiş əsaslarla və miqdarda tutulmalar aparılır. Pensiyadan tutulmaların əsasları, n&ouml;vləri, həm&ccedil;inin b&uuml;t&uuml;n tutulmalardan asılı olmayaraq məhkumun şəxsi hesabına ke&ccedil;irilən pensiyanın minimum məbləği bu Məcəllənin 99.3-c&uuml; maddəsi ilə m&uuml;əyyən edilir.</p>\r\n<p><strong><em>Maddə 91. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslərin maddi məişət təminatı</em></strong></p>\r\n<p>91.1. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər cəza&ccedil;əkmə m&uuml;əssisələrində sanitariya və gigiyena qaydalarına uyğun zəruri maddi-məişət şəraiti ilə təmin olunurlar.</p>\r\n<p>91.2. Cəza&ccedil;əkmə m&uuml;əssisələrində, tərbiyə m&uuml;əssisələrində və həbsxanalarda hər məhkum &uuml;&ccedil;&uuml;n yaşayış sahəsinin norması d&ouml;rd kvardatmetrdən, m&uuml;alicə m&uuml;əssisələrində isə beş kvadratmetrdən az ola bilməz.</p>\r\n<p>91.3. Məhkumlara fərdi yataq yeri və yataq ləvazimatı verilir. Onlar cinsləri, m&ouml;vs&uuml;m və iqlim şəraiti nəzərə alınmaqla <em>d&ouml;vlət hesabına</em> geyim, alt paltarı və ayaqqabı ilə təmin edilirlər.</p>\r\n<p>91.4. Məhkumlar g&uuml;n ərzində orqanizmin normal fəaliyyətini təmin edən &uuml;&ccedil; dəfə yeməklə <em>d&ouml;vlət hesabına</em> təmin olunurlar. Məhkumları əməyə cəlb edən m&uuml;əssisələrin hesabına onların m&uuml;əyyən olunmuş normadan əlavə qidalanması təşkil edilə bilər.</p>\r\n<p>91.5. Məhkumların yemək və maddi-məişət normaları <a title="Azərbaycan Respublikasının Nazirlər Kabineti / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanı</a> tərəfindən m&uuml;əyyən edilir.</p>\r\n<p>91.6. &ccedil;ıxarılıb &nbsp;&nbsp;&nbsp;</p>\r\n<p>91.7. &ccedil;ıxarılıb &nbsp;&nbsp;&nbsp;</p>\r\n<p>91.8. Hamilə və ya &uuml;&ccedil; yaşına &ccedil;atmamış uşağı olan məhkum qadınlara, yetkinlik yaşına &ccedil;atmayan, həm&ccedil;inin xəstə, birinci və ikinci qrup əlilliyi olan məhkumlara yaxşılaşdırılmış maddi-məişət şəraiti yaradılır və onlar &uuml;&ccedil;&uuml;n yeməyin x&uuml;susi normaları m&uuml;əyyən edilir.</p>\r\n<p>91.9. Ərzaq məhsullarının və ən zəruri malların əldə edilməsi &uuml;&ccedil;&uuml;n xərcləməyə icazə verilən məbləğdən başqa, məhkumlar &ouml;z şəxsi hesablarında olan vəsaitlə cəza&ccedil;əkmə m&uuml;əssisələrində istifadəsinə icazə verilən ayaqqabı və paltar əldə edə bilərlər. Məhkumlar normativ h&uuml;quqi aktlarla m&uuml;əyyən olunmuş qaydada haqqı &ouml;dənilən m&uuml;alicə-profilaktika xidmətlərindən və başqa əlavə xidmətlərdən istifadə edə bilərlər.</p>\r\n<p><strong><em>Maddə 91-1. Cəza&ccedil;əkmə m&uuml;əssisələrində bədən tərbiyəsi və idman <br /> </em></strong><em><br /> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 91-1.1. Cəza&ccedil;əkmə m&uuml;əssisələrinin rəhbərliyi azadlıqdan məhrum edilmiş şəxslərin bədən tərbiyəsi və idmanla məşğul olması &uuml;&ccedil;&uuml;n şəraiti təmin edir və idman tədbirlərinin ke&ccedil;irilməsini təşkil edir. </em></p>\r\n<p><em>91-1.2. Cəza&ccedil;əkmə m&uuml;əssisələrinin g&uuml;n b&ouml;lg&uuml;s&uuml;ndə fiziki məşqlər, o c&uuml;mlədən səhər gimnastikası və g&uuml;ndə bir saatdan az olmayaraq idmanla məşğul olma nəzərdə tutulur.</em></p>\r\n<p><em>91-1.3. Cəza&ccedil;əkmə m&uuml;əssisələrində cəza &ccedil;əkən şəxslər m&uuml;əyyən edilmiş normativlərdən əlavə bədən tərbiyəsi və idmanla məşğul olmaq &uuml;&ccedil;&uuml;n &ouml;z vəsaitləri hesabına idman geyimi və alətləri əldə etmək h&uuml;ququna malikdirlər.</em></p>\r\n<p><strong><em>Maddə 92. Hamilə və ya &uuml;&ccedil; yaşına &ccedil;atmayan uşağı olan məhkum qadınların maddi-məişət təminatının x&uuml;susiyyətləri</em></strong></p>\r\n<p>92.1. Cəza&ccedil;əkmə m&uuml;əssisələrində məhkum qadınların &uuml;&ccedil; yaşına &ccedil;atmayan uşaqlarının yerləşdirilməsi &uuml;&ccedil;&uuml;n uşaq evləri təşkil edilir. Uşaq evləri uşaqların normal inkişafı və yaşayışı &uuml;&ccedil;&uuml;n zəruri şəraitlə təmin edilir. Məhkum qadınlar asudə vaxtlarında uşaqlarla məhdudiyyətsiz g&ouml;r&uuml;şə bilərlər. Onların uşaqla birgə yaşayışına icazə verilə bilər.</p>\r\n<p>92.2. Məhkum qadınların uşaqları ananın razılığı ilə onun qohumlarına və ya ananın razılığı, qəyyumluq və himayə&ccedil;ilik orqanlarının qərarı ilə digər şəxslərə verilə, habelə &uuml;&ccedil; yaşı tamam olduqdan sonra m&uuml;vafiq uşaq m&uuml;əssisələrinə g&ouml;ndərilə bilərlər.</p>\r\n<p>92.3. &Uuml;&ccedil; yaşı tamam olmuş uşaqların anasının cəza&ccedil;əkmə m&uuml;ddətinin qurtarmasına bir ildən &ccedil;ox qalmazsa və o, analıq borcunu vicdanla yerinə yetirərsə, cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti uşağın uşaq evində saxlanma m&uuml;ddətini cəza&ccedil;əkmə m&uuml;ddətinin sonuna qədər artıra bilər.</p>\r\n<p>92.4. Hamilə qadınlar doğuş və doğuşdan sonrakı m&uuml;ddətdə x&uuml;susi tibbi yardım almaq h&uuml;ququna malikdirlər.</p>\r\n<p><strong><em>Maddə 93. Məhkumların tibbi-sanitariya təminatı</em></strong></p>\r\n<p>93.1. Cəza&ccedil;əkmə m&uuml;əssisələrində m&uuml;alicə-profilaktika, sanitariya-profilaktika və sanitariya-epidemiologiya işi qanunvericiliyə, habelə cəzanın icrası qaydasına və şərtlərinə uyğun təşkil edilir və həyata ke&ccedil;irilir.</p>\r\n<p>93.2. <em>Cəza&ccedil;əkmə m&uuml;əssisələrində məhkumlara tibbi xidmət g&ouml;stərmək &uuml;&ccedil;&uuml;n tibb-sanitariya hissələri, həm&ccedil;inin yoluxucu xəstələrin, alkoqolizm və narkomaniyaya d&uuml;&ccedil;ar olanların, habelə vərəmli xəstələrin stasionar m&uuml;alicəsi və saxlanılması &uuml;&ccedil;&uuml;n m&uuml;alicə m&uuml;əssisələri təşkil edilir. </em></p>\r\n<p><em>Azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəza &ccedil;əkən narkoloji xəstələrin narkoloji yardım almaq h&uuml;ququnun hər hansı şəkildə məhdudlaşdırılmasına yol verilmir. Azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəza &ccedil;əkən və abstinent sindromu vəziyyətində olan narkoloji xəstələr digər xəstələrdən ayrı saxlanılır.</em></p>\r\n<p><em>Cəza&ccedil;əkmə m&uuml;əssisələrində insanın immun&ccedil;atışmazlığı virusu infeksiyasının profilaktikası məqsədi ilə insanın immun&ccedil;atışmazlığı virusunun t&ouml;rətdiyi xəstəliklə m&uuml;barizə haqqında Azərbaycan Respublikasının qanunvericiliyində nəzərdə tutulmuş tədbirlər həyata ke&ccedil;irilir. </em>(20)</p>\r\n<p>93.3. Cəza&ccedil;əkmə m&uuml;əssisələrinin m&uuml;diriyyəti məhkumların &uuml;mumi və şəxsi sanitariya-gigiyena tələblərinə əməl edilməsini təmin edir;</p>\r\n<p>93.4. Yemək qəbul etməkdən imtina edən və bununla əlaqədar həyatı &uuml;&ccedil;&uuml;n təhl&uuml;kə yaranan məhkumların tibbi rəyə əsasən məcburi qaydada qidalandırılmasına yol verilir.</p>\r\n<p>93.5. Cəza&ccedil;əkmə m&uuml;əssisələrində məhkumlara tibbi xidmət g&ouml;stərilməsi, sanitar nəzarətinin təşkili və həyata ke&ccedil;irilməsi, m&uuml;əssisədən kənar tibb m&uuml;əssisələrindən istifadə və bu məqsədlə onların tibb iş&ccedil;ilərinin cəlb edilməsi qaydası normativ h&uuml;quqi aktlarla m&uuml;əyyən edilir.</p>\r\n<p>93.6. <em>Məhkumlar</em> tibbi məsləhət və m&uuml;alicə almaq &uuml;&ccedil;&uuml;n pullu xidmət g&ouml;stərən tibb m&uuml;əssisələrinə m&uuml;raciət etmək h&uuml;ququna malikdirlər. Bu n&ouml;v xidmətlərin, dərmanların &ouml;dəniş haqqı məhkum və ya onun qohumları tərəfindən həyata ke&ccedil;irilir. Belə hallarda tibbi məsləhətin verilməsi və m&uuml;alicənin aparılması cəza&ccedil;əkmə m&uuml;əssisəsinin tibb-sanitariya hissəsində, <em>habelə m&uuml;alicə m&uuml;əssisəsində həmin m&uuml;əssisənin tibb</em> iş&ccedil;ilərinin m&uuml;şahidəsi altında həyata ke&ccedil;irilir. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i4#i4">4</a>, <a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 94. Məhkumların maddi məsuliyyəti</em></strong></p>\r\n<p>94.1. Cəza&ccedil;əkmə m&uuml;əssisələrində məhkumlar vurduqları maddi ziyana g&ouml;rə məsuliyyət daşıyırlar:</p>\r\n<p>94.1.1. əmək vəzifələrinin icrası zamanı vurduqları ziyana g&ouml;rə Azərbaycan Respublikası əmək qanunvericiliyi ilə m&uuml;əyyən edilmiş qaydada;</p>\r\n<p>94.1.2. vurulan digər ziyana g&ouml;rə Azərbaycan Respublikasının m&uuml;lki qanunvericiliyi ilə m&uuml;əyyən edilmiş qaydada.</p>\r\n<p>94.2. &Ouml;zlərinə qəsdən bədən xəsarəti yetirmiş məhkumların m&uuml;alicəsi və ya cəza&ccedil;əkmə m&uuml;əssisəsindən qa&ccedil;mış məhkumun tutulması ilə əlaqədar &ccedil;əkilən xərclər qanunvericiliklə m&uuml;əyyən edilmiş qaydada məhkumlar tərəfindən &ouml;dənilməlidir.</p>\r\n<p><strong>XII fəsil. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzalara məhkum olunmuş şəxslərin əməyi, </strong><strong><em>ilk peşə-ixtisas təhsili</em></strong><strong> və peşə hazırlığı </strong><em>(20)</em></p>\r\n<p><strong><em>Maddə 95. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzalara məhkum olunmuş şəxslərin əməyə cəlb edilməsi</em></strong></p>\r\n<p>95.1. Hər bir məhkum cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti tərəfindən m&uuml;əyyən edilmiş yerdə və işlərdə əmək fəaliyyəti ilə məşğul olmalıdır. Cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti məhkumların cinsini, yaşını, əmək qabiliyyətini, sağlamlığını, imkan daxilində ixtisaslarını nəzərə alaraq, onları faydalı əməyə cəlb etməlidirlər. Məhkumlar bir qayda olaraq cəza&ccedil;əkmə m&uuml;əssisələrinin istehsalat <em>sahələrində, bu m&uuml;əssisələrin normal fəaliyyətinin təmin olunması &uuml;&ccedil;&uuml;n nəzərdə tutulmuş təsərr&uuml;fat və məişət işlərində, habelə</em> cəza&ccedil;əkmə m&uuml;əssisələrindən kənar digər istehsalat obyektlərində m&uuml;əyyən olunmuş qaydada m&uuml;hafizə və təcrid olunmaqla əməyə cəlb olunurlar. Məhkumlar cəza&ccedil;əkmə m&uuml;əssisəsində m&uuml;diriyyətin icazəsi ilə fərdi əməklə məşğul ola bilərlər. <em>Cəza&ccedil;əkmə m&uuml;əssisələrində təsərr&uuml;fat və məişət işlərinə cəlb edilmiş məhkumların sayı m&uuml;əssisədə cəza &ccedil;əkməsi nəzərdə tutulan məhkumların sayının on faizindən &ccedil;ox ola bilməz. Məhkumların əməyindən istifadə edilməsinə icazə verilməyən işlərin siyahısı cəza&ccedil;əkmə m&uuml;əssisələrinin Daxili İntizam Qaydaları ilə m&uuml;əyyən edilir.</em></p>\r\n<p>95.2. Altmış yaşından yuxarı kişi, əlli beş yaşından yuxarı qadın məhkumlara, birinci və ikinci qrup əlilliyi<em>, 18 yaşınadək sağlamlıq imkanlarının məhdudluğu</em> olan məhkumlara, hamiləliyi d&ouml;rd aydan &ccedil;ox olan və ya cəza&ccedil;əkmə m&uuml;əssisələri nəzdindəki uşaq evlərində uşaqları olan qadın məhkumlara k&ouml;n&uuml;ll&uuml;l&uuml;k əsasında işləməyə icazə verilir. Yetkinlik yaşına &ccedil;atmayan məhkumlar əmək qanunvericiliyinə uyğun işə cəlb edilirlər.</p>\r\n<p>95.3. Həbsxanada cəza &ccedil;əkən məhkumların əməyi ancaq həbsxana ərazisində təşkil olunur.</p>\r\n<p>95.4. Məhkumların əməyindən istifadə edilməsi, qadağan olunan işlərin və vəzifələrin siyahısı cəza&ccedil;əkmə m&uuml;əssisələrinin Daxili İntizam Qaydaları ilə m&uuml;əyyən edilir.</p>\r\n<p>95.5. Məhkumların istehsalat fəaliyyəti cəza&ccedil;əkmə m&uuml;əssisələrinin əsas vəzifəsinə, yəni məhkumların islah edilməsinə mane olmamalıdır.</p>\r\n<p>95.6. Məhkumlara əmək m&uuml;bahisələrinin həlli &uuml;&ccedil;&uuml;n əmək fəaliyyətini dayandırmaq və tətil etmək qadağan edilir. &Uuml;zrs&uuml;z səbəbdən əməkdən imtina edilməsi və ya əmək fəaliyyətinin dayandırılması cəzanın icrası qaydalarının pozulması sayılır və məhkuma tənbeh tədbirlərinin tətbiq edilməsinə və ya onların maddi məsuliyyətə cəlb olunmasına səbəb olur. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i15#i15">15</a>, <a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 96. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzalara məhkum olunmuş şəxslərin əmək şəraiti</em></strong></p>\r\n<p>96.1. Məhkumların iş vaxtının m&uuml;ddəti, əməyin m&uuml;hafizəsi və istehsalat sanitariyası qaydaları əmək qanunvericiliyinə uyğun olaraq m&uuml;əyyən edilir.</p>\r\n<p>96.2. Məhkumlara istirahət g&uuml;nləri verilir və onlar əmək qanunvericiliyində nəzərdə tutulmuş qaydada <em>səsvermə, iş g&uuml;n&uuml; hesab edilməyən bayram g&uuml;nləri və &uuml;mumxalq h&uuml;zn g&uuml;n&uuml;ndə</em> işdən azad edilirlər.</p>\r\n<p>96.3. Məhkumların g&ouml;rd&uuml;kləri işin x&uuml;susiyyətini nəzərə alaraq, tərbiyə m&uuml;əssisələri istisna olmaqla, iş vaxtının cəmləşdirilmiş u&ccedil;otuna yol verilir. U&ccedil;ot d&ouml;vr&uuml;ndə iş vaxtının m&uuml;ddəti qanunla m&uuml;əyyən edilmiş g&uuml;ndəlik iş vaxtı m&uuml;ddətindən &ccedil;ox olmamalıdır.</p>\r\n<p>96.4. Məhkumların &ouml;dənişli əməyə cəlb edildikləri vaxt &uuml;mumi əmək stajına daxil edilir. Əmək stajının hesablanması təqvim ilinin yekununa əsasən, cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti tərəfindən aparılır. Ardıcıl olaraq əmək vəzifələrini yerinə yetirməyən, yaxud digər hallarda işdən boyun qa&ccedil;ıran məhkumların m&uuml;vafiq d&ouml;vrdəki iş vaxtları cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyətinin təqdimatına əsasən məhkəmənin qərarı ilə onların &uuml;mumi əmək stajından &ccedil;ıxarıla bilər.</p>\r\n<p>96.5. Əmək fəaliyyəti ilə məşğul olan məhkumlar hər il əmək qanunvericiliyi ilə m&uuml;əyyən edilmiş &ouml;dənişli məzuniyyətdən istifadə h&uuml;ququna malikdirlər. Məzuniyyətdən istifadə vaxtı cəza&ccedil;əkmə m&uuml;əssisələrinin h&uuml;dudlarından kənara qısa m&uuml;ddətli səfər etmək qaydası bu Məcəllənin 89.4-c&uuml; maddəsi ilə m&uuml;əyyən olunur.</p>\r\n<p>96.6. Məhkum qadınlar hamiləliyə və doğuşa g&ouml;rə qanunvericilikdə m&uuml;əyyən edilmiş m&uuml;ddətə işdən azad edilirlər.</p>\r\n<p>96.7. İş normalarını artıqlaması ilə yerinə yetirən və ya m&uuml;əyyən edilmiş tapşırıqları n&uuml;munəvi yerinə yetirən məhkumlara əmək qanunvericiliyində nəzərdə tutulmuş əlavə məzuniyyət verilə bilər.</p>\r\n<p><strong><em>Maddə 97. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzalara məhkum olunmuş şəxslərin əməyinin &ouml;dənilməsi</em></strong></p>\r\n<p>97.1. Məhkumlar əmək qanunvericiliyinə uyğun olaraq əməyin &ouml;dənilməsi h&uuml;ququna malikdirlər.</p>\r\n<p>97.2. İş vaxtının normasını tam işləmiş və &ouml;z əmək vəzifələrini yerinə yetirmiş məhkumların aylıq əmək haqqının məbləği əməyin &ouml;dənilməsinin m&uuml;əyyən olunmuş minimum miqdarından az olmamalıdır.</p>\r\n<p>97.3. Natamam iş vaxtına g&ouml;rə əməyin &ouml;dənilməsi işlənmiş vaxta m&uuml;tənasib və ya istehsaldan asılı olaraq həyata ke&ccedil;irilir.</p>\r\n<p>97.4. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslərin əmək haqqının &ouml;dənilməsi qaydası <a title="Azərbaycan Respublikasının Nazirlər Kabineti / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanı</a> tərəfindən m&uuml;əyyən edilir.</p>\r\n<p><strong><em>Maddə 98. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzalara məhkum olunmuş şəxslərin haqqı &ouml;dənilməyən işlərə cəlb edilməsi</em></strong></p>\r\n<p>98.1. Məhkumlar m&uuml;hafizə qurğuları istisna olmaqla, cəza&ccedil;əkmə m&uuml;əssisələrinin və onların ərazisinin abadlaşdırılması, məhkumların mədəni-məişət şəraitinin yaxşılaşdırılması &uuml;&ccedil;&uuml;n k&ouml;mək&ccedil;i işlərə haqqı &ouml;dənilmədən cəlb edilə bilərlər.</p>\r\n<p>98.2. Bu Məcəllənin 98.1-ci maddəsində g&ouml;stərilən işlərə məhkumlar, bir qayda olaraq n&ouml;vbə ilə işdən azad vaxtlarda cəlb edilirlər. Belə işlərin m&uuml;ddəti ay ərzində səkkiz saatdan &ccedil;ox ola bilməz.</p>\r\n<p>98.3. Məhkumlar m&uuml;hafizə qurğularının bərpasına yalnız təbii fəlakət, yaxud digər f&ouml;vqəladə hallar nəticəsində bu qurğulara zərər vurulduqda və ya onlar dağıldıqda haqqı &ouml;dənilmədən cəlb edilə bilərlər.</p>\r\n<p><strong><em>Maddə 99. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzalara məhkum olunmuş şəxslərin qazancından tutulmalar</em></strong></p>\r\n<p>99.1. Cəza&ccedil;əkmə m&uuml;əssisələrində və həbsxanalarda məhkumların qazancından, pensiyalarından və digər gəlirlərindən tutulmalar aparılır.</p>\r\n<p>99.2. <em>Məhkumların əmək haqqından gəlir vergisi, D&ouml;vlət Sosial M&uuml;dafiə Fonduna ayırmalar və digər məcburi &ouml;dənişlər tutulur.</em> İcra vərəqəsinə və başqa icra sənədlərinə g&ouml;rə tutulmalar qalan məbləğdən qanunvericiliklə m&uuml;əyyən edilmiş qaydada aparılır. Yetkinlik yaşına &ccedil;atmayan uşaqlar &uuml;&ccedil;&uuml;n aliment məhkumun qazancının b&uuml;t&uuml;n məbləğindən hesablanır.</p>\r\n<p>99.3. Cəza&ccedil;əkmə m&uuml;əssisələrində və həbsxanalarda məhkumların şəxsi hesabına, b&uuml;t&uuml;n tutulmalı məbləğlərdən asılı olmayaraq onlara hesablanmış aylıq əmək haqqının, pensiyanın və digər gəlirlərin ən azı <em>əlli</em> faizi, altmış yaşından yuxarı kişi, əlli beş yaşından yuxarı qadın məhkumların, birinci və ikinci qrup əlilliyi olan məhkumların, yetkinlik yaşına &ccedil;atmayanların və ya hamilə, cəza&ccedil;əkmə m&uuml;əssisələri nəzdindəki uşaq evlərində uşaqları olan məhkum qadınların şəxsi hesabına isə aylıq əmək haqqının, pensiyanın və digər gəlirlərin ən azı <em>altmış </em>faizi ke&ccedil;irilir.</p>\r\n<p>99.4. Məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisələrində cəza &ccedil;əkən şəxslərə, habelə bu Məcəlləyə uyğun olaraq cəza&ccedil;əkmə m&uuml;əssisəsindən kənarda yaşamağa icazə verilmiş məhkum qadınlara tutulmalı b&uuml;t&uuml;n məbləğlərdən asılı olmayarq &uuml;mumi gəlirin ən azı altmış beş faizi verilir. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 100. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslərin </em></strong><strong><em>ilk peşə-ixtisas təhsili və peşə hazırlığı </em></strong>(20)</p>\r\n<p>100.1. Peşəsi və ya ixtisası olmayan məhkumlar &uuml;&ccedil;&uuml;n cəza&ccedil;əkmə m&uuml;əssisələrində <em>ilk peşə-ixtisas təhsili</em> və ya peşə hazırlığı həyata ke&ccedil;irilir. (20)</p>\r\n<p>100.2. Hamilə və ya cəza&ccedil;əkmə m&uuml;əssisələri nəzdindəki uşaq evlərində uşaqları olan məhkum qadınlar, altmış yaşından yuxarı kişi və əlli beş yaşından yuxarı qadın məhkumlar, birinci və ikinci qrup əlilliyi<em>, 18 yaşınadək sağlamlıq imkanlarının məhdudluğu</em> olan məhkumlar <em>ilk peşə-ixtisas təhsilinə</em> və ya peşə hazırlığına &ouml;z arzularına g&ouml;rə cəlb edilirlər. (20)</p>\r\n<p>100.3. Tərbiyə m&uuml;əssisələrində <em>ilk peşə-ixtisas təhsili</em> və ya peşə hazırlığı məhkumların iş g&uuml;n&uuml; m&uuml;ddəti ərzində həyata ke&ccedil;irilir. (20)</p>\r\n<p>100.4. Cəza&ccedil;əkmə m&uuml;əssisələrində saxlanılan məhkumlar <em>ilk peşə-ixtisas təhsili</em> &uuml;zrə imtahan vermək &uuml;&ccedil;&uuml;n əmək qanunvericiliyində nəzərdə tutulmuş m&uuml;ddətə işdən azad edilirlər. Bu m&uuml;ddət &uuml;&ccedil;&uuml;n onlara əmək haqqı hesablanmır, lakin yemək pulsuz verilir. (20)</p>\r\n<p>100.5. Məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisələrində cəza &ccedil;əkən məhkumların <em>ilk peşə-ixtisas təhsili</em> &uuml;zrə imtahan verməsi d&ouml;vr&uuml;ndə onların maddi təminatı <a title="Azərbaycan Respublikasının Nazirlər Kabineti / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanının</a> m&uuml;əyyən etdiyi qayda və şərtlərlə həyata ke&ccedil;irilir. (20)</p>\r\n<p>100.6. Məhkumların <em>ilk peşə-ixtisas təhsili</em> və ya peşə hazırlığı həvəsləndirilir və onların islah edilməsi m&uuml;əyyən edilərkən nəzərə alınır. (20)</p>\r\n<p>100.7. <em>İlk peşə-ixtisas təhsili</em> və ya peşə hazırlığı <a title="Azərbaycan Respublikasının Nazirlər Kabineti / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanı</a> tərəfindən m&uuml;əyyən edilmiş qaydada həyata ke&ccedil;irilir. (20)</p>\r\n<p>100.8. &ccedil;ıxarılıb&nbsp;&nbsp;&nbsp;</p>\r\n<p><strong>XIII fəsil. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzalara məhkum olunmuş şəxslərlə tərbiyə işi</strong></p>\r\n<p><strong><em>Maddə 101. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzalara məhkum olunmuş şəxslərlə tərbiyə işinin təşkili</em></strong></p>\r\n<p>101.1. Məhkumlarla tərbiyə işi onların ictimai faydalı əməklə məşğul olmasına, əməyə vicdanlı m&uuml;nasibətə və cəmiyyətdə qəbul edilmiş davranış normalarına riayət edilməsinin, təhsil və mədəniyyət səviyyəsinin artırılmasının formalaşdırılmasına və m&ouml;hkəmləndirilməsinə y&ouml;nəldilir.</p>\r\n<p>101.2. Tərbiyə tədbirlərində məhkumların iştirakı onların islah edilməsi m&uuml;əyyənləşdirilərkən, habelə həvəsləndirmə və tənbeh tədbirlərinin tətbiqi zamanı nəzərə alınır.</p>\r\n<p>101.3. Məhkumların məcburi olan tərbiyə tədbirlərində iştirakı cəza&ccedil;əkmə m&uuml;əssisəsinin <em>g&uuml;n b&ouml;lg&uuml;s&uuml;ndə</em> nəzərdə tutula bilər.</p>\r\n<p>101.4. Məhkumlarla tərbiyə işi onların şəxsiyyətinin fərdi x&uuml;susiyyətləri, xarakteri və t&ouml;rətdikləri cinayətin səbəbləri nəzərə alınmaqla həyata ke&ccedil;irilir.</p>\r\n<p>101.5. Məhkumlarla tərbiyə işinin təşkili &uuml;&ccedil;&uuml;n cəza&ccedil;əkmə m&uuml;əssisələrində zəruri maddi-texniki baza yaradılır.</p>\r\n<p><strong><em>Maddə 102. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzalara məhkum olunmuş şəxslərlə tərbiyə işinin əsas formaları</em></strong></p>\r\n<p>102.1. Məhkumların islah edilməsi məqsədi ilə cəza&ccedil;əkmə m&uuml;əssisələrində əxlaqı, h&uuml;quqi, əmək, fiziki və başqa tərbiyə tədbirləri həyata ke&ccedil;irilir.</p>\r\n<p>102.2. Məhkumlarla tərbiyə işi pedaqoji və psixoloji &uuml;sullara əsasən cəza&ccedil;əkmə m&uuml;əssisəsinin n&ouml;v&uuml;, cəzanın m&uuml;ddəti, fərdi, qrup və k&uuml;tləvi şəkildə saxlanma şərtləri nəzərə alınmaqla, fərqləndirilmiş qaydada aparılır.</p>\r\n<p>102.3. Məhkumlarla tərbiyə işi aparılarkən k&uuml;tləvi informasiya vasitələrindən, o c&uuml;mlədən radio və televiziyadan, kinofilmlərə baxışdan istifadə edilir, kitabxanalar təşkil edilir.</p>\r\n<p><em>102.4. Tənbeh tədbiri qismində intizam və cərimə təcridxanalarına, birnəfərlik kameralara, kamera tipli otaqlara, həbsxanalarda ciddi saxlanma şəraitinə, habelə bu Məcəllənin 70.4.3-c&uuml; maddəsinə əsasən həbsxanaya ke&ccedil;irilmiş məhkumlara televiziya verilişlərinə baxmağa icazə verilmir.</em> (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 103. Cəza&ccedil;əkmə m&uuml;əssisələrində məhkumların &ouml;zfəaliyyət təşkilatları</em></strong></p>\r\n<p>103.1. Cəza&ccedil;əkmə m&uuml;əssisələrində, &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər istisna olmaqla, digər məhkumların bu m&uuml;əssisələrin m&uuml;diriyyətinin nəzarəti altında fəaliyyət g&ouml;stərən &ouml;zfəaliyyət təşkilatları yaradılır.</p>\r\n<p>103.2. &Ouml;zfəaliyyət təşkilatlarının fəaliyyətində məhkumların iştirakı həvəsləndirilir və onların islah edilməsi m&uuml;əyyənləşdirilərkən nəzərə alınır.</p>\r\n<p>103.3. &Ouml;zfəaliyyət təşkilatlarının əsas məqsədi aşağıdakılardır:</p>\r\n<p>103.3.1. məhkumların islah edilməsinə, tərbiyəsinə, onların mənəvi və fiziki inkişafına k&ouml;məklik g&ouml;stərmək;</p>\r\n<p>103.3.2. məhkumlarda m&uuml;sbət vərdişləri inkişaf etdirmək və onların faydalı təşəbb&uuml;skarlığını həvəsləndirmək;</p>\r\n<p>103.3.3. cəza&ccedil;əkmə m&uuml;əssisəsində əməyin, məişət şəraitinin və asudə vaxtın səmərəli ke&ccedil;məsinin təşkili məsələlərinin həllində iştirak etmək;</p>\r\n<p>103.3.4. məhkumlar arasında intizamı m&ouml;hkəmləndirmək və onların bir-birinə qarşılıqlı m&uuml;sbət m&uuml;nasibətlərini formalaşdırmaq;</p>\r\n<p>103.3.5. məhkumlara və onların ailələrinə yardım g&ouml;stərmək.</p>\r\n<p>103.4. &Ouml;zfəaliyyət təşkilatlarının &uuml;zvlərinə əlavə g&uuml;zəştlər verilmir və onlar istehsalatda əsas işdən azad edilmirlər.</p>\r\n<p>103.5. Məhkumların &ouml;zfəaliyyət təşkilatlarının yaradılması və onların fəaliyyət qaydası <a title="Azərbaycan Respublikasının Ədliyyə Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanı</a> tərəfindən m&uuml;əyyən edilir.</p>\r\n<p><strong><em>Maddə 104. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslərin &uuml;mumi təhsili </em></strong>(20)</p>\r\n<p>104.1. Cəza&ccedil;əkmə m&uuml;əssisələrində məhkumların &uuml;mumi təhsili həyata ke&ccedil;irilir. Yaşı qırxdan &ccedil;ox olan məhkumlar, birinci və ikinci qrup əlilliyi<em>, 18 yaşınadək sağlamlıq imkanlarının məhdudluğu</em> olan məhkumlar &uuml;mumi təhsilə &ouml;z arzularına g&ouml;rə cəlb edilirlər. (20)</p>\r\n<p>104.2. Məhkumların &uuml;mumi təhsili həvəsləndirilir və onların islah edilməsi m&uuml;əyyənləşdirilərkən nəzərə alınır. (20)</p>\r\n<p>104.3. Əmək qanunvericiliyinə uyğun olaraq &uuml;mumi&nbsp; təhsil alan məhkumlar imtahan vermək &uuml;&ccedil;&uuml;n işdən azad edilirlər. (20)</p>\r\n<p>104.4. Cəza&ccedil;əkmə m&uuml;əssisələrinin nəzdindəki &uuml;mumi&nbsp; təhsil m&uuml;əssisələrinin pedaqoji kollektivi tərbiyə işinin aparılmasında cəza&ccedil;əkmə m&uuml;əssisələrinin m&uuml;diriyyətinə k&ouml;məklik edir. (20)</p>\r\n<p>104.5. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslərin &uuml;mumi&nbsp; təhsili <a title="Azərbaycan Respublikasının Ədliyyə Nazirliyi və Azərbaycan Respublikasının Təhsil Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanının</a> m&uuml;əyyən etdiyi qaydada həyata ke&ccedil;irilir. (20)</p>\r\n<p>104.6. &Ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər &uuml;mumi&nbsp; təhsilə cəlb olunmurlar. Onların &ouml;z-&ouml;z&uuml;nə təhsil alması &uuml;&ccedil;&uuml;n cəzanın icrası və &ccedil;əkilməsi qaydasına və şərtlərinə zidd olmayan şərait yaradılır. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i15#i15">15</a>, 20)</p>\r\n<p><strong><em>Maddə 105. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzalara məhkum olunmuş şəxslərlə tətbiq edilən həvəsləndirmə tədbirləri</em></strong></p>\r\n<p>105.1. N&uuml;munəvi davranışa, əməyə və təhsilə vicdanlı m&uuml;nasibətə, &ouml;zfəaliyyət təşkilatlarında və tərbiyə tədbirlərində fəal iştirakına g&ouml;rə məhkumlara aşağıdakı həvəsləndirmə tədbirləri tətbiq edilə bilər:</p>\r\n<p>105.1.1. təşəkk&uuml;r elan etmək;</p>\r\n<p>105.1.2. hədiyyə ilə m&uuml;kafatlandırmaq;</p>\r\n<p>105.1.3. <em>d&ouml;rd manatadək</em> miqdarda pulla m&uuml;kafatlandırmaq;</p>\r\n<p>105.1.4. əlavə sovqat, bağlama və ya banderol almağa icazə vermək;</p>\r\n<p>105.1.5. əlavə telefon danışığına icazə vermək;</p>\r\n<p>105.1.6. əlavə qısa m&uuml;ddətli və ya uzun m&uuml;ddətli g&ouml;r&uuml;şə icazə vermək;</p>\r\n<p>105.1.7. ərzaq məhsulları və ən zəruri mallar əldə etmək &uuml;&ccedil;&uuml;n <em>on beş manatadək</em> miqdarda əlavə pul xərcləməyə icazə vermək;</p>\r\n<p>105.1.8. verilmiş tənbehi vaxtından əvvəl g&ouml;t&uuml;rmək;</p>\r\n<p>105.1.9. saxlanılma şəraitini yaxşılaşdırmaq;</p>\r\n<p>105.1.10. x&uuml;susi rejimli cəza&ccedil;əkmə m&uuml;əssisələrində saxlanılan və cəza m&uuml;ddətinin ən azı &uuml;&ccedil;də birini &ccedil;əkmiş məhkumları kamera tipli otaqlardan adi yaşayış binalarına ke&ccedil;irmək;</p>\r\n<p><em>105.1.11. x&uuml;susi rejimli cəza&ccedil;əkmə m&uuml;əssisəsində və həbsxanada kameralarda saxlanılan məhkumlara bir ay ərzində bir saatadək əlavə gəzintiyə icazə vermək.</em></p>\r\n<p>105.2. Məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisələrində cəza &ccedil;əkən məhkumlara istirahət və <em>iş g&uuml;n&uuml; hesab edilməyən</em> bayram g&uuml;nlərinin məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisəsinin ərazisindən kənarda ke&ccedil;irilməsinə icazə vermək kimi həvəsləndirmə tədbiri də tətbiq edilə bilər.</p>\r\n<p>105.3. &Ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər istisna olmaqla m&uuml;sbət xarakterizə olunan məhkumlara onların ərizələrinə əsasən bu Məcəllənin 70-ci maddəsində nəzərdə tutulmuş qaydada digər həvəsləndirmə tədbirləri tətbiq edilə bilər.</p>\r\n<p>105.4. Cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti tərəfindən məhkumların razılığı ilə onların əfv edilməsi haqqında vəsatət qaldırıla bilər. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i10#i10">10</a>, <a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i14#i14">14</a>, <a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 106. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzalara məhkum olunmuş şəxslərə həvəsləndirmə tədbirlərinin tətbiqi qaydası</em></strong></p>\r\n<p>106.1. Məhkumlara həvəsləndirmə tədbirləri yazılı şəkildə verilir.</p>\r\n<p>106.2. Verilmiş tənbehin həvəsləndirmə qaydasında vaxtından əvvəl g&ouml;t&uuml;r&uuml;lməsinə:</p>\r\n<p>106.2.1. bu Məcəllənin 107.1.1 və 107.1.2-ci maddələrində g&ouml;stərilən tənbehlərin verilməsi g&uuml;n&uuml;ndən ən azı &uuml;&ccedil; aydan sonra;</p>\r\n<p>106.2.2. bu Məcəllənin 107.1.3 və 107.1.4-c&uuml; maddələrində g&ouml;stərilən tənbehlərin verilməsi g&uuml;n&uuml;ndən ən azı altı ay sonra yol verilir.</p>\r\n<p>106.3. Tənbeh tədbiri kimi x&uuml;susi rejimli cəza&ccedil;əkmə m&uuml;əssisələrində adi yaşayış binalarından kamera tipli otaqlara ke&ccedil;irilmiş məhkumlara adi yaşayış binalarına ke&ccedil;irilmə n&ouml;v&uuml;ndə həvəsləndirmə tədbiri tənbeh verilməsi g&uuml;n&uuml;ndən ən azı bir il ke&ccedil;dikdən sonra yenidən tətbiq edilə bilər.</p>\r\n<p><strong><em>Maddə 107. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzalara məhkum olunmuş şəxslərə tətbiq edilən tənbeh tədbirləri</em></strong></p>\r\n<p>107.1. Cəzanın icrası qaydalarını pozan məhkumlara aşağıdakı tənbeh tədbirləri tətbiq edilə bilər:</p>\r\n<p>107.1.1. xəbərdarlıq;</p>\r\n<p>107.1.2. t&ouml;hmət;</p>\r\n<p>107.1.3. cəza&ccedil;əkmə m&uuml;əssisələrində və həbsxanalarda saxlanılan kişi məhkumları on beş g&uuml;nədək, qadın məhkumları isə on g&uuml;nədək m&uuml;ddətə cərimə təcridxanasına ke&ccedil;irmək;</p>\r\n<p>107.1.4. x&uuml;susi rejimli cəza&ccedil;əkmə m&uuml;əssisələrində saxlanılan məhkumları adi yaşayış binalarından kamera tipli otaqlara ke&ccedil;irmək.</p>\r\n<p>107.2. Cəzanın icrası qaydaları qərəzli pozulduqda &uuml;mumi və ciddi rejimli cəza&ccedil;əkmə m&uuml;əssisələrində saxlanılan kişi məhkumları bir aydan altı ay m&uuml;ddətinədək kamera tipli otaqlara, x&uuml;susi rejimli cəza&ccedil;əkmə m&uuml;əssisələrində saxlanılan məhkumları bir aydan altı ay m&uuml;ddətinədək birnəfərlik kameralara, həbsxanalarda saxlanılan məhkumları iki aydan altı ayadək m&uuml;ddətə ciddi saxlanma şəraitinə ke&ccedil;irmək n&ouml;v&uuml;ndə tənbeh tədbiri tətbiq edilə bilər. Bu tənbeh tədbirləri yalnız bu Məcəllənin 107.1.1&mdash;107.1.3-c&uuml; maddələrində nəzərdə tutulmuş tənbeh tədbirləri verilmiş məhkumlara tətbiq edilə bilər.</p>\r\n<p>107.3. <em>Məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisələrində cəzanın icrası qaydalarını qərəzli pozan məhkumlara bir ay m&uuml;ddətində m&uuml;əssisədən kənara &ccedil;ıxmağın və ya &uuml;&ccedil; ay ərzində istirahət və bayram g&uuml;nlərini cəza&ccedil;əkmə m&uuml;əssisəsindən kənarda ke&ccedil;irməyin qadağan edilməsi kimi tənbeh tədbirləri, yaxud bu Məcəllənin 70-ci maddəsində nəzərdə tutulmuş qaydada digər tənbeh tədbirləri tətbiq edilir</em>. (22)</p>\r\n<p>107.4. Məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisələrində cəza &ccedil;əkən məhkumlara bu Məcəllənin 107.2-ci maddəsində nəzərdə tutulmuş tənbeh tədbiri tətbiq edilmir.</p>\r\n<p>107.5. Cəzanın icrası qaydalarını qərəzli pozan məhkumlara bu Məcəllənin 70-ci maddəsində nəzərdə tutulmuş qaydada digər tənbeh tədbirləri tətbiq edilə bilər.</p>\r\n<p><em>107.6. Həvəsləndirmə tədbiri qaydasında saxlanılma şəraiti yaxşılaşdırılmış məhkum barədə bu Məcəllənin 107.1-107.3, 107.5 və 125-ci maddələrində nəzərdə tutulmuş tənbeh tədbiri tətbiq edildikdə, yaxşılaşdırılmış saxlanılma şəraiti ləğv edilir.</em> (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 108. Cəzanın icrası qaydalarının qərəzli pozulması</em></strong></p>\r\n<p>108.1. Cəzanın icrası qaydalarının qərəzli pozulması aşağıdakı hallar hesab olunur:</p>\r\n<p>108.1.1. alkoqol və ya digər g&uuml;cl&uuml; təsir g&ouml;stərən maddələri qəbul etmək;</p>\r\n<p>108.1.2. narkotik <em>vasitələri</em> və ya psixotrop maddələri qəbul etmək;</p>\r\n<p>108.1.3. xırda xuliqanlıq;</p>\r\n<p>108.1.4. cəza&ccedil;əkmə m&uuml;əssisəsinin iş&ccedil;i heyətini hədələmək, təhqir etmək və ya onların qanuni tələblərinə tabe olmamaq;</p>\r\n<p>108.1.5. xidməti borclarını yerinə yetirən digər d&ouml;vlət n&uuml;mayəndələrini hədələmək və ya təhqir etmək;</p>\r\n<p>108.1.6. qrup halında tabe olmamaq və ya belə qruplarda fəal iştirak etmək;</p>\r\n<p>108.1.7. il ərzində &uuml;&ccedil; dəfədən &ccedil;ox tənbeh edilmək.</p>\r\n<p><em>108.1-1. Məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisələrində cəzanın icrası qaydalarının qərəzli pozulması, həm&ccedil;inin aşağıdakı hallar hesab edilir:</em></p>\r\n<p><em>108. 1-1.1. məhkumun cəza&ccedil;əkmə m&uuml;əssisəsini icazəsiz tərk etməsi;</em></p>\r\n<p><em>108. 1-1.2. məhkumun &uuml;zrs&uuml;z səbəbdən ayda iki dəfədən &ccedil;ox cəza&ccedil;əkmə m&uuml;əssisəsinə vaxtında qayıtmaması</em><em>. </em>(22)</p>\r\n<p>108.2. Rejimin qərəzli pozulması məhkuma tənbeh tədbirlərinin tətbiq edilməsi ilə eyni vaxtda cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyətinin əsaslandırılmış qərarı ilə m&uuml;əyyən edilir. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i13#i13">13</a>, <a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 109. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzalara məhkum olunmuş şəxslərə tənbeh tədbirlərinin tətbiqi qaydası</em></strong></p>\r\n<p>109.1. Tənbeh tədbirlərinin tətbiqi zamanı <em>məhkuma &ouml;z əməlini yazılı və ya şifahi qaydada izah etmək imkanı verilir,</em> xətanın t&ouml;rədilmə şəraiti, məhkumun şəxsiyyəti və cəzanın &ccedil;əkilməsi m&uuml;ddətindəki davranışı nəzərə alınır. Verilən tənbeh tədbirləri xətanın ağırlığına və x&uuml;susiyyətinə uyğun olmalıdır. Tənbeh tədbirləri xətanın aşkar olunduğu vaxtdan on g&uuml;n ərzində tətbiq edilir. Əgər həmin xəta ilə əlaqədar yoxlama aparılarsa onun başa &ccedil;atdığı g&uuml;ndən, lakin xətanın t&ouml;rədildiyi vaxtdan bir aydan gec olmayaraq tənbeh tədbiri tətbiq edilir. Eyni zamanda t&ouml;rədilən bir ne&ccedil;ə xəta &uuml;&ccedil;&uuml;n bir tənbeh tədbiri tətbiq edilir.</p>\r\n<p>109.2. Tənbeh tədbirləri yazılı şəkildə cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyətinin əsaslandırılmış qərarı ilə verilir. <em>Məhkumun bu Məcəllənin 107.1-107.3, 107.5 və 125-ci maddələrində nəzərdə tutulmuş tənbeh tədbirlərinin tətbiqindən m&uuml;vafiq icra hakimiyyəti orqanına və ya məhkəməyə şikayət vermək h&uuml;ququ vardır. Əsas olduqda, tənbeh tədbiri ləğv edilə bilər. Şikayətin verilməsi tənbeh tədbirinin icrasını dayandırmır.</em></p>\r\n<p>109.3. Məhkumlar kamera tipli otaqlara və birnəfərlik kameralara bu tənbeh tədbirlərinin m&uuml;ddəti g&ouml;stərilməklə ke&ccedil;irilirlər.</p>\r\n<p>109.4. Kamera tipli otağa ke&ccedil;irilmiş məhkuma&mdash;kamera tipli otağa ke&ccedil;irilməkdən başqa, digər tənbeh tədbirləri tətbiq edilə bilər.</p>\r\n<p>109.5. Tənbeh tədbiri verildiyi g&uuml;ndən bir il ərzində məhkuma yeni tənbeh tədbiri tətbiq edilməzsə o, tənbehi olmayan sayılır. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 110. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzalara məhkum olunmuş şəxslərin cərimə təcridxanalarında, kamera tipli otaqlarda və birnəfərlik kameralarda saxlanılması şərtləri</em></strong></p>\r\n<p>110.1. Cərimə təcridxanalarına ke&ccedil;irilmiş məhkumlara g&ouml;r&uuml;şlər, telefon danışıqları verilmir, ərzaq məhsulları və ən zəruri mallar əldə etmək, bağlama, sovqat və ya banderol almaq qadağan edilir. Onların hər g&uuml;n bir saatlıq gəzinti h&uuml;quqları vardır.</p>\r\n<p>110.2. Tənbeh tədbiri qaydasında kamera tipli otaqlarda və ya birdəfəlik kameralarda saxlanılan məhkumlar aşağıdakı h&uuml;quqlara malikdirlər:</p>\r\n<p>110.2.1. ərzaq məhsulları və ən zəruri malların əldə edilməsi &uuml;&ccedil;&uuml;n bu Məcəllə ilə m&uuml;əyyən edilmiş məbləğində pul xərcləmək;</p>\r\n<p>110.2.2. bir bağlama, sovqat və ya banderol almaq;</p>\r\n<p>110.2.3. hər g&uuml;n bir saat gəzintidən istifadə etmək;</p>\r\n<p>110.2.4. cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyətinin icazəsi ilə bir qısa m&uuml;ddətli g&ouml;r&uuml;ş almaq.</p>\r\n<p>110.3. Kamera tipli otaqlarda və ya birnəfərlik kameralarda saxlanılan məhkumlar digər məhkumlardan ayrı işləyirlər.</p>\r\n<p>110.4. Cərimə təcridxanalarında, kamera tipli otaqlarda və birnəfərlik kameralarda &ouml;z&uuml;nə xəsarət yetirmə və &ouml;z&uuml;n&uuml; xəstəliyə vurma halları istisna olmaqla, m&uuml;alicə m&uuml;əssisələrinə <em>və cəza&ccedil;əkmə m&uuml;əssisələrinin tibb-sanitariya hissələrinə</em> ke&ccedil;irilmiş məhkumların burada saxlanılma vaxtı verilən tənbehin &ccedil;əkilmə m&uuml;ddətinə hesablanır.</p>\r\n<p>110.5. Cərimə təcridxanalarında, kamera tipli otaqlarda və birnəfərlik kameralarda saxlanılan məhkumların maddi-məişət təminatı həbsxanalarda &uuml;mumi saxlanma şəraitində olan məhkumlar &uuml;&ccedil;&uuml;n m&uuml;əyyən edilmiş normalara uyğun m&uuml;əyyən edilir.</p>\r\n<p>110.6. Bir təqvim ili ərzində cərimə təcridxanasında saxlanma m&uuml;ddəti altmış g&uuml;ndən, kamera tipli otaqlarda və birnəfərlik kameralarda isə altı aydan &ccedil;ox ola bilməz. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 111. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzalara məhkum olunmuş şəxslərə həvəsləndirmə və tənbeh tədbirlərini tətbiq edən vəzifəli şəxslər </em></strong></p>\r\n<p>Bu Məcəllənin 105 və 107-ci maddələrində g&ouml;stərilən həvəsləndirmə və tənbeh tədbirlərini məhkumlara cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti tətbiq edir.</p>\r\n<p><strong>XIV fəsil. M&uuml;xtəlif n&ouml;vl&uuml; cəza&ccedil;əkmə m&uuml;əssisələrində cəzanın icrası</strong></p>\r\n<p><strong><em>Maddə 112. &Uuml;mumi rejimli cəza&ccedil;əkmə m&uuml;əssisələri </em></strong></p>\r\n<p>112.1. &Uuml;mumi rejimli cəza&ccedil;əkmə m&uuml;əssisələrində qəsdən t&ouml;rədilmiş b&ouml;y&uuml;k ictimai təhl&uuml;kə t&ouml;rətməyən, az ağır və ağır cinayətlərə g&ouml;rə ilk dəfə m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etməyə məhkum olunmuş şəxslər, habelə ehtiyatsızlıqdan t&ouml;rədilmiş cinayətlərə g&ouml;rə beş ildən artıq m&uuml;ddətə azadlıqdan məhrum etməyə məhkum olunmuş şəxslər, həm&ccedil;inin cəzaları m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəza ilə əvəz edilmiş şəxslər cəza &ccedil;əkirlər.</p>\r\n<p>112.2. Ehtiyatsızlıqdan t&ouml;rədilmiş, qəsdən t&ouml;rədilmiş b&ouml;y&uuml;k ictimai təhl&uuml;kə t&ouml;rətməyən və az ağır cinayətlərə g&ouml;rə məhkum olunmuş kişilər ilk dəfə ağır cinayətlərə g&ouml;rə məhkum olunmuş <em>və bu Məcəllənin 70.2.2-ci maddəsində nəzərdə tutulmuş qaydada ciddi rejimli cəza&ccedil;əkmə m&uuml;əssisələrindən ke&ccedil;irilmiş</em> kişilərdən ayrı saxlanılırlar. (22)</p>\r\n<p><strong><em>Maddə 113. &Uuml;mumi rejimli cəza&ccedil;əkmə m&uuml;əssisələrində cəzanın icrası şərtləri</em></strong></p>\r\n<p>113.1. &Uuml;mumi rejimli cəza&ccedil;əkmə m&uuml;əssisələrində məhkumlar cəza&ccedil;əkmə m&uuml;əssisələrinin daxili intizam qaydalarına uyğun olaraq cəza&ccedil;əkmə m&uuml;əssisəsi ərazisinin daxilində hərəkət edə bilərlər.</p>\r\n<p>113.2. Ehtiyatsızlıqdan t&ouml;rədilmiş, qəsdən t&ouml;rədilmiş b&ouml;y&uuml;k ictimai təhl&uuml;kə t&ouml;rətməyən və az ağır cinayətlərə g&ouml;rə məhkum olunmuş şəxslərin saxlandığı &uuml;mumi rejimli cəza&ccedil;əkmə m&uuml;əssisələrində məhkumlar:</p>\r\n<p>113.2.1. adi yaşayış binalarında saxlanılırlar;</p>\r\n<p>113.2.2. ərzaq məhsulları və ən zəruri mallar əldə etmək &uuml;&ccedil;&uuml;n ayda <em>əlli manatadək</em> miqdarda pul xərcləyə bilərlər;</p>\r\n<p>113.2.3. il ərzində qısa m&uuml;ddətli qırx səkkiz g&ouml;r&uuml;ş və uzun m&uuml;ddətli d&ouml;rd g&ouml;r&uuml;ş almaq h&uuml;ququna malikdirlər;</p>\r\n<p>113.2.4. il ərzində qırx səkkiz bağlama, sovqat və ya banderol almaq h&uuml;ququna malikdirlər.</p>\r\n<p>113.3. Məhkumlar cəzanın icrası qaydalarını pozmadıqda və əməyə vicdanla yanaşdıqda, cəza m&uuml;ddətinin ən azı &uuml;&ccedil;də birini &ccedil;əkdikdən sonra onların saxlanılması şəraiti yaxşılaşdırıla bilər. Bu halda məhkumlar əlavə olaraq:</p>\r\n<p>113.3.1. ərzaq məhsulları və ən zəruri mallar əldə etmək &uuml;&ccedil;&uuml;n ayda <em>on beş manatadək</em> miqdarda pul xərcləyə bilərlər;</p>\r\n<p>113.3.2. il ərzində uzun m&uuml;ddətli d&ouml;rd g&ouml;r&uuml;ş almaq h&uuml;ququna malikdirlər;</p>\r\n<p>113.3.3. hər il məzuniyyət vaxtı cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyətinin qərarı ilə cəza&ccedil;əkmə m&uuml;əssisələrinin h&uuml;dudlarından kənara qısa m&uuml;ddətli səfər edə bilərlər.</p>\r\n<p>113.4. Ağır cinayətlərə g&ouml;rə ilk dəfə məhkum olunmuş <em>və bu Məcəllənin 70.2.2-ci maddəsində nəzərdə tutulmuş qaydada ciddi rejimli cəza&ccedil;əkmə m&uuml;əssisələrindən ke&ccedil;irilmiş</em> şəxslərin saxlandığı &uuml;mumi rejimli cəza&ccedil;əkmə m&uuml;əssisələrində məhkumlar: (22)</p>\r\n<p>113.4.1. adi yaşayış binalarında saxlanılırlar;</p>\r\n<p>113.4.2. ərzaq məhsulları və ən zəruri mallar əldə etmək &uuml;&ccedil;&uuml;n ayda <em>qırx beş manatadək</em> miqdarda pul xərcləyə bilərlər;</p>\r\n<p>113.4.3. il ərzində qısa m&uuml;ddətli iyirmi d&ouml;rd g&ouml;r&uuml;ş və uzun m&uuml;ddətli &uuml;&ccedil; g&ouml;r&uuml;ş almaq h&uuml;ququna malikdirlər;</p>\r\n<p>113.4.4. il ərzində iyirmi d&ouml;rd bağlama, sovqat və ya banderol almaq h&uuml;ququna malikdirlər.</p>\r\n<p>113.5. Məhkumlar cəzanın icrası qaydalarını pozmadıqda və əməyə vicdanla yanaşdıqda, cəza m&uuml;ddətinin ən azı &uuml;&ccedil;də birini &ccedil;əkdikdən sonra onların saxlanılması şəraiti yaxşılaşdırıla bilər. Bu halda məhkumlar əlavə olaraq;</p>\r\n<p>113.5.1. ərzaq məhsulları və ən zəruri mallar əldə etmək &uuml;&ccedil;&uuml;n ayda <em>on beş manatadək</em> miqdarda pul xərcləyə bilərlər;</p>\r\n<p>113.5.2. il ərzində qısa m&uuml;ddətli on iki g&ouml;r&uuml;ş və uzun m&uuml;ddətli &uuml;&ccedil; g&ouml;r&uuml;ş almaq h&uuml;ququna malikdirlər;</p>\r\n<p>113.5.3. il ərzində on iki bağlama, sovqat və ya banderol almaq h&uuml;ququna malikdirlər. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i14#i14">14</a>, <a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 114. Ciddi rejimli cəza&ccedil;əkmə m&uuml;əssisələri</em></strong></p>\r\n<p>Ciddi rejimli cəza&ccedil;əkmə m&uuml;əssisələrində x&uuml;susilə ağır cinayətlər t&ouml;rətməyə g&ouml;rə ilk dəfə m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş, habelə cinayətlərin residivi zamanı, yəni əvvəllər m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəza &ccedil;əkmiş şəxslər və cinayətlərin x&uuml;susi təhl&uuml;kəli residivinə g&ouml;rə m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş qadınlar cəza &ccedil;əkirlər.</p>\r\n<p><strong><em>Maddə 115. Ciddi rejimli cəza&ccedil;əkmə m&uuml;əssisələrində cəzanın icrası şərtləri</em></strong></p>\r\n<p>115.1. Ciddi rejimli cəza&ccedil;əkmə m&uuml;əssisələrində məhkumlar cəza&ccedil;əkmə m&uuml;əssisələrinin daxili intizam qaydalarına uyğun olaraq cəza&ccedil;əkmə m&uuml;əssisəsi ərizəsinin daxilində hərəkət edə bilərlər.</p>\r\n<p>115.2. Ciddi rejimli cəza&ccedil;əkmə m&uuml;əssisələrində məhkumlar:</p>\r\n<p>115.2.1. adi yaşayış binalarında saxlanılırlar:</p>\r\n<p>115.2.2. ərzaq məhsulları və ən zəruri mallar əldə etmək &uuml;&ccedil;&uuml;n ayda <em>qırx manatadək</em> miqdarda pul xərcləyə bilərlər;</p>\r\n<p>115.2.3. il ərzində qısa m&uuml;ddətli on iki g&ouml;r&uuml;ş və uzun m&uuml;ddətli iki g&ouml;r&uuml;ş almaq h&uuml;ququna malikdirlər;</p>\r\n<p>115.2.4. il ərzində on iki bağlama, sovqat və ya banderol almaq h&uuml;ququna malikdirlər.</p>\r\n<p>115.3. Məhkumlar cəzanın icrası qaydalarını pozmadıqda və əməyə vicdanla yanaşdıqda, cəza m&uuml;ddətinin ən azı yarısını &ccedil;əkdikdən sonra onların saxlanılması şəraiti yaxşılaşdırıla bilər. Bu halda məhkumlar əlavə olaraq;</p>\r\n<p>115.3.1. ərzaq məhsulları və ən zəruri mallar əldə etmək &uuml;&ccedil;&uuml;n ayda <em>on beş manatadək</em> miqdarda pul xərcləyə bilərlər;</p>\r\n<p>115.3.2. il ərzində qısa m&uuml;ddətli altı g&ouml;r&uuml;ş və uzun m&uuml;ddətli iki g&ouml;r&uuml;ş almaq h&uuml;ququna malikdirlər;</p>\r\n<p>115.3.3. il ərzində on iki bağlama, sovqat və ya banderol almaq h&uuml;ququna malikdirlər. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i14#i14">14</a>, <a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 116. X&uuml;susi rejimli cəza&ccedil;əkmə m&uuml;əssisələri</em></strong></p>\r\n<p>116.1. X&uuml;susi rejimli cəza&ccedil;əkmə m&uuml;əssisələrində cinayətlərin x&uuml;susi təhl&uuml;kəli residivinə g&ouml;rə m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş və &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzası m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəza ilə əvəz olunmuş kişilər cəza &ccedil;əkirlər.</p>\r\n<p>116.2. X&uuml;susi rejimli cəza&ccedil;əkmə m&uuml;əssisələrinə cəza &ccedil;əkməyə g&ouml;ndərilmiş məhkumlar kamera tipli otaqlarda saxlanılırlar. Məhkumlar bu Məcəllənin 105.10-cu maddəsində nəzərdə tutulmuş qaydada kamera tipli otaqlardan adi yaşayış binalarına ke&ccedil;irilə bilərlər.</p>\r\n<p><strong><em>Maddə 117. X&uuml;susi rejimli cəza&ccedil;əkmə m&uuml;əssisələrində cəzanın icrası şərtləri</em></strong></p>\r\n<p>117.1. X&uuml;susi rejimli cəza&ccedil;əkmə m&uuml;əssisələrində məhkumlar:</p>\r\n<p>117.1.1. kamera tipli otaqlarda və ya adi yaşayış binalarında saxlanılırlar;</p>\r\n<p>117.1.2. ərzaq məhsulları və ən zəruri mallar əldə etmək &uuml;&ccedil;&uuml;n ayda <em>otuz beş manatadək</em> miqdarda pul xərcləyə bilərlər;</p>\r\n<p>117.1.3. il ərzində qısa m&uuml;ddətli altı g&ouml;r&uuml;ş və uzun m&uuml;ddətli iki g&ouml;r&uuml;ş almaq h&uuml;ququna malikdirlər;</p>\r\n<p>117.1.4. il ərzində səkkiz bağlama, sovqat və ya banderol almaq h&uuml;ququna malikdirlər.</p>\r\n<p>117.2. Məhkumlar cəzanın icrası qaydalarını pozmadıqda və əməyə vicdanla yanaşdıqda cəza m&uuml;ddətinin ən azı yarısını &ccedil;əkdikdən sonra onların saxlanılması şəraiti yaxşılaşdırıla bilər. Bu halda məhkumlar əlavə olaraq:</p>\r\n<p>117.2.1. ərzaq məhsulları və ən zəruri mallar əldə etmək &uuml;&ccedil;&uuml;n ayda <em>on beş manatadək</em> miqdarda pul xərcləyə bilərlər;</p>\r\n<p>117.2.2. il ərzində qısa m&uuml;ddətli iki g&ouml;r&uuml;ş və uzun m&uuml;ddətli bir g&ouml;r&uuml;ş almaq h&uuml;ququna malikdirlər;</p>\r\n<p>117.2.3. il ərzində &uuml;&ccedil; bağlama, sovqat və ya banderol almaq h&uuml;ququna malikdirlər. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i14#i14">14</a>, <a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 118. Məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisələri</em></strong></p>\r\n<p><em>118.1. Məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisələrində ehtiyatsızlıqdan t&ouml;rədilmiş cinayətlərə g&ouml;rə beş ildən artıq olmayan m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər və bu Məcəllənin 70.2.3 və 70.2.4-c&uuml; maddələrində nəzərdə tutulmuş qaydada &uuml;mumi rejimli cəza&ccedil;əkmə m&uuml;əssisələrindən ke&ccedil;irilmiş şəxslər cəza &ccedil;əkirlər. &Uuml;mumi rejimli cəza&ccedil;əkmə m&uuml;əssisələrindən ke&ccedil;irilmiş ağır və ya az ağır cinayətlərə g&ouml;rə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisələrində digər məhkumlardan ayrı saxlanılırlar</em><em>. </em>(22)</p>\r\n<p>118.2. Məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisələrində məhkumlar eyni şəraitdə saxlanılırlar.</p>\r\n<p>118.3. Kişi və qadın məhkumlar eyni məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisələrində saxlanıla bilərlər. İştirak&ccedil;ılıqla cinayət t&ouml;rədən şəxslər bir qayda olaraq ayrılıqda cəza &ccedil;əkirlər.</p>\r\n<p><strong><em>Maddə 119. Məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisələrində cəzanın icrası şərtləri</em></strong></p>\r\n<p><em>119.1. Məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisələrində məhkumlar:</em></p>\r\n<p><em>119.1.1. m&uuml;hafizəsiz, lakin nəzarət altında saxlanılırlar;</em></p>\r\n<p><em>19.1.2. m&uuml;əyyən edilmiş n&uuml;munəli şəxsiyyəti təsdiq edən sənəd daşıyırlar;</em></p>\r\n<p><em>119.1.3. yuxudan durmaq vaxtından yatmaq vaxtınadək m&uuml;əssisədə sərbəst hərəkət edə bilərlər; </em></p>\r\n<p><em>119.1.4. məhdudiyyət qoyulmadan qohumları ilə və başqa şəxslərlə yazışa və g&ouml;r&uuml;şə bilər, bağlama, sovqat və ya banderol ala bilərlər;</em></p>\r\n<p><em><br /> 119.1.5. cəza&ccedil;əkmə m&uuml;əssisəsində g&uuml;ndə bir dəfə m&uuml;ddəti on beş dəqiqə olmaqla telefonla danışa bilərlər;</em></p>\r\n<p><em>119.1.6. məişətdə istifadə edilən &uuml;mumi paltar geyinə, pul və qiymətli şeylər gəzdirə, puldan qeyri-məhdud istifadə edə bilərlər.</em></p>\r\n<p><em>119.2. Məntəqə tipli cəza&ccedil;əkmə m&uuml;əssisəsində cəza &ccedil;əkən məhkumların kommunal, yemək, maddi-məişət, tibbi-sanitariya və digər təminatları d&ouml;vlət hesabına həyata ke&ccedil;irilir.</em></p>\r\n<p><em>119.3. Cəza&ccedil;əkmə m&uuml;əssisəsindən kənarda m&uuml;əssisəyə yaxın ərazidə işə d&uuml;zələn və ya ali, orta ixtisas, ilk peşə-ixtisas təhsili m&uuml;əssisələrində təhsil alan məhkumlar cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyətinin icazəsi ilə sutkada on iki saatdan artıq olmayan m&uuml;ddətdə m&uuml;əssisədən kənarda, lakin Azərbaycan Respublikasının ərazisi h&uuml;dudlarında nəzarətsiz hərəkət edə bilərlər. Bu halda m&uuml;əssisənin m&uuml;diriyyəti tərəfindən qəbul edilən qərarda hər bir məhkum &uuml;&ccedil;&uuml;n fərdi şəkildə tərtib olunan cəza&ccedil;əkmə m&uuml;əssisəsindən &ccedil;ıxma və cəza&ccedil;əkmə m&uuml;əssisəsinə qayıtma qrafiki m&uuml;əyyən edilir.</em></p>\r\n<p><em>119.4. Cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti tərəfindən zəruri hesab edildikdə, cəza&ccedil;əkmə m&uuml;əssisəsindən kənarda işə d&uuml;zələn və ya təhsil alan məhkumun m&uuml;vafiq olaraq işdən və ya təhsildən yayınıb-yayınmaması yoxlanılır. Yoxlama nəticəsində məhkumun &uuml;zrs&uuml;z səbəbdən işdən və ya təhsildən yayınması m&uuml;əyyən olunarsa, bu Məcəllənin 119.3-c&uuml; maddəsində nəzərdə tutulmuş qərar ləğv edilir.</em> (22)</p>\r\n<p><strong><em>Maddə 120. Həbsxanalar</em></strong></p>\r\n<p>120.1. Həbsxanalarda x&uuml;susilə ağır cinayətlər t&ouml;rətməyə g&ouml;rə beş ildən artıq m&uuml;ddətə azadlıqdan məhrum etməyə məhkum olunmuş şəxslər, cəza m&uuml;ddətini tam və ya onun bir hissəsini &ccedil;əkməklə cinayətlərin x&uuml;susi təhl&uuml;kəli residivinə g&ouml;rə məhkum olunmuş şəxslər, &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər, həm&ccedil;inin &uuml;mumi, ciddi və x&uuml;susi rejimli cəza&ccedil;əkmə m&uuml;əssisələrindən bu Məcəllənin 70.4.3-c&uuml; maddəsinə uyğun olaraq həbsxanaya ke&ccedil;irilmiş məhkumlar saxlanılırlar.</p>\r\n<p>120.2. &Ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş və təsərr&uuml;fat və məişət xidməti işi &uuml;&ccedil;&uuml;n həbsxanada saxlanılmış şəxslər istisna olmaqla, həbsxanalarda saxlanılan digər məhkumlar &uuml;&ccedil;&uuml;n &uuml;mumi və ciddi saxlanma şəraiti m&uuml;əyyən edilir.</p>\r\n<p>120.3. &Uuml;mumi saxlanma şəraitində ilk dəfə həbsxanaya cəza &ccedil;əkməyə g&ouml;ndərilmiş şəxslər və ciddi saxlanma şəraitindən ke&ccedil;irilmiş şəxslər saxlanılırlar.</p>\r\n<p>120.4. Ciddi saxlanma şəraitində əvvəllər həbsxanada cəza &ccedil;əkmiş şəxslər, cəza&ccedil;əkmə m&uuml;əssisələrində t&ouml;rədilmiş cinayətlərə g&ouml;rə məhkum olunmuş və ya m&uuml;əyyən edilmiş qaydada tənbeh tədbiri olaraq &uuml;mumi saxlanma şəraitindən ciddi saxlanma şəraitinə ke&ccedil;irilmiş şəxslər saxlanılırlar. Ciddi saxlanma şəraitində olma m&uuml;ddəti iki aydan altı ayadək m&uuml;əyyən olunur.</p>\r\n<p>120.5. Məhkum qadınlar, həm&ccedil;inin birinci və ikinci qrup əlilliyi olan məhkumlar ciddi saxlanma şəraitinə ke&ccedil;irilə bilməzlər.</p>\r\n<p>120.6. Cəzanın icrası qaydalarını pozmağa g&ouml;rə &uuml;mumi saxlanma şəraitindən ciddi saxlanma şəraitinə ke&ccedil;irilmiş məhkumlar təkrarən &uuml;mumi saxlanma şəraitinə bu Məcəllə ilə m&uuml;əyyən edilmiş qaydada ke&ccedil;irilirlər.</p>\r\n<p><strong><em>Maddə 121. Həbsxanalarda cəzanın icrası şərtləri</em></strong></p>\r\n<p>121.1. <em>Təsərr&uuml;fat və məişət xidməti işi &uuml;&ccedil;&uuml;n cəlb olunmuş</em> şəxslər istisna olmaqla məhkumlar həbsxanada &uuml;mumi kameralarda saxlanılırlar. Zəruri hallarda məhkumlar həbsxana m&uuml;diriyyətinin əsaslandırılmış qərarı ilə birnəfərlik kameralarda saxlanıla bilərlər.</p>\r\n<p>121.2. Həbsxanada məhkumlar kameralar &uuml;zrə bu Məcəllənin 72.1&mdash;72.3 və 72.5-ci maddələrinin tələblərinə əməl olunmaqla yerləşdirilirlər. &Uuml;mumi və ciddi saxlanma şəraitində saxlanılan şəxslər, &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər və təsərr&uuml;fat və məişət xidməti işi &uuml;&ccedil;&uuml;n həbsxanada saxlanılan məhkumlar bir-birindən təcrid olunmuş halda və ayrılıqda saxlanılırlar.</p>\r\n<p>121.3. Həbsxanalarda &uuml;mumi saxlanma şəraitində məhkumlar:</p>\r\n<p>121.3.1. ərzaq məhsulları və ən zəruri mallar əldə etmək &uuml;&ccedil;&uuml;n ayda <em>iyirmi beş manatadək</em> miqdarda pul xərcləyə bilərlər;</p>\r\n<p>121.3.2. il ərzində qısa m&uuml;ddətli d&ouml;rd g&ouml;r&uuml;ş və uzun m&uuml;ddətli iki g&ouml;r&uuml;ş almaq h&uuml;ququna malikdirlər;</p>\r\n<p>121.3.3. il ərzində altı bağlama, sovqat və ya banderol almaq h&uuml;ququna malikdirlər;</p>\r\n<p>121.3.4. hər g&uuml;n bir saat gəzinti h&uuml;ququna malikdirlər.</p>\r\n<p>121.4. Məhkumlar cəzanın icrası qaydalarını pozmadıqda və əməyə vicdanla yanaşdıqda, həbsxanada &ccedil;əkilməsi m&uuml;əyyən olunmuş cəza m&uuml;ddətinin ən azı yarısını &ccedil;əkdikdən sonra onların saxlanılması şəraiti yaxşılaşdırıla bilər. Bu halda məhkumlar əlavə olaraq:</p>\r\n<p>121.4.1. ərzaq məhsulları və ən zəruri mallar əldə etmək &uuml;&ccedil;&uuml;n ayda <em>on beş manatadək</em> miqdarda pul xərcləyə bilərlər;</p>\r\n<p>121.4.2. il ərzində uzun m&uuml;ddətli bir g&ouml;r&uuml;ş almaq h&uuml;ququna malikdirlər;</p>\r\n<p>121.4.3. il ərzində altı bağlama, sovqat və ya banderol almaq h&uuml;ququna malikdirlər.</p>\r\n<p>121.5. Həbsxanalarda ciddi saxlanma şəraitində məhkumlar:</p>\r\n<p>121.5.1. ərzaq məhsulları və ən zəruri mallar əldə etmək &uuml;&ccedil;&uuml;n ayda <em>on beş manatadək</em> miqdarda pul xərcləyə bilərlər;</p>\r\n<p>121.5.2. altı ayda bir bağlama, sovqat və ya banderol almaq h&uuml;ququna malikdirlər;</p>\r\n<p>121.5.3. altı ayda bir qısa m&uuml;ddətli g&ouml;r&uuml;ş almaq h&uuml;ququna malikdirlər;</p>\r\n<p>121.5.4. hər g&uuml;n bir saat gəzinti h&uuml;ququna malikdirlər. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i14#i14">14</a>, <a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 122. &Ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslərin saxlanılması şərtləri</em></strong></p>\r\n<p>122.1. &Ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər həbsxanalarda kameralarda saxlanılırlar. Bu məhkumlar:</p>\r\n<p>122.1.1. ərzaq məhsulları və ən zəruri mallar əldə etmək &uuml;&ccedil;&uuml;n ayda <em>iyirmi beş manatadək</em> miqdarda pul xərcləyə bilərlər;</p>\r\n<p>122.1.2. il ərzində qısa m&uuml;ddətli <em>altı</em> g&ouml;r&uuml;ş və uzun m&uuml;ddətli <em>iki</em> g&ouml;r&uuml;ş almaq h&uuml;ququna malikdirlər;</p>\r\n<p>122.1.3. il ərzində <em>səkkiz</em> bağlama, sovqat və ya banderol almaq h&uuml;ququna malikdirlər;</p>\r\n<p>122.1.4. hər g&uuml;n bir saat gəzinti h&uuml;ququna malikdirlər.</p>\r\n<p>122.2. Məhkumlar cəzanın icrası qaydalarını pozmadıqda və əməyə vicdanla yanaşdıqda, təyin olunmuş cəzadan ən azı on il ke&ccedil;dikdən sonra onların saxlanılması şəraiti yaxşılaşdırıla bilər. Bu halda məhkumlar əlavə olaraq:</p>\r\n<p>122.2.1. ərzaq məhsulları və ən zəruri mallar əldə etmək &uuml;&ccedil;&uuml;n ayda <em>iyirmi beş manatadək</em> miqdarda pul xərcləyə bilərlər;</p>\r\n<p>122.2.2. il ərzində qısa m&uuml;ddətli iki g&ouml;r&uuml;ş və uzun m&uuml;ddətli bir g&ouml;r&uuml;ş almaq h&uuml;ququna malikdirlər;</p>\r\n<p>122.2.3. il ərzində iki bağlama, sovqat və ya banderol almaq h&uuml;ququna malikdirlər;</p>\r\n<p>122.2.4. il ərzində iki dəfə telefonla danışmaq h&uuml;ququna malikdirlər.</p>\r\n<p>122.3. Yaxşılaşdırılmış saxlanılma şəraiti ləğv edildikdə, bu həvəsləndirmə tədbiri m&uuml;vafiq qərarın qəbul olunduğu vaxtdan &uuml;&ccedil; il ke&ccedil;dikdən sonra yenidən tətbiq edilə bilər. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i14#i14">14</a>, <a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong>XV fəsil. Yetkinlik yaşına &ccedil;atmayan məhkumların azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəza &ccedil;əkməsinin x&uuml;susiyyətləri</strong></p>\r\n<p><strong><em>Maddə 123. Yetkinlik yaşına &ccedil;atmayan məhkumlar &uuml;&ccedil;&uuml;n cəza&ccedil;əkmə m&uuml;əssisələri</em></strong></p>\r\n<p>123.1. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş yetkinlik yaşına &ccedil;atmayan məhkumlar &uuml;mumi və m&ouml;hkəm rejimli tərbiyə m&uuml;əssisələrində cəza &ccedil;əkirlər.</p>\r\n<p>123.2. &Uuml;mumi rejimli tərbiyə m&uuml;əssisələrində yetkinlik yaşına &ccedil;atmayan qızlar, habelə ilk dəfə m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş yetkinlik yaşına &ccedil;atmayan oğlanlar cəza &ccedil;əkirlər.</p>\r\n<p>123.3. M&ouml;hkəm rejimli tərbiyə m&uuml;əssisələrində əvvəllər m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəza &ccedil;əkmiş yetkinlik yaşına &ccedil;atmayan oğlanlar cəza &ccedil;əkirlər.</p>\r\n<p><strong><em>Maddə 124. Tərbiyə m&uuml;əssisələrində cəzanın icrası şərtləri</em></strong></p>\r\n<p>124.1. Yetkinlik yaşına &ccedil;atmayan məhkumlar cəza&ccedil;əkmə m&uuml;əssisələrinin daxili intizam qaydalarına uyğun olaraq tərbiyə m&uuml;əssisəsinin ərazisi daxilində hərəkət edə bilərlər.</p>\r\n<p>124.2. &Uuml;mumi rejimli tərbiyə m&uuml;əssisələrində məhkumlar:</p>\r\n<p>124.2.1. adi yaşayış binalarında saxlanılırlar;</p>\r\n<p>124.2.2. ərzaq məhsulları və ən zəruri mallar əldə etmək &uuml;&ccedil;&uuml;n ayda <em>əlli manatadək</em> miqdarda pul xərcləyə bilərlər;</p>\r\n<p>124.2.3. il ərzində qısa m&uuml;ddətli qırx səkkiz və uzun m&uuml;ddətli səkkiz g&ouml;r&uuml;ş almaq h&uuml;ququna malikdirlər;</p>\r\n<p>124.2.4. il ərzində qırx səkkiz bağlama, sovqat və ya banderol almaq h&uuml;ququna malikdirlər.</p>\r\n<p>124.3. &Uuml;mumi rejimli tərbiyə m&uuml;əssisələrində məhkumlar cəzanın icrası qaydalarını pozmadıqda, əməyə və təhsilə vicdanla yanaşdıqda cəza m&uuml;ddətinin ən azı d&ouml;rddə birini &ccedil;əkdikdən sonra onların saxlanılması şəraiti yaxşılaşdırıla bilər.Bu halda məhkumlar əlavə olaraq:</p>\r\n<p>124.3.1. ərzaq məhsulları və ən zəruri mallar əldə etmək &uuml;&ccedil;&uuml;n ayda <em>on beş manatadək</em> miqdarda pul xərcləyə bilərlər;</p>\r\n<p>124.3.2. il ərzində uzun m&uuml;ddətli d&ouml;rd g&ouml;r&uuml;ş almaq h&uuml;ququna malikdirlər;</p>\r\n<p>124.3.3. hər il məzuniyyət vaxtı cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyətinin qərarı ilə cəza&ccedil;əkmə m&uuml;əssisələrinin h&uuml;dudlarından kənara valideynlərinin və ya himayə&ccedil;ilərinin m&uuml;şayiəti ilə qısa m&uuml;ddətli səfər etməyə icazə verilə bilər.</p>\r\n<p>124.4. M&ouml;hkəm rejimli tərbiyə m&uuml;əssisələrində məhkumlar:</p>\r\n<p>124.4.1. adi yaşayış binalarında saxlanılırlar;</p>\r\n<p>124.4.2. ərzaq məhsulları və ən zəruri mallar əldə etmək &uuml;&ccedil;&uuml;n ayda <em>qırx manatadək</em> miqdarda pul xərcləyə bilərlər;</p>\r\n<p>124.4.3. il ərzində qısa m&uuml;ddətli iyirmi d&ouml;rd g&ouml;r&uuml;ş və uzun m&uuml;ddətli &uuml;&ccedil; g&ouml;r&uuml;ş almaq h&uuml;ququna malikdirlər;</p>\r\n<p>124.4.4. il ərzində iyirmi d&ouml;rd bağlama, sovqat və ya banderol almaq h&uuml;ququna malikdirlər.</p>\r\n<p>124.5. M&ouml;hkəm rejimli tərbiyə m&uuml;əssisələrində məhkumlar cəzanın icrası qaydalarını pozmadıqda, əməyə və təhsilə vicdanla yanaşdıqda, cəza m&uuml;ddətinin azı &uuml;&ccedil;də birini &ccedil;əkdikdən sonra onların saxlanılması şəraiti yaxşılaşdırıla bilər. Bu halda məhkumlar əlavə olaraq:</p>\r\n<p>124.5.1. ərzaq məhsulları və ən zəruri mallar əldə etmək &uuml;&ccedil;&uuml;n ayda <em>on beş manatadək</em> miqdarda pul xərcləyə bilərlər;</p>\r\n<p>124.5.2. il ərzində qısa m&uuml;ddətli on iki g&ouml;r&uuml;ş və uzun m&uuml;ddətli &uuml;&ccedil; g&ouml;r&uuml;ş almaq h&uuml;ququna malikdirlər. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i14#i14">14</a>, <a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 124-1. Tərbiyə m&uuml;əssisələrində məhkumlara tətbiq edilən həvəsləndirmə tədbirləri</em></strong></p>\r\n<p><em>124-1.1. Tərbiyə m&uuml;əssisələrində n&uuml;munəvi davranışa, əməyə və təhsilə vicdanlı m&uuml;nasibətə, &ouml;zfəaliyyət təşkilatlarında və tərbiyə tədbirlərində fəal iştirakına g&ouml;rə yetkinlik yaşına &ccedil;atmayan məhkumlara bu Məcəllənin 105.1-ci maddəsində nəzərdə tutulmuş həvəsləndirmə tədbirlərindən əlavə tərbiyə m&uuml;əssisəsi n&uuml;mayəndəsinin m&uuml;şayiəti ilə m&uuml;əssisənin h&uuml;dudlarından kənarda idman, mədəni-k&uuml;tləvi və digər tədbirlərə bir dəfə tamaşa etməyə icazə vermək n&ouml;v&uuml;ndə həvəsləndirmə tədbiri tətbiq edilə bilər.</em></p>\r\n<p><em>124-1.2. Həvəsləndirmə tədbiri qaydasında tərbiyə m&uuml;əssisəsinin n&uuml;mayəndəsinin m&uuml;şayiəti ilə m&uuml;əssisəsinin h&uuml;dudlarından kənarda idman, mədəni-k&uuml;tləvi və digər tədbirlərə tamaşa etməyə icazə verilmiş məhkumlara tədbir zamanı onlara məxsus m&uuml;lki paltarlar verilir.</em></p>\r\n<p><em>124-1.3. Tərbiyə m&uuml;əssisəsindən kənara &ccedil;ıxma m&uuml;ddəti səkkiz saatdan &ccedil;ox olmamaq şərti ilə m&uuml;əssisənin m&uuml;diriyyəti tərəfindən m&uuml;əyyən edilir.</em></p>\r\n<p><em>124-1.4. Məhkumların gecə vaxtı ke&ccedil;irilən idman, mədəni-k&uuml;tləvi və digər tədbirlərdə iştirakına yol verilmir.</em> (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i16#i16">16</a>)</p>\r\n<p><strong><em>Maddə 125. Tərbiyə m&uuml;əssisələrində məhkumlara tətbiq edilən tənbeh tədbirləri</em></strong></p>\r\n<p>Cəzanın icrası qaydalarını pozan yetkinlik yaşına &ccedil;atmayan məhkumlara bu Məcəllənin 107.1.1 və 107.1.2-ci maddələrində nəzərdə tutulmuş tənbeh tədbirlərindən əlavə təhsili davam etdirmək şərtilə yeddi g&uuml;nədək m&uuml;ddətə intizam təcridxanasına ke&ccedil;irmək n&ouml;v&uuml;ndə tənbeh tədbiri də tətbiq edilə bilər.</p>\r\n<p><strong><em>Maddə 126. Məhkumların intizam təcridxanasında saxlanılması şərtləri</em></strong></p>\r\n<p>126.1. İntizam təcridxanalarına ke&ccedil;irilmiş məhkumlara uzun m&uuml;ddətli g&ouml;r&uuml;ş, telefon danışıqları, ərzaq məhsulları və ən zəruri mallar əldə etmək, bağlama, sovqat və ya banderol almaq, kinofilmlərə və televiziya verilişlərinə baxmaq, stol&uuml;st&uuml; oyunlardan istifadə etmək qadağan edilir. Onlar hər g&uuml;n iki saat gəzinti h&uuml;ququna malikdirlər.</p>\r\n<p>126.2. İntizam təcridxanasında saxlanılan məhkumlara intizam təcridxanasına ke&ccedil;irilməkdən başqa digər tənbeh tədbirləri tətbiq edilə bilər.</p>\r\n<p>126.3. Bir təqvim ili ərzində intizam təcridxanasında saxlanma m&uuml;ddəti otuz g&uuml;ndən &ccedil;ox ola bilməz.</p>\r\n<p><strong><em>Maddə 127. Tərbiyə m&uuml;əssisələrində məhkumlara həvəsləndirmə və tənbeh tədbirlərini tətbiq edən vəzifəli şəxslər </em></strong></p>\r\n<p>Yetkinlik yaşına &ccedil;atmayan məhkumlara həvəsləndirmə və tənbeh tədbirlərini tərbiyə m&uuml;əssisəsinin m&uuml;diriyyəti tətbiq edir.</p>\r\n<p><strong><em>Maddə 128. Yetkinlik yaşına &ccedil;atmış məhkumların tərbiyə m&uuml;əssisələrində saxlanılması</em></strong></p>\r\n<p>128.1. On səkkiz yaşına &ccedil;atmış məhkumlar bir qayda olaraq tərbiyə m&uuml;əssisələrindən cəza&ccedil;əkmə m&uuml;əssisələrinə ke&ccedil;irilirlər. Tərbiyə m&uuml;əssisələrində məhkəmənin qərarına əsasən məhkumlar iyirmi yaşınadək saxlanıla bilərlər.</p>\r\n<p>128.2. On səkkiz yaşı tamam olandan sonra tərbiyə m&uuml;əssisələrində saxlanılan məhkumlara yetkinlik yaşına &ccedil;atmayan məhkumlar &uuml;&ccedil;&uuml;n m&uuml;əyyən edilmiş cəzanın icrası şərtləri, yemək normaları və maddi-məişət təminatı aid edilir. Bu məhkumların əmək şəraiti əmək qanunvericiliyinə uyğun olaraq m&uuml;əyyən edilir.</p>\r\n<p><strong><em>Maddə 129. Məhkumların tərbiyə m&uuml;əssisəsindən cəza&ccedil;əkmə m&uuml;əssisəsinə ke&ccedil;irilməsi</em></strong></p>\r\n<p>On səkkiz yaşına &ccedil;atmış və tərbiyə m&uuml;əssisələrində saxlanılmış məhkumlar cəzanın icrası qaydalarını pozduqda tərbiyə m&uuml;əssisəsinin m&uuml;diriyyətinin təqdimatı və məhkəmənin qərarı ilə &uuml;mumi rejimli cəza&ccedil;əkmə m&uuml;əssisəsinə ke&ccedil;irilirlər.</p>\r\n<p><strong><em>Maddə 130. Tərbiyə m&uuml;əssisələrində tədris-tərbiyə prosesinin təşkili</em></strong></p>\r\n<p>130.1. Tərbiyə m&uuml;əssisələrində məhkumların islah edilməsi və m&uuml;stəqil həyata hazırlanması məqsədilə onlarda qanuna itaətin həvəsləndirilməsinə, əməyə və təhsilə vicdanlı m&uuml;nasibətin formalaşmasına, onların mədəni səviyyəsinin artırılmasına y&ouml;nəldilmiş vahid tədris-tərbiyə prosesi həyata ke&ccedil;irilir.</p>\r\n<p>130.2. Məhkumların &uuml;mumi təhsili tərbiyə m&uuml;əssisələrinin &uuml;mumtəhsil məktəblərində, <em>ilk peşə-ixtisas təhsili</em> ilə tərbiyə m&uuml;əssisələrinin peşə məktəblərində və ya tərbiyə m&uuml;əssisələrinin istehsalat sahələrində həyata ke&ccedil;irilir. (20)</p>\r\n<p><em>130.3. Tərbiyə m&uuml;əssisələrində psixoloji-tibbi-pedaqoji komissiyanın rəyində m&uuml;əyyən edilmiş şərtlərə əməl etməklə sağlamlıq imkanları məhdud məhkumların x&uuml;susi təhsili qanunvericiliklə m&uuml;əyyən edilmiş qaydada təşkil edilir.</em></p>\r\n<p><strong><em>Maddə 131. Tərbiyə m&uuml;əssisələrinin işində ictimaiyyətin iştirakı</em></strong></p>\r\n<p>131.1. Tərbiyə m&uuml;əssisəsinin m&uuml;diriyyətinə tədris-tərbiyə prosesinin təşkilində, onun maddi-texniki bazasının m&ouml;hkəmləndirilməsində, məhkumların sosial m&uuml;dafiəsinə dair məsələlərin həllində, cəza &ccedil;əkməkdən azad olunanlara əmək və məişət şəraitinin yaradılmasında k&ouml;mək g&ouml;stərilməsi məqsədi ilə tərbiyə m&uuml;əssisəsinin nəzdində d&ouml;vlət m&uuml;əssisə, idarə və ya təşkilatlarının, ictimai birliklərin, digər m&uuml;əssisələrin n&uuml;mayəndələrinin və fiziki şəxslərin iştirakı ilə himayə&ccedil;ilik şurası yaradılır. Himayə&ccedil;ilik şurasının işinin təşkili və fəaliyyət qaydası <a title="Azərbaycan Respublikasının Nazirlər Kabineti / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanının</a> təsdiq etdiyi normativ h&uuml;quqi aktla tənzimlənir.</p>\r\n<p>131.2. Məhkumlara g&ouml;stərilən tərbiyəvi təsirin səmərəliliyini artırmaq və tərbiyə m&uuml;əssisəsinin m&uuml;diriyyətinə islah işində k&ouml;mək etmək məqsədi ilə bu m&uuml;əssisələrdə məhkumların valideynlərindən və ya digər yaxın qohumlarından ibarət valideynlər komitəsi yaradıla bilər. Valideynlər komitəsinin fəaliyyəti <a title="Azərbaycan Respublikasının Ədliyyə Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanının</a> təsdiq etdiyi normativ-h&uuml;quqi aktla tənzimlənir.</p>\r\n<p><strong><em>IV</em></strong><strong><em>b&ouml;lmə. Hərbi xidmət &uuml;zrə məhdudlaşdırma, intizam xarakterli hərbi hissədə saxlama n&ouml;v&uuml;ndə cəzaların icrası</em></strong></p>\r\n<p><strong>XVI fəsil. Hərbi xidmət &uuml;zrə məhdudlaşdırma n&ouml;v&uuml;ndə cəzanın icrası</strong></p>\r\n<p><strong><em>Maddə 132. Hərbi xidmət &uuml;zrə məhdudlaşdırma n&ouml;v&uuml;ndə cəzanın icrası ilə əlaqədar hərbi hissə komandirinin (rəisinin) vəzifələri</em></strong></p>\r\n<p>132.1. Hərbi hissə komandiri (rəisi) məhkəmədən h&ouml;km&uuml;n surətini və cəzanın icrası barədə g&ouml;stərişi aldıqdan sonra &uuml;&ccedil; g&uuml;ndən gec olmayan m&uuml;ddətə yazılı əmr verir. Əmrdə h&ouml;kmə əsasən məhkum olunmuş hərbi qulluq&ccedil;unun hansı əsaslara g&ouml;rə və hansı m&uuml;ddətə vəzifədə və ya r&uuml;tbədə y&uuml;ksəlməməsi və cəza m&uuml;ddətinin n&ouml;vbəti hərbi r&uuml;tbənin verilməsi &uuml;&ccedil;&uuml;n xidmət illərinə daxil edilməməsi g&ouml;stərilməlidir. Əmrdə həm&ccedil;inin cəzanın icrası m&uuml;ddəti ərzində məhkumun pul təminatından d&ouml;vlət nəfinə tutulmanın məbləği g&ouml;stərilməlidir. Məhkum olunmuş hərbi qulluq&ccedil;u əmrlə tanış edilir.</p>\r\n<p>132.2. Hərbi hissə komandiri (rəisi) h&ouml;km&uuml;n surətini və cəzanın icrası barədə g&ouml;stərişi alması və h&ouml;km&uuml;n icraya y&ouml;nəldilməsi barədə h&ouml;km&uuml; &ccedil;ıxaran məhkəməyə dərhal məlumat verir.</p>\r\n<p><strong><em>Maddə 133. Məhkumların qazancından tutulmalar</em></strong></p>\r\n<p>Hərbi xidmət &uuml;zrə məhdudlaşdırma n&ouml;v&uuml;ndə cəzaya məhkum olunmuş hərbi qulluq&ccedil;uların qazancından məhkəmənin h&ouml;km&uuml; ilə m&uuml;əyyən edilmiş məbləğdə d&ouml;vlət nəfinə tutulmalar aparılır.</p>\r\n<p><strong><em>Maddə 134. Məhkumlarla tərbiyə işi</em></strong></p>\r\n<p>Hərbi hissə komandiri (rəisi) hərbi xidmət &uuml;zrə məhdudlaşdırma n&ouml;v&uuml;ndə cəzaya məhkum olunmuş hərbi qulluq&ccedil;ularla tərbiyə işini t&ouml;rədilmiş cinayətin ictimai təhl&uuml;kəlilik dərəcəsini və xarakterini, məhkumun şəxsiyyətini, davranışını, həm&ccedil;inin hərbi xidmətə m&uuml;nasibətini nəzərə alaraq təşkil edir və həyata ke&ccedil;irir.</p>\r\n<p><strong><em>Maddə 135. Məhkumun xidməti yerdəyişmələri</em></strong></p>\r\n<p>135.1. Məhkəmənin h&ouml;km&uuml;nə əsasən hərbi xidmət &uuml;zrə məhdudlaşdırma n&ouml;v&uuml;ndə cəzaya məhkum olunmuş hərbi qulluq&ccedil;ular məhkəmə tərəfindən m&uuml;əyyən edilmiş cəzanın icrası m&uuml;ddətində vəzifədə və ya r&uuml;tbədə y&uuml;ksələ bilməzlər.</p>\r\n<p>135.2. Əgər məhkum t&ouml;rədilmiş cinayətin x&uuml;susiyyəti və digər hallar nəzərə alınmaqla tabeliyində olan şəxslərə rəhbərlik etməklə əlaqədar vəzifədə saxlanıla bilməzsə, o, m&uuml;vafiq komandirin (rəisin) əmrinə və ya təqdimatına əsasən həmin hərbi hissədə, yaxud digər hərbi hissədə başqa vəzifəyə ke&ccedil;irilir. Bu halda h&ouml;km&uuml; &ccedil;ıxarmış məhkəməyə dərhal məlumat verilir.</p>\r\n<p><strong><em>Maddə 136. Cəzanın icrasının dayandırılması</em></strong></p>\r\n<p>Hərbi xidmət &uuml;zrə məhdudlaşdırmanın məhkəmə tərəfindən m&uuml;əyyən olunmuş m&uuml;ddətinin qurtarmasına azı &uuml;&ccedil; g&uuml;n qalmış hərbi hissə komandiri (rəisi) cəzanın icrasının hansı tarixdən dayandırılması barədə əmr verir. Əmr hərbi qulluq&ccedil;unun nəzərinə &ccedil;atdırılır.</p>\r\n<p><strong><em>Maddə 137. Hərbi xidmət &uuml;zrə məhdudlaşdırma n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxsin hərbi xidmətdən tərxis olunması ilə əlaqədar cəza &ccedil;əkməkdən azad edilməsi və ya cəzanın digər cəza n&ouml;v&uuml; ilə əvəz edilməsi</em></strong></p>\r\n<p>137.1. Hərbi xidmət &uuml;zrə məhdudlaşdırma n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxs h&ouml;kmlə m&uuml;əyyən edilmiş cəza m&uuml;ddətinin qurtarmasına qədər qanunvericilikdə nəzərdə tutulan əsaslarla hərbi xidmətdən tərxis oluna bilər.</p>\r\n<p>137.2. Hərbi xidmət &uuml;zrə məhdudlaşdırma n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxsin hərbi xidmətdən tərxis olunması ilə əlaqədar hərbi hissənin komandiri (rəisi) cəzanın &ccedil;əkilməmiş hissəsinin digər cəza n&ouml;v&uuml; ilə əvəz olunması &uuml;&ccedil;&uuml;n məhkəməyə təqdimat verir. Məhkum &ouml;z n&ouml;vbəsində cəzanın &ccedil;əkilməmiş hissəsinin daha y&uuml;ng&uuml;l cəza n&ouml;v&uuml; ilə əvəz edilməsi və ya cəzadan şərti olaraq vaxtından əvvəl azad edilməsi barədə məhkəməyə ərizə ilə m&uuml;raciət edə bilər.</p>\r\n<p><strong>XVII fəsil. İntizam xarakterli hərbi hissədə saxlama n&ouml;v&uuml;ndə cəzanın icrası</strong></p>\r\n<p><strong><em>Maddə 138. İntizam xarakterli hərbi hissələr</em></strong></p>\r\n<p>138.1. İntizam xarakterli hərbi hissədə saxlama n&ouml;v&uuml;ndə cəzanın icrası bu məqsədlər &uuml;&ccedil;&uuml;n yaradılmış hərbi hissənin komandiri tərəfindən həyata ke&ccedil;irilir. İntizam xarakterli hərbi hissənin təşkilat strukturu və onun say tərkibi <a title="Azərbaycan Respublikasının M&uuml;dafiə Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanı</a> tərəfindən m&uuml;əyyən edilir.</p>\r\n<p>138.2. İntizam xarakterli hərbi hissəyə məhkumların g&ouml;ndərilməsi və qəbulu qaydaları m&uuml;vafiq icra hakimiyyəti orqanı tərəfindən m&uuml;əyyən edilir.</p>\r\n<p>138.3. İntizam xarakterli hərbi hissədə saxlanılan məhkumlara &uuml;mumi təhsil və peşə hazırlığı kimi islah edilmə vasitəsi istisna olmaqla, bu Məcəllənin m&uuml;ddəalarına uyğun digər islah edilmə vasitələri tətbiq edilir və əlavə olaraq onlarla d&ouml;y&uuml;ş hazırlığı təlimi ke&ccedil;irilir.</p>\r\n<p><strong><em>Maddə 139. İntizam xarakterli hərbi hissədə rejim</em></strong></p>\r\n<p>139.1. İntizam xarakterli hərbi hissənin h&uuml;dudlarında məhkumların m&uuml;hafizəsi, onlar &uuml;zərində nəzarəti təmin etmək vəzifələrini, onların h&uuml;quqlarının həyata ke&ccedil;irilməsini, intizam xarakterli hərbi hissədə xidmət ke&ccedil;ən hərbi qulluq&ccedil;uların və məhkumların təhl&uuml;kəsizliyini təmin edən cəzanın icrası və &ccedil;əkilməsi qaydaları (rejimi) m&uuml;əyyən edilir.</p>\r\n<p>139.2. Məhkum olunmuş hərbi qulluq&ccedil;ular intizam xarakterli hərbi hissədə m&uuml;əyyən edilmiş rejimin tələblərinə riayət etməlidirlər.</p>\r\n<p>139.3. İntizam xarakterli hərbi hissədə cəzanın icrası m&uuml;ddətində b&uuml;t&uuml;n məhkumlar hərbi r&uuml;tbədən, əvvəlki xidmətin x&uuml;susiyyətindən asılı olmayaraq hərbi qulluq&ccedil;ular &uuml;&ccedil;&uuml;n m&uuml;əyyən edilmiş geyim forması daşıyırlar.</p>\r\n<p><strong><em>Maddə 140. İntizam xarakterli hərbi hissədə məhkumlara g&ouml;r&uuml;ş verilməsi</em></strong></p>\r\n<p>140.1. İntizam xarakterli hərbi hissədə saxlama n&ouml;v&uuml;ndə cəzaya məhkum olunmuş hərbi qulluq&ccedil;ular qısa m&uuml;ddətli və uzun m&uuml;ddətli g&ouml;r&uuml;ş, telefonla danışmaq h&uuml;ququna malikdirlər.</p>\r\n<p>140.2. Məhkumlara qohumları və başqa şəxslərlə qısa m&uuml;ddətli g&ouml;r&uuml;şlər intizam xarakterli hərbi hissənin komandiri tərəfindən m&uuml;əyyən edilmiş g&uuml;nlərdə və saatlarda işdən və təlimdən sonrakı asudə vaxtda ayda iki dəfə d&ouml;rd saatadək m&uuml;ddətə verilir. G&ouml;r&uuml;şlər x&uuml;susi təchiz olunmuş otaqda hərbi hissənin n&uuml;mayəndəsinin iştirakı ilə verilir.</p>\r\n<p>140.3. Məhkumlara yaxın qohumları ilə uzun m&uuml;ddətli g&ouml;r&uuml;şlər ildə d&ouml;rd dəfə &uuml;&ccedil; g&uuml;nədək m&uuml;ddətə birgə yaşamaq h&uuml;ququ ilə intizam xarakterli hərbi hissənin x&uuml;susi təchiz olunmuş otaqlarında və ya hərbi hissə komandirinin razılığına əsasən, hərbi hissədən kənarda verilə bilər.</p>\r\n<p>140.4. Uzun m&uuml;ddətli g&ouml;r&uuml;ş zamanı məhkumlar işdən və təlimdən azad edilirlər.</p>\r\n<p>140.5. İntizam xarakterli hərbi hissədə saxlanılan məhkumlar bu Məcəllənin 84.1-ci maddəsində nəzərdə tutulmuş qaydada ildə on iki dəfə telefonla danışmaq h&uuml;ququna malikdirlər. Məhkumun xahişi ilə uzun m&uuml;ddətli və ya qısa m&uuml;ddətli g&ouml;r&uuml;ş telefon danışığı ilə əvəz edilə bilər.</p>\r\n<p>140.6. H&uuml;quqi yardım almaq &uuml;&ccedil;&uuml;n məhkumların &ouml;zlərinin, yaxın qohumlarının və ya onların qanuni n&uuml;mayəndələrinin ərizəsinə əsasən məhkumlara vəkillərlə, həm&ccedil;inin belə yardımı g&ouml;stərmək h&uuml;ququna malik olan digər şəxslərlə g&ouml;r&uuml;ş verilir. Vəkillə g&ouml;r&uuml;şlər bu Məcəllənin 140.2 və 140.3-c&uuml; maddələrində nəzərdə tutulmuş g&ouml;r&uuml;şlərin sayına daxil edilmir. Belə g&ouml;r&uuml;şlər tərəflərin arzusu ilə təklikdə ke&ccedil;irilir.</p>\r\n<p><strong><em>Maddə 141. İntizam xarakterli hərbi hissədə məhkumların bağlama, sovqat, banderol və pul baratları alması</em></strong></p>\r\n<p>141.1. Məhkumlar ayda bir bağlama, sovqat və ya banderol və g&ouml;r&uuml;şlər zamanı əlavə olaraq sovqat almaq h&uuml;ququna malikdirlər.</p>\r\n<p>141.2. Məhkum bağlama, sovqat və ya banderolu intizam xarakterli hərbi hissənin n&uuml;mayəndəsinin iştirakı ilə a&ccedil;ır və i&ccedil;ərisində olan malları g&ouml;t&uuml;r&uuml;r.</p>\r\n<p>141.3. Bağlama, sovqat və ya banderollarda məhkuma qadağan olunan mallar aşkar edildikdə onlar g&ouml;t&uuml;r&uuml;l&uuml;r, məhkumun şəxsi əşyalarının siyahısına qeyd edilir və cəzanın icrası m&uuml;ddətinin sonuna qədər başqa şəxsi əşyalarla birgə saxlanılır və ya intizam xarakterli hərbi hissənin komandirinin əsaslandırılmış qərarına əsasən məhv edilir və bu barədə m&uuml;vafiq akt tərtib olunur.</p>\r\n<p>141.4. Məhkumun adına g&ouml;ndərilmiş pullar onun şəxsi hesabına k&ouml;&ccedil;&uuml;r&uuml;l&uuml;r və o həmin hesabdan ayda <em>doqquz manatadək</em> vəsaitlə ərzaq məhsulları və zəruri mallar əldə edə bilər. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i14#i14">14</a>)</p>\r\n<p><strong><em>Maddə 142. İntizam xarakterli hərbi hissədə məhkumların yazışması</em></strong></p>\r\n<p>142.1. Məhkumlar məhdudiyyətsiz sayda məktub, teleqram almaq və g&ouml;ndərmək h&uuml;ququna malikdirlər.</p>\r\n<p>142.2. Daxil olmuş məktubu intizam xarakterli hərbi hissənin n&uuml;mayəndəsi təqdim edir. Məhkum məktubu n&uuml;mayəndənin iştirakı ilə a&ccedil;ır. Məktubda qadağan olunmuş əşyalar aşkar edildikdə g&ouml;t&uuml;r&uuml;l&uuml;r.</p>\r\n<p>142.3. Məhkumların məktub və teleqramları senzuradan ke&ccedil;irilmir.</p>\r\n<p><strong><em>Maddə 143. Məhkumların intizam xarakterli hərbi hissədən kənara qısa m&uuml;ddətli səfəri</em></strong></p>\r\n<p>143.1. İntizam xarakterli hərbi hissədə saxlanılan məhkumlara bu Məcəllənin 89.1-ci maddəsində nəzərdə tutulmuş qaydada hərbi hissənin h&uuml;dudlarından kənara qısa m&uuml;ddətli səfərə getməyə icazə verilə bilər. Məhkumun qısa m&uuml;ddətli səfər ilə əlaqədar intizam xarakterli hərbi hissədən kənarda olması vaxtı cəzanın icrası m&uuml;ddətinə hesablanır.</p>\r\n<p>143.2. Hərbi qulluq&ccedil;ular &uuml;&ccedil;&uuml;n nəzərdə tutulan məzuniyyətlər intizam xarakterli hərbi hissədə saxlanılan məhkumlara şamil edilmir.</p>\r\n<p><strong><em>Maddə 144. İntizam xarakterli hərbi hissədə məhkumların əməyi</em></strong></p>\r\n<p>144.1. Məhkumlar intizam xarakterli hərbi hissənin obyektlərində və ya <a title="Azərbaycan Respublikasının M&uuml;dafiə Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanı</a> tərəfindən m&uuml;əyyən edilmiş digər obyektlərdə əməyə cəlb oluna bilərlər.</p>\r\n<p>144.2. Məhkumların əməyi qanunvericilik ilə m&uuml;əyyən olunmuş qaydalara riayət edilməklə təşkil olunur.</p>\r\n<p>144.3. Məhkumlar tərəfindən yerinə yetirilən işlərə q&uuml;vvədə olan qanunvericiliyə uyğun olaraq m&uuml;əyyən edilmiş norma və qiymətlər şamil edilir. Məhkumlara əmək haqqı verilmir.</p>\r\n<p>144.4. Məhkumların əməyinə g&ouml;rə işəg&ouml;t&uuml;rən tərəfindən k&ouml;&ccedil;&uuml;r&uuml;lm&uuml;ş məbləğin yarısı intizam xarakterli hərbi hissənin abadlaşdırılması, onun m&uuml;stəqil istehsalat bazasının yaradılması və ya inkişaf etdirilməsi, məhkumların maddi-məişət ehtiyaclarının &ouml;dənilməsi və maddi m&uuml;kafatlandırma fondunun təşkili &uuml;&ccedil;&uuml;n həmin hərbi hissənin hesabına, digər hissəsi isə d&ouml;vlət nəfinə ke&ccedil;irilir.</p>\r\n<p>144.5. Məhkumlar intizam xarakterli hərbi hissənin ərazisinin abadlaşdırılması, məhkumların mədəni-məişət şəraitinin yaxşılaşdırılması və onların ərzaqla təmin olunması &uuml;&ccedil;&uuml;n əlavə işlərə n&ouml;vbə ilə cəlb oluna bilərlər.</p>\r\n<p><strong><em>Maddə 145. İntizam xarakterli hərbi hissədə məhkumların d&ouml;y&uuml;ş hazırlığı təlimi</em></strong></p>\r\n<p>Məhkumların d&ouml;y&uuml;ş hazırlığı təlimi <a title="Azərbaycan Respublikasının M&uuml;dafiə Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanı</a> tərəfindən hazırlanmış x&uuml;susi proqram əsasında təşkil edilir və intizam xarakterli hərbi hissənin komandiri tərəfindən həyata ke&ccedil;irilir. Məhkumların d&ouml;y&uuml;ş hazırlığı təliminin təşkili &uuml;&ccedil;&uuml;n zəruri maddi-texniki baza yaradılır.</p>\r\n<p><strong><em>Maddə 146. İntizam xarakterli hərbi hissədə məhkumlarla tərbiyə işi</em></strong></p>\r\n<p>146.1. Məhkumlarla tərbiyə işini intizam xarakterli hərbi hissənin komandiri təşkil edir və həyata ke&ccedil;irir.</p>\r\n<p>146.2. Məhkumların əvvəllər hərbi xidmət ke&ccedil;dikləri hərbi hissələrin komandirləri intizam xarakterli hərbi hissənin komandiri ilə daim əlaqə saxlamağa, tabe&ccedil;iliklərində olmuş hərbi qulluq&ccedil;uların davranışı ilə maraqlanmağa və onların islah edilməsinə k&ouml;məklik g&ouml;stərməlidirlər.</p>\r\n<p>146.3. Məhkumlarla tərbiyə işinin aparılmasında <a title="Azərbaycan Respublikasının M&uuml;dafiə Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanı</a> intizam xarakterli hərbi hissənin komandirinə k&ouml;mək edir.</p>\r\n<p><strong><em>Maddə 147. İntizam xarakterli hərbi hissədə məhkumların ictimai şuraları</em></strong></p>\r\n<p>147.1. Məhkumların islah edilməsi işində intizam xarakterli hərbi hissənin komandirinə k&ouml;mək etmək məqsədi ilə n&uuml;munəvi davranışı, əməyə və hərbi xidmətə vicdanlı m&uuml;nasibəti ilə fərqlənən şəxslərdən məhkumların ictimai şurası yaradılır.</p>\r\n<p>147.2. İctimai şuranın &uuml;zvləri məhkumların &uuml;mumi yığıncaqlarında se&ccedil;ilir və ictimai şuranın tərkibi intizam xarakterli hərbi hissənin komandiri tərəfindən təsdiq olunur. Həmin şuranın fəaliyyətinə nəzarət hərbi hissənin komandiri tərəfindən həyata ke&ccedil;irilir.</p>\r\n<p>147.3. İctimai şura hərbi hissə komandirinə tərbiyə tədbirlərinin və məhkumların asudə vaxtının səmərəli ke&ccedil;məsinə k&ouml;məklik edir, məhkumlarla fərdi izahat işi aparır.</p>\r\n<p><strong><em>Maddə 148. İntizam xarakterli hərbi hissədə məhkumlara tətbiq edilən həvəsləndirmə tədbirləri</em></strong></p>\r\n<p>148.0. Məhkumlara n&uuml;munəvi davranışa, əməyə və hərbi xidmətə vicdanla yaşamağa, ictimai şuranın işində fəal iştiraka g&ouml;rə aşağıdakı həvəsləndirmə tədbirləri tətbiq edilir:</p>\r\n<p>148.0.1. təşəkk&uuml;r elan etmək;</p>\r\n<p>148.0.2. hədiyyə və ya <em>d&ouml;rd manatadək</em> miqdarda pulla m&uuml;kafatlandırmaq;</p>\r\n<p>148.0.3. qısa m&uuml;ddətli və ya uzun m&uuml;ddətli əlavə g&ouml;r&uuml;ş vermək;</p>\r\n<p>148.0.4. verilmiş tənbehi vaxtından əvvəl g&ouml;t&uuml;rmək. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i14#i14">14</a>)</p>\r\n<p><strong><em>Maddə 149. İntizam xarakterli hərbi hissədə məhkumlara tətbiq edilən tənbeh tədbirləri</em></strong></p>\r\n<p>149.1. Cəzanın icrası qaydalarını pozan məhkumlara aşağıdakı tənbeh tədbirləri tətbiq edilir:</p>\r\n<p>149.1.1. məzəmmət;</p>\r\n<p>149.1.2. t&ouml;hmət;</p>\r\n<p>149.1.3. şiddətli t&ouml;hmət;</p>\r\n<p>149.1.4. on g&uuml;nədək m&uuml;ddətə hauptvaxtda saxlamaq.</p>\r\n<p>149.2. Məhkumlar intizam xarakterli hərbi hissənin hauptvaxtında birnəfərlik kameralarda saxlanılırlar.</p>\r\n<p><strong><em>Maddə 150. İntizam xarakterli hərbi hissədə məhkumlara həvəsləndirmə və tənbeh tədbirlərinin tətbiqi qaydası</em></strong></p>\r\n<p>Həvəsləndirmə və tənbeh tədbirlərinin verilməsi, onların tətbiqi qaydası, həm&ccedil;inin qeydiyyatı &uuml;zrə komandirlərin (rəislərin) h&uuml;quqları Azərbaycan Respublikası Silahlı Q&uuml;vvələrinin İntizam Nizamnaməsinin tələblərinə uyğun m&uuml;əyyən edilir.</p>\r\n<p><strong><em>Maddə 151. İntizam xarakterli hərbi hissədə məhkumların maddi-məişət və tibbi təminatı</em></strong></p>\r\n<p>151.1. Məhkumlar &uuml;&ccedil;&uuml;n Azərbaycan Respublikası Silahlı Q&uuml;vvələrinin Daxili Xidmət Nizamnaməsinin tələblərinə uyğun olaraq zəruri maddi-məişət şəraiti yaradılır.</p>\r\n<p>151.2. Məhkumlar <a title="Azərbaycan Respublikasının M&uuml;dafiə Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanı</a> tərəfindən təsdiq olunmuş normalar &uuml;zrə ərzaq, paltar və digər ləvazimatla təmin olunurlar.</p>\r\n<p>151.3. Məhkumların tibbi təminatı Azərbaycan Respublikası Silahlı Q&uuml;vvələrinin Daxili Xidmət Nizamnaməsinin tələblərinə uyğun həyata ke&ccedil;irilir.</p>\r\n<p>151.4. Stasionar şəraitdə m&uuml;alicəyə ehtiyacı olan məhkumlar m&uuml;hafizə altında hospitala g&ouml;ndərilir və orada x&uuml;susi təchiz olunmuş otaqlarda saxlanılırlar.</p>\r\n<p>151.5. Məhkumlara verilən pul təminatı hər ay onların şəxsi hesablarına ke&ccedil;irilir.</p>\r\n<p>151.6. Məhkumlara saxlamağa, nağdsız hesabla əldə etməyə və ya bağlamada, sovqatda, banderolda almağa icazə verilən ərzaq məhsullarının və ən zəruri malların siyahısı, habelə məhkumlara saxlanılması qadağan edilən əşyaların siyahısı intizam xarakterli hərbi hissənin daxili intizam qaydaları ilə m&uuml;əyyən edilir.</p>\r\n<p><strong><em>Maddə 152. İntizam xarakterli hərbi hissədən hərbi qulluğa yararsız hesab edilmiş məhkumların cəzadan şərti olaraq vaxtından əvvəl azad edilməsi</em></strong></p>\r\n<p>İntizam xarakterli hərbi hissədə saxlama n&ouml;v&uuml;ndə cəza &ccedil;əkən məhkumlar hərbi xidmətə yararsız hesab edilməsinə səbəb olan xəstəliklərə d&uuml;&ccedil;ar olduqda, məhkumlar və onların qanuni n&uuml;mayəndəsi intizam xarakterli hərbi hissədən şərti olaraq vaxtından əvvəl azad edilməsi və ya cəzanın &ccedil;əkilməmiş hissəsinin daha y&uuml;ng&uuml;l cəza n&ouml;v&uuml; ilə əvəz edilməsi barədə ərizə ilə məhkəməyə m&uuml;raciət edə bilərlər.</p>\r\n<p><strong><em>Maddə 153. Hərbi qulluq&ccedil;uların intizam xarakterli hərbi hissədə cəza &ccedil;əkdikləri vaxtın m&uuml;ddətli həqiqi hərbi xidmət m&uuml;ddətinə hesablanması</em></strong></p>\r\n<p>153.1. Məhkumun intizam xarakterli hərbi hissədə cəza &ccedil;əkdiyi vaxt m&uuml;ddətli həqiqi hərbi xidmət m&uuml;ddətinə hesablanmır.</p>\r\n<p>153.2. İntizam xarakterli hərbi hissədə saxlama n&ouml;v&uuml;ndə cəzadan azad edilən vaxt həqiqi hərbi xidmətdə olmanın qanunla m&uuml;əyyən olunmuş son yaş həddinə &ccedil;atmış şəxslər həqiqi hərbi xidmətdən tərxis edilirlər.</p>\r\n<p><strong><em>V</em></strong><strong><em>b&ouml;lmə. Əlavə cəzaların icrası</em></strong></p>\r\n<p><strong>XVIII fəsil. Nəqliyyat vasitəsini idarəetmə h&uuml;ququndan məhrum etmə, x&uuml;susi və ya hərbi r&uuml;tbədən, fəxri addan və d&ouml;vlət təltifindən məhrum etmə, əmlak m&uuml;sadirəsi və Azərbaycan Respublikasının h&uuml;dudlarından kənara məcburi &ccedil;ıxarma n&ouml;v&uuml;ndə cəzaların icrası</strong></p>\r\n<p><strong><em>Maddə 154. Nəqliyyat vasitəsini idarə etmə h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəzanın icrası qaydası</em></strong></p>\r\n<p>154.1. Nəqliyyat vasitəsini idarəetmə h&uuml;ququndan məhrum etmə barədə h&ouml;km qanuni q&uuml;vvəyə mindikdən sonra və ya bu Məcəllənin 154.3-c&uuml; maddəsində nəzərdə tutulmuş hallarda icra məmuru onun surətini və nəqliyyat vasitəsini idarəetmə h&uuml;ququ verən sənədi məhkuma bu h&uuml;ququ verən orqana g&ouml;ndərir.</p>\r\n<p>154.2. M&uuml;vafiq orqan qanunvericiliklə m&uuml;əyyən edilmiş qaydada məhkumun nəqliyyat vasitəsini idarəetmə h&uuml;ququndan h&ouml;kmlə m&uuml;əyyən olunmuş m&uuml;ddətə məhrum edilməsi ilə əlaqədar nəqliyyat vasitəsini idarəetmə h&uuml;ququnu verən sənədin saxlanılması &uuml;&ccedil;&uuml;n tədbirlər g&ouml;r&uuml;r.</p>\r\n<p>154.3. Nəqliyyat vasitəsini idarəetmə h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəza intizam xarakterli hərbi hissədə saxlama və ya m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzalara əlavə olaraq təyin edildikdə, cəzanı icra edən m&uuml;əssisə və ya orqan əsas cəza m&uuml;ddəti qurtardıqdan sonra, yaxud məhkum şərti olaraq vaxtından əvvəl azad edilərsə və ya cəzasının &ccedil;əkilməmiş hissəsi daha y&uuml;ng&uuml;l cəza ilə əvəz edilərsə h&ouml;km&uuml;n surətini və nəqliyyat vasitəsini idarəetmə h&uuml;ququ verən sənədi məhkumun yaşadığı yerin icra məmuruna g&ouml;ndərir. (22)</p>\r\n<p>154.4. Nəqliyyat vasitəsini idarəetmə h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəzanı icra edən m&uuml;əssisə və ya orqan Azərbaycan Respublikasının tərəfdar &ccedil;ıxdığı beynəlxalq m&uuml;qavilələrə uyğun olaraq bu cəzaya məhkum olunmuş əcnəbi və ya vətəndaşlığı olmayan şəxs barəsində məhkəmənin qanuni q&uuml;vvəyə minmiş h&ouml;km&uuml;n&uuml;n surətini və nəqliyyat vasitəsini idarəetmə h&uuml;ququ verən sənədi məhkumun mənsub olduğu və ya daimi yaşadığı d&ouml;vlətin Azərbaycan Respublikasındakı diplomatik və konsulluq n&uuml;mayəndəliklərinə və ya həmin d&ouml;vlətə g&ouml;ndərməklə dərhal məlumat verir.</p>\r\n<p><strong><em>Maddə 155. Nəqliyyat vasitəsini idarəetmə h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəzanın icrası zamanı işəg&ouml;t&uuml;rənin vəzifələri</em></strong></p>\r\n<p>155.1. Nəqliyyat vasitəsini idarəetmə h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum edilmiş şəxsin işlədiyi yerin işəg&ouml;t&uuml;rəni məhkəmənin h&ouml;km&uuml;ndə g&ouml;stərilən tələblərə riayət etməlidir.</p>\r\n<p>155.2. Cəzanı icra edən m&uuml;əssisə və ya orqan intizam xarakterli hərbi hissədə saxlama və ya m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzalardan əlavə nəqliyyat vasitəsini idarəetmə h&uuml;ququndan məhrum etməyə məhkum olunmuş şəxsləri nəqliyyat vasitəsini idarəetmə ilə əlaqədar işlərdə istifadə edə bilməz.(22)</p>\r\n<p><strong><em>Maddə 156. Nəqliyyat vasitəsini idarəetmə h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəzanın icrası zamanı icra məmurunın vəzifələri</em></strong></p>\r\n<p>156.0. Nəqliyyat vasitəsini idarəetmə h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxsin yaşadığı yerin icra məmuru;</p>\r\n<p>156.0.1. məhkumun qeydiyyatını aparır;</p>\r\n<p>156.0.2. məhkumun məhkəmənin h&ouml;km&uuml;ndə g&ouml;stərilən tələblərə riayət etməsinə nəzarət edir;</p>\r\n<p>156.0.3. h&ouml;kmdə g&ouml;stərilən tələblərin məhkumun işlədiyi yerin işəg&ouml;t&uuml;rəni tərəfindən yerinə yetirilməsini yoxlayır.</p>\r\n<p><strong><em>Maddə 157. Nəqliyyat vasitəsini idarəetmə h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəzanın icrası m&uuml;ddətinin hesablanması</em></strong></p>\r\n<p>157.1. Nəqliyyat vasitəsini idarəetmə h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəza ictimai işlər, islah işləri n&ouml;v&uuml;ndə cəzalara əlavə olaraq təyin edildikdə, habelə şərti məhkum etmə zamanı əlavə cəzanın m&uuml;ddəti h&ouml;km&uuml;n qanuni q&uuml;vvəyə mindiyi vaxtdan hesablanır. (22)</p>\r\n<p>157.2. Nəqliyyat vasitəsini idarəetmə h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəza intizam xarakterli hərbi hissədə saxlama və ya m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzalara əlavə olaraq təyin edildiyi hallarda əlavə cəza əsas cəzanın &ccedil;əkildiyi b&uuml;t&uuml;n m&uuml;ddətə və bundan əlavə h&ouml;kmdə bu cəza n&ouml;v&uuml; &uuml;&ccedil;&uuml;n m&uuml;əyyən edilmiş m&uuml;ddətə şamil olunur.</p>\r\n<p><strong><em>Maddə 158. Nəqliyyat vasitəsini idarəetmə h&uuml;ququndan məhrum etmə barədə h&ouml;km&uuml;n icra edilməməsinin nəticələri</em></strong></p>\r\n<p>158.1. Nəqliyyat vasitəsini idarəetmə h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxs məhkəmənin h&ouml;km&uuml; ilə qadağan edilmiş nəqliyyat vasitəsini idarəetmə ilə əlaqədar işə girdikdə işəg&ouml;t&uuml;rən məhkumla əmək qanunvericiliyinə uyğun olaraq əmək m&uuml;qaviləsinə xitam verir.</p>\r\n<p>158.2. Nəqliyyat vasitəsini idarəetmə h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxs məhkəmənin h&ouml;km&uuml; ilə qadağan edilmiş nəqliyyat vasitəsini idarəetmə ilə əlaqədar əmək fəaliyyətilə məşğul olduqda, icra məmuru işəg&ouml;t&uuml;rənə məlumat verərək məhkumun h&ouml;kmlə qadağan edilmiş m&uuml;vafiq əmək fəaliyyətinə &uuml;&ccedil; g&uuml;n m&uuml;ddətinə xitam verilməsini təklif edir və onu h&ouml;km&uuml;n icra edilməməsinin məsuliyyətə səbəb olması barədə xəbərdar edir.</p>\r\n<p>158.3. Nəqliyyat vasitəsini idarəetmə h&uuml;ququndan məhrum etmə barədə məhkəmə h&ouml;km&uuml;n&uuml; qərəzli olaraq icra etməyən məhkum və ya vəzifəli şəxs Azərbaycan Respublikasının qanunvericiliyi ilə m&uuml;əyyən edilmiş məsuliyyəti daşıyır.</p>\r\n<p><strong><em>Maddə 159. X&uuml;susi və ya hərbi r&uuml;tbədən, fəxri addan və d&ouml;vlət təltifindən məhrum etmə n&ouml;v&uuml;ndə cəzanın icrası qaydası</em></strong></p>\r\n<p>159.1. X&uuml;susi və ya hərbi r&uuml;tbədən, fəxri addan və d&ouml;vlət təltifindən məhrum etmə barədə h&ouml;km qanuni q&uuml;vvəyə mindikdən sonra h&ouml;km&uuml;n surəti və m&uuml;vafiq təqdimat məhkəmə tərəfindən x&uuml;susi və ya hərbi r&uuml;tbəni, fəxri adı və d&ouml;vlət təltifini vermiş orqana g&ouml;ndərilir.</p>\r\n<p>159.2. M&uuml;vafiq orqan m&uuml;əyyən edilmiş qaydada məhkumun x&uuml;susi və ya hərbi r&uuml;tbədən, fəxri addan və d&ouml;vlət təltifindən məhrum edilməsi haqqında m&uuml;vafiq sənədlərə yazılı qeyd edir, habelə şəxsin x&uuml;susi və ya hərbi r&uuml;tbə, fəxri addan və d&ouml;vlət təltifi ilə əlaqədar h&uuml;quq və g&uuml;zəştlərdən məhrum edilməsinə dair tədbirlər g&ouml;r&uuml;r.</p>\r\n<p>159.3. Ehtiyatda olan hərbi qulluq&ccedil;uya dair h&ouml;km&uuml;n surəti onun qeydiyyatda olduğu hərbi komissarlığa g&ouml;ndərilir.</p>\r\n<p>159.4. H&ouml;km&uuml;n surətini və təqdimatı alan m&uuml;vafiq orqan bir ay ərzində h&ouml;km&uuml; &ccedil;ıxaran məhkəməyə onun icra edilməsi haqqında məlumat verir.</p>\r\n<p><strong><em>Maddə 160. Əmlak m&uuml;sadirəsi n&ouml;v&uuml;ndə cəzanın icrası qaydası</em></strong></p>\r\n<p>160.1. Əmlak m&uuml;sadirəsi barədə h&ouml;km&uuml; &ccedil;ıxarmış məhkəmə h&ouml;km qanuni q&uuml;vvəyə mindikdən sonra h&ouml;km&uuml;n surətini, h&ouml;km&uuml;n icrası barədə g&ouml;stərişi və əmlakın siyahısının surətini icra məmuruna g&ouml;ndərir və bu barədə m&uuml;vafiq maliyyə orqanına məlumat verir.</p>\r\n<p>160.2. Əmlak m&uuml;sadirəsi n&ouml;v&uuml;ndə cəzanın icrasını əmlakın olduğu yer &uuml;zrə icra məmuru həyata ke&ccedil;irir.</p>\r\n<p><strong><em>Maddə 161. M&uuml;sadirə edilən əmlak</em></strong></p>\r\n<p><em>161.1. Məhkəmənin h&ouml;km&uuml;ndə və əmlak siyahısında g&ouml;stərilən aşağıdakı əmlak m&uuml;sadirə edilir:</em></p>\r\n<p><em>161.1.1. cinayət t&ouml;rədilərkən istifadə edilmiş alət və vasitələr;</em></p>\r\n<p><em>161.1.2. cinayətin predmetləri;</em></p>\r\n<p><em>161.1.3. cinayət yolu ilə əldə edilmiş əmlak;</em></p>\r\n<p><em>161.1.4. cinayət yolu ilə əldə edilmiş əmlak və ya cinayətin predmeti istifadə edildiyi, &ouml;zgəninkiləşdirildiyi və ya başqa səbəblərdən d&ouml;vlət nəfinə alına bilinmədiyi halda həmin əmlakın dəyəri məbləğində məhkuma məxsus olan pul və ya digər əmlak.</em> (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i9#i9">9</a>)</p>\r\n<p>161.2. Məhkəmənin h&ouml;km&uuml; ilə m&uuml;sadirə edilməli əmlakın kimə mənsub olması haqqında m&uuml;bahisə m&uuml;lki məhkəmə icraatı qaydasında həll edilir.</p>\r\n<p><strong><em>Maddə 162. Əmlak m&uuml;sadirəsi n&ouml;v&uuml;ndə cəzanın icrası zamanı icra məmurunın vəzifələri</em></strong></p>\r\n<p>162.1. Icra məmuru məhkəmə h&ouml;km&uuml;n&uuml;n surətini, h&ouml;km&uuml;n icrası barədə g&ouml;stərişi aldıqdan sonra dərhal əmlak siyahısında g&ouml;stərilən əmlakın m&ouml;vcudluğunu yoxlayır.</p>\r\n<p>162.2. M&uuml;sadirə edilən əmlak plomblanır, yaxud m&ouml;h&uuml;rlənir, zəruri hallarda isə bu barədə siyahıda qeydlər edilir.</p>\r\n<p>162.3. M&uuml;sadirə edilməli əmlakın saxlanılması &uuml;&ccedil;&uuml;n icra məmuru zəruri tədbirlər g&ouml;r&uuml;r.</p>\r\n<p><strong><em>Maddə 163. M&uuml;sadirə edilməli əmlakla əlaqədar digər şəxslərin vəzifələri</em></strong></p>\r\n<p>163.1. M&uuml;sadirə edilməli əmlak m&uuml;əssisə, idarə, təşkilat və ya vətəndaşlarda olarsa, onlar bu barədə məhkəməyə və ya m&uuml;vafiq maliyyə orqanına xəbər verməlidirlər.</p>\r\n<p>163.2. M&uuml;əssisə, idarə, təşkilat və ya vətəndaşlar onlara saxlanmaq &uuml;&ccedil;&uuml;n verilən m&uuml;sadirə edilən əmlakın qorunub saxlanmasını təmin etməlidirlər.</p>\r\n<p>163.3. M&uuml;sadirə edilən əmlakın gizlədilməsinə, israf edilməsinə, &ouml;zgəninkiləşdirilməsinə və ya dəyişdirilməsinə g&ouml;rə təqsirli şəxslər qanunvericiliyə uyğun məsuliyyətə cəlb olunurlar.</p>\r\n<p><strong><em>Maddə 164. M&uuml;sadirə edilmiş əmlakın maliyyə orqanlarına verilməsi</em></strong></p>\r\n<p>164.1. Məhkumun m&uuml;sadirə edilmiş əmlakı bu əmlak barəsində irəli s&uuml;r&uuml;lən b&uuml;t&uuml;n tələblər qanunvericilikdə m&uuml;əyyən edilmiş qaydada təmin edildikdən sonra maliyyə orqanlarına verilir.</p>\r\n<p>164.2. M&uuml;sadirə edilmiş əmlakın d&ouml;vlət nəfinə verilmə qaydası <a title="Azərbaycan Respublikasının Nazirlər Kabineti / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanı</a> tərəfindən m&uuml;əyyən edilir.</p>\r\n<p><strong><em>Maddə 165. H&ouml;km&uuml;n icrasından sonra aşkar edilmiş əmlakın m&uuml;sadirəsi</em></strong></p>\r\n<p>H&ouml;km&uuml;n əmlakın m&uuml;sadirə edilməsinə dair hissəsi icra edildikdən sonra, məhkumun t&ouml;rətdiyi cinayət ilə əlaqədar əldə edilmiş əmlak aşkar edildiyi hallarda, icra məmuru h&ouml;km&uuml; &ccedil;ıxaran məhkəməyə və ya h&ouml;km&uuml;n icra edildiyi yerin məhkəməsinə təqdimat verir.</p>\r\n<p><strong><em>Maddə 166. Azərbaycan Respublikasının h&uuml;dudlarından kənara məcburi &ccedil;ıxarma n&ouml;v&uuml;ndə cəzanın icrası qaydası</em></strong></p>\r\n<p>166.1. Azərbaycan Respublikasının h&uuml;dudlarından kənara məcburi &ccedil;ıxarma Azərbaycan Respublikasının qanunvericiliyində nəzərdə tutulmuş hallarda tətbiq edilir.</p>\r\n<p>166.2. Azərbaycan Respublikasının h&uuml;dudlarından kənara məcburi &ccedil;ıxarma n&ouml;v&uuml;ndə cəzanı icra etmək &uuml;&ccedil;&uuml;n məhkum olunmuş şəxs haqqında əsas cəzanı icra edən m&uuml;əssisə və ya orqan məhkəmə h&ouml;km&uuml; ilə m&uuml;əyyən olunmuş m&uuml;ddətin başa &ccedil;atmasına bir ya qalmış <a title="Azərbaycan Respublikasının Xarici İşlər Nazirliyi / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanına</a> məlumat verir.</p>\r\n<p>166.3. Azərbaycan Respublikasının h&uuml;dudlarından kənara məcburi &ccedil;ıxarma n&ouml;v&uuml;ndə cəzanı icra edən orqan məhkumun Azərbaycan Respublikasının h&uuml;dudlarından kənara məcburi &ccedil;ıxarılması barədə m&uuml;vafiq icra hakimiyyəti orqanına və məhkumun mənsub olduğu, habelə məhkuma himayədarlığı &uuml;zərinə g&ouml;t&uuml;rm&uuml;ş d&ouml;vlətin diplomatik və konsulluq n&uuml;mayəndəliklərinə məlumat verir.</p>\r\n<p>166.4. Azərbaycan Respublikasının h&uuml;dudlarından kənara məcburi &ccedil;ıxarma n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxs, əsas cəzanı &ccedil;əkməkdən azad olunduğu g&uuml;ndən, &uuml;z&uuml;rl&uuml; səbəblər istisna olmaqla, Azərbaycan Respublikasının h&uuml;dudlarını dərhal tərk etməlidir.</p>\r\n<p>166.5. Azərbaycan Respublikasının h&uuml;dudlarından kənara &ccedil;ıxarma xərclərini məhkumun &ouml;z&uuml; və ya onun mənsub olduğu d&ouml;vlətin diplomatik və konsulluq n&uuml;mayəndəlikləri &ouml;dəyirlər.</p>\r\n<p>166.6. M&uuml;stəsna hallarda məhkumun Azərbaycan Respublikasının h&uuml;dudlarından kənara &ccedil;ıxarma xərclərini cəzanı icra edən orqan &ouml;dəyə bilər.</p>\r\n<p><strong><em>VI b&ouml;lmə. Cəza &ccedil;əkməkdən azad etmə. Cəza &ccedil;əkməkdən azad edilən şəxslərə yardım g&ouml;stərilməsi və onlara nəzarət edilməsi</em></strong></p>\r\n<p><strong>XIX fəsil. Cəza &ccedil;əkməkdən azad etmə</strong></p>\r\n<p><strong><em>Maddə 167. Cəza &ccedil;əkməkdən azad etmənin əsasları</em></strong></p>\r\n<p>167.0. Məhkumlar cəza &ccedil;əkməkdən aşağıdakı əsaslarla azad edilirlər:</p>\r\n<p>167.0.1. cəza m&uuml;ddəti qurtardıqda;</p>\r\n<p>167.0.2. h&ouml;km&uuml;n ləğv edilməsi ilə əlaqədar işin icraatına xitam verildikdə;</p>\r\n<p>167.0.3. cəzadan şərti olaraq vaxtından əvvəl azad edildikdə;</p>\r\n<p>167.0.4.cəzanın &ccedil;əkilməmiş hissəsi daha y&uuml;ng&uuml;l cəza n&ouml;v&uuml; ilə əvəz edildikdə;</p>\r\n<p>167.0.5. amnistiya və ya əfvetmə aktına əsasən;</p>\r\n<p>167.0.6. xəstəliyə və qanunvericiliklə nəzərdə tutulmuş digər əsaslara g&ouml;rə.</p>\r\n<p><strong><em>Maddə 168. Cəza m&uuml;ddətinin qurtarması ilə əlaqədar cəza &ccedil;əkməkdən azad etmə qaydaları</em></strong></p>\r\n<p>168.1. M&uuml;əyyən vəzifə tutma və ya m&uuml;əyyən fəaliyyətlə məşğul olma h&uuml;ququndan məhrum etmə, ictimai işlər, islah işləri, m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə, həm&ccedil;inin hərbi xidmət &uuml;zrə məhdudlaşdırma və intizam xarakterli hərbi hissədə saxlama n&ouml;v&uuml;ndə cəzaların m&uuml;ddəti cəza &ccedil;əkmənin axırıncı g&uuml;n&uuml; bitir.(22)</p>\r\n<p>168.2. <em>M&uuml;əyyən</em> m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslər cəzanın icrası yerindən cəza m&uuml;ddətinin axırıncı g&uuml;n&uuml;n&uuml;n birinci yarısında azad edilirlər. Əgər cəza m&uuml;ddəti <em>istirahət, səsvermə, iş g&uuml;n&uuml; hesab edilməyən bayram g&uuml;nləri və &uuml;mumxalq h&uuml;zn g&uuml;n&uuml; qurtarırsa, məhkum həmin g&uuml;nlərdən</em> əvvəlki g&uuml;n azad edilir. Cəza m&uuml;ddəti aylarla hesablanarsa, bu m&uuml;ddət axırıncı ayın m&uuml;vafiq g&uuml;n&uuml;ndə, əgər bu ayda m&uuml;vafiq g&uuml;n yoxdursa, həmin ayın axırıncı g&uuml;n&uuml; qurtarır. (22)</p>\r\n<p>168.3. Cəza &ccedil;əkməkdən azad edilərkən məhkuma ona məxsus olan mallar və qiymətli əşyalar, şəxsi hesabında saxlanılan pul və şəxsi sənədlər, şəxsiyyət vəsiqəsi, əmək kitab&ccedil;ası, pensiya vəsiqəsi, həm&ccedil;inin cəzadan azad olunması və əmək fəaliyyəti barəsində sənədlər verilir.</p>\r\n<p>168.4. <em>M&uuml;əyyən </em>m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəza &ccedil;əkməkdən azad olunan məhkumun şəxsiyyət vəsiqəsi, əmək kitab&ccedil;ası və pensiya vəsiqəsi məhkumun şəxsi işində olmadıqda, cəzanı icra edən m&uuml;əssisənin m&uuml;diriyyəti əvvəlcədən onların alınması &uuml;&ccedil;&uuml;n tədbirlər g&ouml;r&uuml;r.(22)</p>\r\n<p>168.5. Məhkumun cəzadan şərti olaraq vaxtından əvvəl azad edilməsi m&uuml;vafiq sənəd məhkəmədən daxil olduğu g&uuml;n, sənəd iş g&uuml;n&uuml; qurtardıqdan sonra alındıqda isə, n&ouml;vbəti g&uuml;n səhər həyata ke&ccedil;irilir.</p>\r\n<p>168.6. Icra məmuru islah işləri n&ouml;v&uuml;ndə cəza m&uuml;ddətinin qurtardığı g&uuml;n, məhkum həmin cəzadan başqa əsasla azad edildikdə isə m&uuml;vafiq sənədi aldıqdan sonra, n&ouml;vbəti g&uuml;ndən gec olmayaraq, məhkumun işlədiyi yerin işəg&ouml;t&uuml;rəninə onun əmək haqqından tutulmaların dayandırılması barəsində məlumat verməlidir.</p>\r\n<p>168.7. H&ouml;km&uuml;n ləğv olunması ilə əlaqədar azad edilən şəxsə, cəzanı icra edən m&uuml;əssisə və ya orqan tərəfindən əmlak, əmək, yaşayış və digər itirilmiş h&uuml;quqların bərpası qaydası izah edilir. Bu halda azad etmə barədə sənəddə məhkumdan d&ouml;vlət adından rəsmi &uuml;zr istənilir.</p>\r\n<p><strong><em>Maddə 169. Hərbi qulluq&ccedil;uların cəza &ccedil;əkməkdən azad edilməsi</em></strong></p>\r\n<p>Hərbi xidmət &uuml;zrə məhdudlaşdırma, intizam xarakterli hərbi hissədə saxlama n&ouml;v&uuml;ndə cəzaya məhkum olunmuş hərbi qulluq&ccedil;ular xəstəliyə g&ouml;rə hərbi xidmətə yararsız sayıldıqları hallarda cəzanın &ccedil;əkilməmiş hissəsini &ccedil;əkməkdən azad edilirlər. Cəzanın &ccedil;əkilməmiş hissəsi məhkəmə tərəfindən daha y&uuml;ng&uuml;l cəza n&ouml;v&uuml; ilə əvəz edilə bilər.</p>\r\n<p><strong><em>Maddə 170. Məhkumun cəzadan vaxtından əvvəl azad edilməsi və ya cəzanın &ccedil;əkilməmiş hissəsinin daha y&uuml;ng&uuml;l cəza n&ouml;v&uuml; ilə əvəz edilməsi, habelə cəzanın &ccedil;əkilməsinin təxirə salınması qaydaları</em></strong></p>\r\n<p>170.1. Cəzadan şərti olaraq vaxtından əvvəl azad etmə və ya cəzanın &ccedil;əkilməmiş hissəsini daha y&uuml;ng&uuml;l cəza n&ouml;v&uuml; ilə əvəz etmə, habelə cəzanın &ccedil;əkilməsinin təxirə salınması tətbiq oluna bilən məhkum Azərbaycan Respublikasının Cinayət-Prosessual Qanunvericiliyi ilə m&uuml;əyyən edilmiş qaydada məhkəməyə ərizə ilə m&uuml;raciət edə bilər.</p>\r\n<p>170.2. Cəzadan şərti olaraq vaxtından əvvəl azad etmə və ya cəzanın &ccedil;əkilməmiş hissəsini daha y&uuml;ng&uuml;l cəza n&ouml;v&uuml; ilə əvəz etmə zamanı məhkumun cəza&ccedil;əkmə m&uuml;ddətində davranışı, əməyə, təhsilə və t&ouml;rətdiyi cinayətə m&uuml;nasibəti barədə cəzanı icra edən m&uuml;əssisə və ya orqanın m&uuml;lahizəsi nəzərə alınır. M&uuml;lahizə ilə birlikdə məhkəməyə məhkumun şəxsi işi də g&ouml;ndərilir.</p>\r\n<p>170.3. Amnistiyanın tətbiqi qaydaları amnistiya aktını qəbul etmiş orqan tərəfindən m&uuml;əyyən edilir.</p>\r\n<p>170.4. Əfv etməyə təqdim olunan məhkum barəsində cəzanı icra edən m&uuml;əssisə və ya orqan m&uuml;vafiq vəsatət verir.</p>\r\n<p>170.5. Psixi və sair ağır xəstəliklə əlaqədar cəza &ccedil;əkməkdən azad etmə barədə məhkəməyə məhkumun &ouml;z&uuml; və ya onun qanuni n&uuml;mayəndəsi ərizə, cəzanı icra edən m&uuml;əssisə və ya orqan isə təqdimat verir. Təqdimatla birlikdə məhkəməyə tibbi-sosial komissiyasının rəyi və məhkumun şəxsi ili g&ouml;ndərilir. Təqdimatda məhkumun b&uuml;t&uuml;n cəza&ccedil;əkmə m&uuml;ddətində davranışı barədə məlumat olmalıdır.</p>\r\n<p>170.6. İctimai işlər <em>və ya islah işləri</em> n&ouml;v&uuml;ndə cəza &ccedil;əkən məhkum qadın onun hamiləlik və ya doğuşa g&ouml;rə işdən azad olunduğu andan cəzanın &ccedil;əkilməsinin təxirə salınması barədə ərizə ilə məhkəməyə m&uuml;raciət edə bilər. (22)</p>\r\n<p>170.7. Məhkumun cəzadan şərti olaraq vaxtından əvvəl azad edilməsi və ya cəzanın &ccedil;əkilməmiş hissəsinin daha y&uuml;ng&uuml;l cəza n&ouml;v&uuml; ilə əvəz edilməsi, habelə cəzanın &ccedil;əkilməsinin təxirə salınması məhkəmə tərəfindən rədd edildiyi hallarda, təkrar ərizəyə məhkəmənin rədd haqqında qərar qəbul etdiyi g&uuml;ndən ən azı altı ay sonra baxıla bilər.</p>\r\n<p><strong><em>Maddə 171. &Ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxslərin cəzadan şərti olaraq vaxtından əvvəl azad edilməsi</em></strong></p>\r\n<p>171.1. &Ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş şəxs <a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4056">Azərbaycan Respublikasının Cinayət Məcəlləsi</a> ilə m&uuml;əyyən olunmuş m&uuml;ddət ke&ccedil;dikdən sonra cəzadan şərti olaraq vaxtından əvvəl azad edilmə haqqında məhkəməyə ərizə ilə m&uuml;raciət edə bilər.</p>\r\n<p>171.2. &Ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəza &ccedil;əkən məhkumun ərizəsinə əsasən onun cəzadan şərti olaraq vaxtından əvvəl azad edilməsi məhkəmə tərəfindən rədd edildikdə, təkrar ərizəyə məhkəmənin rədd haqqında qərarının qəbul edildiyi g&uuml;ndən ən azı altı ay sonra baxıla bilər.</p>\r\n<p><strong><em>Maddə 172. Hamilə və azyaşlı uşaqları olan məhkum qadınlar, habelə səkkiz yaşına &ccedil;atmamış uşağını təkbaşına b&ouml;y&uuml;dən kişilər barəsində cəzanın &ccedil;əkilməsinin təxirə salınması</em></strong></p>\r\n<p>172.1. Cəza&ccedil;əkmə m&uuml;əssisələrində cəza &ccedil;əkən hamilə və azyaşlı uşaqları olan məhkum qadınların<em>, habelə səkkiz yaşına &ccedil;atmamış uşağını təkbaşına b&ouml;y&uuml;dən kişilərin</em> cəzalarının &ccedil;əkilməsi məhkəmə tərəfindən uşaq səkkiz yaşına &ccedil;atanadək təxirə salına bilər.</p>\r\n<p>172.2. Şəxsiyyət əleyhinə ağır və ya x&uuml;susilə ağır cinayət t&ouml;rətməyə g&ouml;rə beş ildən &ccedil;ox m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzaya məhkum olunmuş hamilə və ya səkkiz yaşına qədər uşağı olan məhkum qadınlara<em>, habelə səkkiz yaşına &ccedil;atmamış uşağını təkbaşına b&ouml;y&uuml;dən kişilərə</em> cəzanın &ccedil;əkilməsinin təxirə salınması tətbiq edilmir.</p>\r\n<p>172.3. Hamilə və ya səkkiz yaşına qədər uşağı olan məhkum qadın<em>, habelə səkkiz yaşına &ccedil;atmamış uşağını təkbaşına b&ouml;y&uuml;dən kişi</em> cəzasının &ccedil;əkilməsinin təxirə salınması haqqında məhkəməyə ərizə ilə m&uuml;raciət edə bilər. Ərizəyə məhkumun xasiyyətnaməsi, <em>məhkuma</em> və onun uşağına yaşayış yeri verməklə zəruri yaşayış şəraiti yaradılması barədə qohumlarının yazılı razılığı, qadının hamiləliyi haqqında həkimin rəyi və ya uşağın doğum haqqında şəhadətnaməsinin surəti əlavə olunur. Məhkəmə <em>məhkumun</em> ərizəsinə baxarkən cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyətinin m&uuml;lahizəsini nəzərə alır.</p>\r\n<p>172.4. Cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti məhkəmədən hamilə və ya səkkiz yaşına qədər uşağı olan məhkum qadın<em>, habelə səkkiz yaşına &ccedil;atmamış uşağını təkbaşına b&ouml;y&uuml;dən kişi</em> barəsində cəzanın &ccedil;əkilməsinin təxirə salınması haqqında qərarı aldıqdan sonra onu azad edir. Məhkum azad edildiyi vaxtdan sonra on beş g&uuml;n m&uuml;ddətində yaşayış yeri &uuml;zrə <em>icra qurumuna</em> qeydiyyata g&ouml;t&uuml;r&uuml;lməsi ilə əlaqədar m&uuml;raciət etməsi haqqında ondan iltizam alınır.</p>\r\n<p>172.5. Hamilə və ya səkkiz yaşına qədər uşağı olan məhkum qadın<em>, habelə səkkiz yaşına &ccedil;atmamış uşağını təkbaşına b&ouml;y&uuml;dən kişi</em> cəzanın &ccedil;əkilməsindən azad edildikdən sonra yaşayış yerinə d&ouml;vlət hesabına gedir.</p>\r\n<p>172.6. Hamilə və ya səkkiz yaşına qədər uşağı olan məhkum qadının<em>, habelə səkkiz yaşına &ccedil;atmamış uşağını təkbaşına b&ouml;y&uuml;dən kişinin</em> azad olunduğu vaxtdan &uuml;&ccedil; g&uuml;ndən gec olmayaraq, yaşayış yeri &uuml;zrə məhkəməyə <em>və icra qurumuna</em> azad etmə tarixi g&ouml;stərilməklə, cəzanın icrasının təxirə salınması haqqında qərarın surəti g&ouml;ndərilir.</p>\r\n<p>172.7. Icra məmuru hamilə və ya səkkiz yaşına qədər uşağı olan məhkum qadını<em>, habelə səkkiz yaşına &ccedil;atmamış uşağını təkbaşına b&ouml;y&uuml;dən kişini</em> qeydiyyata g&ouml;t&uuml;r&uuml;r və onun &uuml;zərində nəzarəti həyata ke&ccedil;irir.</p>\r\n<p>172.8. Icra məmuru hamilə və ya səkkiz yaşına qədər uşağı olan məhkum qadını<em>, habelə səkkiz yaşına &ccedil;atmamış uşağını təkbaşına b&ouml;y&uuml;dən kişini</em> qeydiyyata aldıqdan sonra &uuml;&ccedil; g&uuml;n ərzində onun azad edildiyi cəza&ccedil;əkmə m&uuml;əssisəsinə məlumat g&ouml;ndərir. (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i11#i11">11</a>)</p>\r\n<p><strong><em>Maddə 173. Hamilə və ya azyaşlı uşaqları olan məhkum qadınların, habelə səkkiz yaşına &ccedil;atmamış uşağını təkbaşına b&ouml;y&uuml;dən kişilərin cəzanın &ccedil;əkilməsinin təxirə salınması şərtlərinə riayət etməmələrinin nəticələri</em></strong></p>\r\n<p>173.1. Hamilə və ya səkkiz yaşına qədər uşağı olan məhkum qadın<em>, habelə səkkiz yaşına &ccedil;atmamış uşağını təkbaşına b&ouml;y&uuml;dən kişi</em> azad edildiyi g&uuml;ndən on beş g&uuml;n ərzində qeydiyyata g&ouml;t&uuml;r&uuml;lməsi ilə əlaqədar &uuml;z&uuml;rs&uuml;z səbəbdən icra məmuruna m&uuml;raciət etmədikdə, onun yerinin m&uuml;əyyən edilməsi &uuml;&ccedil;&uuml;n tədbirlər həyata ke&ccedil;irilir, məhkumun yeri m&uuml;əyyən olunmadıqda isə <em>icra məmurunun qərarı ilə axtarış elan edilir və qərarın surəti m&uuml;vafiq icra hakimiyyəti orqanına g&ouml;ndərilir</em>.</p>\r\n<p>173.2. Hamilə və ya səkkiz yaşına qədər uşağı olan məhkum qadın<em>, habelə səkkiz yaşına &ccedil;atmamış uşağını təkbaşına b&ouml;y&uuml;dən kişi</em> cəzanın &ccedil;əkilməsinin təxirə salınması d&ouml;vr&uuml;ndə uşağı tərbiyə etməkdən boyun qa&ccedil;ırdıqda və ya uşaqdan imtina etdikdə icra məmuru tərəfindən ona rəsmi xəbərdarlıq edilir.</p>\r\n<p>173.3. Məhkum <span style="text-decoration: line-through;">qadın</span> xəbərdarlıqdan sonra uşaqdan imtina etdiyi və ya uşağı tərbiyə etməkdən boyun qa&ccedil;ırmaqda davam etdiyi halda icra məmuru məhkumun yaşayış yeri &uuml;zrə məhkəməyə cəzanın &ccedil;əkilməsinin təxirə salınmasının ləğvi və məhkumun h&ouml;kmlə m&uuml;əyyən olunmuş cəzanın icrası yerinə g&ouml;ndərilməsi haqqında təqdimat verir. Təqdimata cəzanın &ccedil;əkilməsinin təxirə salınması haqqında qərarın surəti əlavə olunur.</p>\r\n<p>173.4. Uşaq səkkiz yaşına &ccedil;atdıqda və ya uşaq vəfat etdikdə məhkəmə <em>məhkumun</em> davranışını, uşağın tərbiyəsinə m&uuml;nasibətini nəzərə alaraq, onun cəzadan şərti olaraq vaxtından əvvəl azad edilməsi və ya cəzanın &ccedil;əkilməmiş hissəsinin daha y&uuml;ng&uuml;l cəza n&ouml;v&uuml; ilə əvəz edilməsi, yaxud cəza&ccedil;əkmə m&uuml;əssisələrinə g&ouml;ndərilməsi məsələsini həll edir. <em>Məsələyə baxarkən məhkəmə icra məmurunun m&uuml;lahizəsini nəzərə alır.</em></p>\r\n<p><strong><em>Maddə 174. Cəza &ccedil;əkməkdən azad edilmiş şəxslərin h&uuml;quqi vəziyyəti</em></strong></p>\r\n<p>Cəza &ccedil;əkməkdən azad edilmiş şəxslər məhkumluğu olan şəxslər &uuml;&ccedil;&uuml;n qanunvericiliklə m&uuml;əyyən edilmiş məhdudiyyətlər nəzərə alınmaqla, Azərbaycan Respublikasının vətəndaşları &uuml;&ccedil;&uuml;n m&uuml;əyyən edilmiş h&uuml;quqlardan istifadə edir və vəzifələri daşıyırlar.</p>\r\n<p><strong>XX fəsil. Cəza &ccedil;əkməkdən azad edilmiş şəxslərin sosial adaptasiyası, onlara yardım g&ouml;stərilməsi və nəzarət edilməsi (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i12#i12">12</a></strong><strong>)</strong></p>\r\n<p><strong><em>Maddə 175. Penitensiar m&uuml;əssisələrdə cəza &ccedil;əkməkdən azad edilən şəxslərin sosial adaptasiyası ilə əlaqədar cəzanı icra edən m&uuml;əssisə və ya orqanın vəzifələri</em></strong></p>\r\n<p><em>175.1. Cəza &ccedil;əkməkdən azad edilməyə hazırlıq məqsədilə cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti məhkumlarla hazırlıq işi aparır, onlara h&uuml;quq və vəzifələrini izah edir, cəza &ccedil;əkməkdən azad edilmiş şəxslərin sosial adaptasiyası barədə qanunvericilik haqqında məlumat verir. (22)</em></p>\r\n<p><em>175.2</em> <em>Cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti</em><em>, m&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzalara məhkum edilmiş şəxslərin cəza m&uuml;ddətinin qurtarmasına &uuml;&ccedil; ay qalmış onların siyahısını m&uuml;vafiq icra hakimiyyəti orqanına təqdim edir. M&uuml;vafiq icra hakimiyyəti orqanı &nbsp;cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti ilə birlikdə ayda bir dəfədən az olmayaraq, sosial adaptasiyaya ehtiyacı olan cəza &ccedil;əkməkdən azad ediləcək şəxsləri m&uuml;əyyən edir və bu şəxsləri onların razılığı ilə qeydiyyata g&ouml;t&uuml;r&uuml;r. Qeydiyyata g&ouml;t&uuml;r&uuml;lm&uuml;ş şəxslərə onların h&uuml;quq və vəzifələri, sosial adaptasiya tədbirlərinin məzmunu və həyata ke&ccedil;irilməsi qaydası izah olunur. Sosial adaptasiyadan imtina etmiş şəxslərə cəza &ccedil;əkməkdən azad olunduqdan sonra &uuml;&ccedil; ay m&uuml;ddətində sosial adaptasiya məqsədi ilə m&uuml;vafiq icra hakimiyyəti orqanına m&uuml;raciət etmək h&uuml;ququ barədə məlumat verilir. Digər əsaslarla cəza &ccedil;əkməkdən azad olunmuş və sosial adaptasiyaya ehtiyacı olan şəxslər barədə cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti dərhal m&uuml;vafiq icra hakimiyyəti orqanını məlumatlandırır və bu şəxslərin sosial adaptasiya mərkəzlərinə g&ouml;ndərilməsi &uuml;&ccedil;&uuml;n birgə tədbirlər g&ouml;r&uuml;l&uuml;r. (22)</em></p>\r\n<p><em>175.3. Birinci və ikinci qrup əlilliyi olan məhkumlar, altmış iki yaşına &ccedil;atmış məhkum kişilər və əlli yeddi yaşına &ccedil;atmış məhkum qadınlar &ouml;z ərizələri və cəza&ccedil;əkmə m&uuml;əssisəsi m&uuml;diriyyətinin təqdimatı ilə m&uuml;vafiq icra hakimiyyəti orqanı tərəfindən əlillər və ya qocalar evinə g&ouml;ndərilirlər.</em></p>\r\n<p><em>175.4. Valideynlərini itirmiş və valideyn himayəsindən məhrum olmuş cəza &ccedil;əkməkdən azad edilmiş yetkinlik yaşına &ccedil;atmayanların yaşayış sahəsi olmadıqda, onlar qanunvericiliklə m&uuml;əyyən edilmiş qaydada m&uuml;vafiq icra hakimiyyəti orqanı tərəfindən internat məktəblərinə g&ouml;ndərilir və tam d&ouml;vlət təminatına g&ouml;t&uuml;r&uuml;l&uuml;rlər.</em> (<a href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/temp58772.htm#i12#i12">12</a>)</p>\r\n<p><strong><em>Maddə 176. Cəza &ccedil;əkməkdən azad edilmiş şəxslərə yardım g&ouml;stərilməsi</em></strong></p>\r\n<p>176.1. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və ya &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzanı &ccedil;əkməkdən azad edilmiş şəxslər yaşayış yerinə getmək &uuml;&ccedil;&uuml;n ərzaq məhsulları və gediş haqqı ilə təmin olunurlar.</p>\r\n<p>176.2. Cəza&ccedil;əkmə m&uuml;əssisələrindən azad edilmiş şəxslərin zəruri m&ouml;vs&uuml;mi geyimi, ayaqqabısı və bunları əldə etməyə imkanı olmadıqda, onlar d&ouml;vlət hesabına geyim və ayaqqabı ilə təmin olunurlar. Onlara birdəfəlik pul m&uuml;avinəti də verilə bilər.</p>\r\n<p>176.3. Cəza &ccedil;əkməkdən azad edilmiş şəxslərin ərzaq, geyim, ayaqqabı ilə təmin olunması, onlara gediş haqqının &ouml;dənilməsi, həm&ccedil;inin birdəfəlik pul m&uuml;avinətinin verilməsi <a title="Azərbaycan Respublikasının Nazirlər Kabineti / Azərbaycan Respublikası Prezidentinin Fərmanı - 25.08.2000, № 388" href="../../../../Documents%20and%20Settings/a.osmanova/My%20Documents/Local%20Settings/Администратор/Application%20Data/4044">m&uuml;vafiq icra hakimiyyəti orqanı</a> tərəfindən m&uuml;əyyən edilmiş qaydada cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti tərəfindən həyata ke&ccedil;irilir.</p>\r\n<p>176.4. M&uuml;əyyən m&uuml;ddətə azadlıqdan məhrum etmə və ya &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəza &ccedil;əkməkdən azad edilən və səhhətləri ilə əlaqədar digər şəxslərin qulluğuna ehtiyacı olan şəxslər, hamilə və ya səkkiz yaşına qədər uşağı olan məhkum qadınlar, həm&ccedil;inin yetkinlik yaşına &ccedil;atmayanlar azad edildikdə, cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti onların qohumlarını və ya digər şəxsləri xəbərdar edir.</p>\r\n<p>176.5. Cəza&ccedil;əkmə m&uuml;əssisələrindən azad edilmiş və bu Məcəllənin 176.4-c&uuml; maddəsində g&ouml;stərilən şəxslər yaşayış yerinə qohumlarının, himayə&ccedil;ilərinin və ya digər şəxslərin, yaxud cəza&ccedil;əkmə m&uuml;əssisəsinin iş&ccedil;ilərinin m&uuml;şayiəti ilə g&ouml;ndərilirlər.</p>\r\n<p><strong><em>Maddə 177. Cəza &ccedil;əkməkdən azad edilmiş şəxslərin əmək və məişət şəraiti ilə təmin olunmaq və başqa n&ouml;v sosial yardım almaq h&uuml;ququ</em></strong></p>\r\n<p><em>M&uuml;əyyən </em>m&uuml;ddətə azadlıqdan məhrum etmə və ya &ouml;m&uuml;rl&uuml;k azadlıqdan məhrum etmə n&ouml;v&uuml;ndə cəzadan azad edilmiş şəxslər qanunvericiliyə uyğun olaraq əmək və məişət şəraiti ilə təmin olunmaq və başqa n&ouml;v sosial yardım almaq h&uuml;ququna malikdirlər. (22)</p>\r\n<p><strong><em>Maddə 178. Cəzadan şərti olaraq vaxtından əvvəl azad edilmiş şəxslərə nəzarət</em></strong></p>\r\n<p>178.1. Cəzadan şərti olaraq vaxtından əvvəl azad edilmiş şəxslər &uuml;zərində nəzarəti məhkumun yaşayış yeri &uuml;zrə icra məmuru, hərbi qulluq&ccedil;ular &uuml;zərində nəzarəti isə hərbi hissələrin və m&uuml;əssisələrin komandanlığı həyata ke&ccedil;irir.</p>\r\n<p><em>178.2. Cəzadan şərti olaraq vaxtından əvvəl azad etmə haqqında qərarın surəti şəxsin azad olunduğu vaxtdan &uuml;&ccedil; g&uuml;ndən gec olmayaraq onun yaşayış yeri &uuml;zrə məhkəməyə və icra qurumuna g&ouml;ndərilir. Eyni zamanda cəza&ccedil;əkmə m&uuml;əssisəsinin m&uuml;diriyyəti tərəfindən şəxsdən azad edildiyi vaxtdan on beş g&uuml;n m&uuml;ddətində yaşayış yeri &uuml;zrə icra qurumuna qeydiyyata g&ouml;t&uuml;r&uuml;lməsi ilə əlaqədar m&uuml;raciət etməsi haqqında iltizam alınır.</em></p>\r\n<p><em>178.3. İcra məmuru cəzadan şərti olaraq vaxtından əvvəl azad edilmiş şəxslərin qeydiyyatını aparır, onların ictimai qaydalara riayət etməsinə və məhkəmə tərəfindən onlara həvalə olunan vəzifələrin yerinə yetirilməsinə nəzarət edir.</em></p>\r\n<p><em>178.4. Cəzadan şərti olaraq vaxtından əvvəl azad edilmiş şəxslər məhkəmə tərəfindən onlara həvalə edilən vəzifələri yerinə yetirməli və icra məmurunun &ccedil;ağırışı ilə gəlməlidirlər. &Uuml;zrs&uuml;z səbəblərdən gəlməyən şəxslər icra qurumunun rəhbəri tərəfindən təsdiq olunmuş icra məmurunun qərarı ilə məcburi gətirilə bilərlər. </em></p>\r\n<p><em>178.5. Cəzadan şərti olaraq vaxtından əvvəl azad edilmiş şəxs məhkəmə tərəfindən onun &uuml;zərinə qoyulmuş vəzifələrin yerinə yetirilməsindən m&uuml;təmadi yaxud qərəzli olaraq boyun qa&ccedil;ırdıqda və ya ictimai qaydaları pozduğuna g&ouml;rə barəsində inzibati tənbeh tətbiq edildikdə, icra məmuru və ya hərbi hissənin komandiri (rəisi) şərti olaraq vaxtından əvvəl azad etmənin ləğv edilməsi və cəzanın &ccedil;əkilməmiş hissəsinin icra olunması haqqında məhkəməyə təqdimat verir. </em></p>\r\n<p><em>178.6. Cəzadan şərti olaraq vaxtından əvvəl azad edilmiş şəxs tərəfindən onun &uuml;zərinə qoyulmuş vəzifələrin il ərzində dəfələrlə yerinə yetirilməməsi vəzifələri m&uuml;təmadi yerinə yetirməmə hesab edilir. </em></p>\r\n<p><em>178.7. Otuz g&uuml;ndən artıq m&uuml;ddətdə olduğu yer m&uuml;əyyən edilməyən şəxs ona həvalə edilmiş vəzifələri yerinə yetirməkdən qərəzli boyun qa&ccedil;ırmış hesab edilir.</em></p>\r\n<p><strong><em>VII b&ouml;lmə. Şərti məhkum edilmiş şəxslər &uuml;zərində nəzarət</em></strong></p>\r\n<p><strong>XXI fəsil. Şərti məhkum edilmiş şəxslər &uuml;zərində nəzarətin həyata ke&ccedil;irilməsi</strong></p>\r\n<p><strong><em>Maddə 179. Şərti məhkum edilmiş şəxslər &uuml;zərində nəzarəti həyata ke&ccedil;irən orqanlar</em></strong></p>\r\n<p>Şərti məhkum edilmiş şəxslər &uuml;zərində nəzarəti sınaq m&uuml;ddəti ərzində məhkumun yaşayış yeri &uuml;zrə icra məmuru, hərbi qulluq&ccedil;ular &uuml;zərində isə hərbi hissələrin və ya m&uuml;əssisələrin komandanlığı həyata ke&ccedil;irir.</p>\r\n<p><strong><em>Maddə 180. Şərti məhkum edilmiş şəxslər &uuml;zərində nəzarətin həyata ke&ccedil;irilməsi qaydası</em></strong></p>\r\n<p>180.1. Icra məmuru sınaq m&uuml;ddəti ərzində məhkumların qeydiyyatını aparır, onların işlədikləri yer &uuml;zrə işəg&ouml;t&uuml;rənin iştirakı ilə məhkumların ictimai qaydalara riayət etməsinə və məhkəmə tərəfindən onlara həvalə olunan vəzifələrin yerinə yetirilməsinə nəzarət edir.</p>\r\n<p>180.2. Məhkuma m&uuml;əyyən vəzifə tutma və ya m&uuml;əyyən fəaliyyətlə məşğul olma h&uuml;ququndan məhrum etmə n&ouml;v&uuml;ndə cəza əlavə cəza kimi təyin edildikdə, bu Məcəllənin 31-ci maddəsində nəzərdə tutulan tədbirləri icra məmuru həyata ke&ccedil;irir.</p>\r\n<p>180.3. <em>&ccedil;ıxarılmışdır. </em>(23)</p>\r\n<p>180.4. Şərti məhkum edilmiş şəxslər məhkəmə tərəfindən onlara həvalə edilən vəzifələri yerinə yetirməli və icra məmurunın &ccedil;ağırışı ilə gəlməlidirlər. &Uuml;z&uuml;rs&uuml;z səbəblərdən gəlməyən məhkumlar <em>icra qurumunun rəhbəri tərəfindən təsdiq olunmuş icra məmurunun qərarı i</em>lə məcburi gətirilə bilərlər.</p>\r\n<p><strong><em>Maddə 181. Sınaq m&uuml;ddətinin hesablanması</em></strong></p>\r\n<p>181.1. Sınaq m&uuml;ddəti məhkəmənin h&ouml;km&uuml;n&uuml;n qanuni q&uuml;vvəyə mindiyi g&uuml;ndən hesablanır.</p>\r\n<p>181.2. Sınaq m&uuml;ddətinin qurtarması ilə məhkum &uuml;zərində nəzarət dayandırılır və o, qeydiyyatdan &ccedil;ıxarılır.</p>\r\n<p><strong><em>Maddə 182. Şərti məhkum edilmiş şəxslərin məsuliyyəti</em></strong></p>\r\n<p>182.1. Şərti məhkum edilmiş şəxs məhkəmə tərəfindən ona &uuml;zərinə qoyulmuş vəzifələrin yerinə yetirilməsindən boyun qa&ccedil;ırdıqda və ya ictimai qaydaları pozduğuna g&ouml;rə barəsində inzibati tənbeh tətbiq edildikdə, icra məmuru və ya hərbi hissənin komandiri (rəisi) sınaq m&uuml;ddətinin uzadılması barədə məhkəməyə təqdimat verir.</p>\r\n<p>182.2. Şərti məhkum edilmiş şəxs sınaq m&uuml;ddətində məhkəmə tərəfindən onun &uuml;zərinə qoyulmuş vəzifələrin yerinə yetirilməsindən m&uuml;təmadi və ya qərəzli olaraq boyun qa&ccedil;ırdıqda icra məmuru və ya hərbi hissənin komandiri (rəisi) şərti məhkum etmənin ləğv edilməsi və h&ouml;kmlə təyin olunmuş cəzanın icra olunması barədə məhkəməyə təqdimat verir.</p>\r\n<p>182.3. Icra məmuru məhkumun &uuml;zərinə onun islah olunmasına k&ouml;mək edən yeni vəzifələrin qoyulması və ya əvvəllər məhkumun &uuml;zərinə qoyulmuş vəzifələrin tamamilə və ya qismən ləğv edilməsi barədə məhkəməyə təqdimat verə bilər.</p>\r\n<p>182.4. Məhkum tərəfindən onun &uuml;zərinə qoyulmuş vəzifələrin il ərzində dəfələrlə yerinə yetirilməməsi sınaq m&uuml;ddəti ərzində vəzifələri m&uuml;təmadi yerinə yetirməmə hesab edilir.</p>\r\n<p>182.5. Otuz g&uuml;ndən artıq m&uuml;ddətdə olduğu yer m&uuml;əyyən edilməyən məhkum ona həvalə edilmiş vəzifələri yerinə yetirməkdən qərəzli boyun qa&ccedil;ırmış hesab edilir.</p>\r\n<div align="center"><hr align="center" size="2" width="100%" /></div>\r\n<p align="center"><strong>Məcəlləyə dəyişiklik və əlavələrin edildiyi sənədlərin<br /> Siyahısı</strong></p>\r\n<p>1. 2 iyul 2001-ci il tarixli, № 172-IIQD n&ouml;mrəli qanun.</p>\r\n<p>2. 27 dekabr 2001-ci il tarixli, № 240-IIQD n&ouml;mrəli qanun.</p>\r\n<p>3. 19 aprel 2002-ci il tarixli, № 306-IIQD n&ouml;mrəli qanun &mdash; &laquo;Azərbaycan&raquo; qəzeti, <br /> 2 iyun 2002-ci il, № 124.</p>\r\n<p>4. 19 aprel 2002-ci il tarixli, № 310-IIQD n&ouml;mrəli qanun</p>\r\n<p>5. 30 aprel 2002-ci il tarixli, № 314-IIQD n&ouml;mrəli qanun &mdash; &laquo;Azərbaycan&raquo; qəzeti, <br /> 2 iyun 2002-ci il, № 124.</p>\r\n<p>6. 2 iyul 2002-ci il tarixli, 356-IIQD n&ouml;mrəli qanun &mdash; &laquo;Azərbaycan&raquo; qəzeti, <br /> 27 avqust 2002-ci il, № 196.</p>\r\n<p>7. 14 may 2004-c&uuml; il tarixli, 649-IIQD n&ouml;mrəli qanun &mdash; &laquo;Azərbaycan&raquo; qəzeti, <br /> 1 iyul 2004-c&uuml; il, № 150.</p>\r\n<p>8. 4 mart 2005-ci il tarixli, 856-IIQD n&ouml;mrəli qanun &mdash; &laquo;Azərbaycan&raquo; qəzeti, <br /> 30 aprel 2005-ci il, № 97.</p>\r\n<p>9. 10 oktyabr 2006-cı il tarixli, 162-IIIQD n&ouml;mrəli qanun &mdash; &laquo;Azərbaycan&raquo; qəzeti, <br /> 3 noyabr 2006-cı il, № 247.</p>\r\n<p>10. 16 iyun 2007-ci il tarixli, 389-IIIQD n&ouml;mrəli qanun &mdash; &laquo;Azərbaycan&raquo; qəzeti, <br /> 21 avqust 2007-ci il, № 185.</p>\r\n<p>11. 1 oktyabr 2007-ci il tarixli, 424-IIIQD n&ouml;mrəli qanun &mdash; &laquo;Azərbaycan&raquo; qəzeti, <br /> 27 noyabr 2007-ci il, № 267.</p>\r\n<p>12. 6 noyabr 2007-ci il tarixli, 480-IIIQD n&ouml;mrəli qanun &mdash; &laquo;Azərbaycan&raquo; qəzeti, <br /> 20 dekabr 2007-ci il, № 287.</p>\r\n<p>13. 7 dekabr 2007-ci il tarixli, 509-IIIQD n&ouml;mrəli qanun &mdash; &laquo;Azərbaycan&raquo; qəzeti, <br /> 30 dekabr 2007-ci il, № 294.</p>\r\n<p>14. 2 iyun 2008-ci il tarixli, 618-IIIQD n&ouml;mrəli qanun &mdash; &laquo;Azərbaycan&raquo; qəzeti, <br /> 29 iyun 2008-ci il, № 140.</p>\r\n<p>15. 13 iyun 2008-ci il tarixli, 648-IIIQD n&ouml;mrəli qanun &mdash; &laquo;Respublika&raquo; qəzeti, <br /> 10 iyul 2008-ci il, № 149.</p>\r\n<p>16. 24 iyun 2008-ci il tarixli, 657-IIIQD n&ouml;mrəli qanun &mdash; &laquo;Respublika&raquo; qəzeti, <br /> 15 iyul 2008-ci il, № 153.</p>\r\n<p>17. 1 fevral 2010-cu il tarixli,&nbsp; 951-IIIQD n&ouml;mrəli qanun &mdash; &ldquo;Azərbaycan&rdquo; qəzeti&nbsp; <br /> 19 mart 2010-cu il, № 62.</p>\r\n<p>18. . 5 mart 2010-cu il tarixli, 971-IIIQD n&ouml;mrəli qanun - &laquo;Azərbaycan&raquo; qəzeti, <br /> 18 aprel 2010-cu il, №&nbsp; 82.</p>\r\n<p>19. 18 iyun 2010-cu il tarixli, IIIQD n&ouml;mrəli qanun - &laquo;Azərbaycan&raquo; qəzeti, <br /> 17 iyul 2010-cu il, №&nbsp; 152.</p>\r\n<p>20. 21 dekabr 2010-cu il tarixli, 37-IVQD n&ouml;mrəli qanun - &laquo;Azərbaycan&raquo; qəzeti, <br /> 19 fevral 2011-ci il, № 39.</p>\r\n<p>21. 21 dekabr 2010-cu il tarixli, 38-IVQD n&ouml;mrəli qanun - &laquo;Azərbaycan&raquo; qəzeti, <br /> 20 fevral 2011-ci il, № 40.</p>\r\n<p>22. 31 may 2011-ci il tarixli, 138-IVQD n&ouml;mrəli qanun - &laquo;Azərbaycan&raquo; qəzeti, <br /> 02 iyul 2011-ci il, № 141.</p>\r\n<p>23. 10 iyun 2011-ci il tarixli, 157-IVQD n&ouml;mrəli qanun - &laquo;Azərbaycan&raquo; qəzeti, <br /> 31 iyul 2011-ci il, № 166.</p>\r\n<p>24. 15 noyabr&nbsp; 2011-ci il tarixli, 252-IVQD n&ouml;mrəli qanun - &laquo;Azərbaycan&raquo; qəzeti, <br /> 29 dekabr 2011-ci il, № 289.</p>\r\n<p>25. 20 aprel&nbsp; 2012-ci il tarixli, 324-IVQD n&ouml;mrəli qanun - &laquo;Azərbaycan&raquo; qəzeti, <br /> 12 may 2012-ci il, № 103.</p>', 10, 'az', 1, 1, NULL, NULL, NULL);
INSERT INTO `vl1_menumodel` (`type`, `menuItemTitle`, `id`, `link`, `modelId`, `treeItemId`, `content`, `r_id`, `lang_id`, `pageId`, `visible`, `forLoggedIn`, `pageType`, `image`) VALUES
(3, 'Sağlam insan uğrunda', 639, 'az/for/human', 10, 269, '<p class="p1">Hər birimiz cəmiyyətimizə xeyir verib, nəslimizi yaşadıb, arzularımızı həyyata ke&ccedil;irmək &uuml;&ccedil;&uuml;n ən azı sağlamlığımızı qoruyub saxlamaq &uuml;&ccedil;&uuml;n m&uuml;əyyən biliklərə malik olmaq haqda t&uuml;ş&uuml;n&uuml;r&uuml;k. S&ouml;hbət yalnız rasional qıdalanmadan getmir<strong>.&nbsp;</strong>Hal-hazırda<strong>&nbsp;</strong>sağlamlığa iqtisadi kateqoriya kimi baxılması təklif olunmuşdur ki&nbsp;bunun&nbsp;<span class="Apple-converted-space">&nbsp;</span>əsasında İNSAN&nbsp;&nbsp;<span class="Apple-converted-space">&nbsp;</span>KAPİTALI&nbsp;<span class="Apple-converted-space">&nbsp;</span>durur.<span class="s2">&nbsp;. İNSAN&nbsp;&nbsp;<span class="Apple-converted-space">&nbsp;</span>KAPİTALI&nbsp;<span class="Apple-converted-space">&nbsp;</span>fiziki,&nbsp;<span class="Apple-converted-space">&nbsp;</span>psixi və&nbsp;<span class="Apple-converted-space">&nbsp;</span>mənəvi sağlamlıqdır. Mənəvi sağlamlıq &ndash; bilik, bacarıq,&nbsp;<span class="Apple-converted-space">&nbsp;</span>qabiliyyət, təcr&uuml;bə, mədəni səviyəyə qoyulan investisiya nəticəsində əldə edilir.&nbsp;</span></p>\r\n<p class="p4">Məlumdur ki, sağlamlıq hər bir insan və b&uuml;t&uuml;nl&uuml;kdə bəşəriyyət &uuml;&ccedil;&uuml;n x&uuml;susi dəyər, insanın m&uuml;h&uuml;m xarakteristikalarından biridir. &Uuml;mumd&uuml;nya Sahiyyə Təşkilatının (&Uuml;ST) nizamnaməsində &ldquo;sağlamlıq&rdquo; anlayışı kifayət qədər abstrakt ifadə edilir: &ldquo;Yalnız fiziki q&uuml;sur və ya xəstəliklərin olmaması deyil, tam fiziki, psixi və sosial rifah halıdır&rdquo;.</p>\r\n<p class="p4">Bu g&uuml;n d&uuml;nyada rəsmi səhiyyə mənbələri təstiqləyir ki insanların xəstəlikləri 75%-dan cox hallarda psixosomatikdir (Liz Burbo, 2005), yəni fiziki səbəblərlə yanaşı emosional, mental mənşəlidirlər.</p>\r\n<p class="p3">&nbsp; &nbsp; &nbsp;<span class="Apple-converted-space">&nbsp;</span>Sağlam qalmağı&nbsp;<span class="Apple-converted-space">&nbsp;</span>m&uuml;əyyən edən b&uuml;t&uuml;n amillər sağlamlığı m&ouml;hkəmləşdirən (&ldquo;sağlamlıq amilləri&rdquo;) və pisləşdirən&nbsp;<span class="Apple-converted-space">&nbsp;</span>(&ldquo;risk faktorlarına&rdquo;)&nbsp;<span class="Apple-converted-space">&nbsp;</span>b&ouml;l&uuml;n&uuml;r.</p>\r\n<p class="p3">B&uuml;t&uuml;n amillər təsirindən asılı olaraq&nbsp;<span class="Apple-converted-space">&nbsp;</span>əsas d&ouml;rd qrupdur:</p>\r\n<p class="p3">1) həyat tərzi faktorları (&uuml;mumi payı 50%);</p>\r\n<p class="p3">2) ekoloji faktorlar (&uuml;mumi payı 20%);</p>\r\n<p class="p3">3) bioloji (irsiyyət) amillər (&uuml;mumi payı 20%);</p>\r\n<p class="p3">4) tibbi xidmət faktorları (&uuml;mumi payı 10%) .</p>\r\n<p class="p3">&nbsp; &nbsp;<span class="Apple-converted-space">&nbsp;</span>Sağlamlığı m&uuml;əyyənləşdirən əsas ekoloji amillərdə daxildir: təsil,&nbsp;<span class="Apple-converted-space">&nbsp;</span>istehsal m&uuml;hit və əmək amilləri, məişət, iqlim və təbii şərait, ətraf m&uuml;hitin təmizliy dərəcəsi. Sağlamlığı m&uuml;əyyənləşdirən əsas bioloji amillər:&nbsp;<span class="Apple-converted-space">&nbsp;</span>irsiyyət, yaş, cins və orqanizmin konstitusiyasının x&uuml;susiyyətləridir. Tibbi yardım&nbsp;<span class="Apple-converted-space">&nbsp;</span>faktorları əhaliyə tibbi xidmətin keyfiyyəti ilə m&uuml;əyyən olunur.</p>\r\n<p class="p3">Aydındır ki, fiziki və əqli rifahın olması&nbsp;<span class="Apple-converted-space">&nbsp;</span>sağlamlıq&nbsp;<span class="Apple-converted-space">&nbsp;</span>səviyyəsi ilə yox,&nbsp;<span class="Apple-converted-space">&nbsp;</span>şəxsin sağlamlığına m&uuml;nasibəti ilə m&uuml;əyyən edilir.&nbsp;<span class="Apple-converted-space">&nbsp;</span>Digər tərəfdən,&nbsp;<span class="Apple-converted-space">&nbsp;</span>ictimaiyyətin m&uuml;nasibəti c&ouml;x&nbsp;<span class="Apple-converted-space">&nbsp;</span>əhəmiyyətlidir. Təəss&uuml;f ki,&nbsp;<span class="Apple-converted-space">&nbsp;</span>sağlam&nbsp;<span class="Apple-converted-space">&nbsp;</span>qalmaq&nbsp;&ldquo;modası&rdquo; hələki&nbsp;<span class="Apple-converted-space">&nbsp;</span>insanların ş&uuml;urunda yoxdur və &ouml;z sağlamlığın&nbsp;<span class="Apple-converted-space">&nbsp;</span>xəstə olmadan qoruyanlara&nbsp;<span class="Apple-converted-space">&nbsp;</span>əksər insanlardan fərqli olan&nbsp;<span class="Apple-converted-space">&nbsp;</span>bir m&ouml;c&uuml;zə kimi&nbsp;<span class="Apple-converted-space">&nbsp;</span>bağırlar.</p>\r\n<p class="p3">&nbsp;<span class="Apple-converted-space">&nbsp;</span>&Ccedil;ox &ccedil;ox insanlar &ouml;z həyat tərzinə diqqət yetirməməklə&nbsp;<span class="Apple-converted-space">&nbsp;</span>sağlamlığa tərəf getməyin əvəzinə ,&nbsp;<span class="Apple-converted-space">&nbsp;</span>sağlamlığdan qa&ccedil;ırlar. Və əsas səbəb &ndash; insanın ş&uuml;urunda, onun psixologiyasındadır. &ldquo;Sağlamlığa m&uuml;nasibət&rdquo; anlayışının strukturu &ouml;z&uuml;ndə bunları birləşdirir:</p>\r\n<p class="p3">1) sağlamlıq halının qiymətləndirilməsi;</p>\r\n<p class="p3">2) sağlamlığın dəyərlər kimi dərk edilməsi;</p>\r\n<p class="p3">3) sağlamlığın qorunması &uuml;&ccedil;&uuml;n fəallıq g&ouml;stərilməsi.</p>', 58, 'az', 4, 1, NULL, 0, ''),
(1, 'Haqqımızda', 635, NULL, 0, 267, '<p>Haqqımızda</p>', 56, 'en', 4, 1, NULL, 0, ''),
