INSERT INTO `vl1_newsmodel` (`r_id`, `lang_id`, `itemTitle`, `content`, `date`, `id`, `image`, `smallImage`, `forLoggedIn`) VALUES
(1966, 'az', 'ATİB-in Ümumi Yığıncağı keçirildi', '<p>26 fevral 2013-c&uuml; ildəAzərbaycan T&uuml;rkiyə İş Adamları Birliyinin (ATİB) ofisində təşkilatın &Uuml;mumi Yığıncağının n&ouml;vbəti iclası ke&ccedil;irildi.</p>\r\n<p>İclas 26 fevral Xocalı Soyqırımı G&uuml;n&uuml; m&uuml;nasibətilə və hər iki &ouml;lkə şəhidlərinin ruhuna h&ouml;rmət əlaməti olaraq bir dəqiqəlik s&uuml;kutla başladı.</p>\r\n<p>Prosedura uyğun olaraq &Uuml;mumi Yığıncağın iştirak&ccedil;ıları tərəfindən iclasın sədri və katiblər se&ccedil;ildi.</p>\r\n<p>G&uuml;ndəliyə uyğun olaraq ATİB İdarə Heyətinin sədri Cemal Yangın İdarə Heyətinin 2012-ci il &uuml;zrə fəaliyyət hesabatını iclas iştirak&ccedil;ılarına təqdim etdi. İllik hesabat &Uuml;mumi Yığıncaq iştirak&ccedil;ıları tərəfindən yekdilliklə qəbul edildi.</p>\r\n<p>ATİB Təftiş Komissiyasının &uuml;zv&uuml; Qorxmaz Ağayev 2012-ci il &uuml;zrə maliyyə hesabatları barədə komissiyanın rəyini təqdim etdi. İclas iştirak&ccedil;ıları təftiş komissiyasının rəyini qəbul etdilər.</p>\r\n<p>Bundan sonra İdarə Heyətinə yeni &uuml;zvlərin se&ccedil;ilməsi məsələsinə baxıldı. İdarə heyəti sədrinin m&uuml;avini İlham Alməmmədov və İdarə Heyətinin &uuml;zv&uuml; Resul Doğan iş fəaliyyətləri ilə əlaqədar ATİB İdarə Heyətinə lazımi qədər vaxt ayıra bilməyəcəklərinə g&ouml;rə xahiş etdilər ki, onlar İdarə Heyəti &uuml;zvl&uuml;y&uuml;ndən azad edilsinlər. Onların yerinə Paşa Həyat Sığorta ASC-nin İdarə Heyətinin sədri M&uuml;rsəl R&uuml;stəmov və Pegasus (Aramex) şirkətinin rəhbəri Aydın Məmmədov ATİB İdarə Heyətinə &uuml;zv se&ccedil;ildilər.</p>\r\n<p>Daha sonra ATİB-in 2013-c&uuml; il &uuml;&ccedil;&uuml;n b&uuml;dcəsi təqdim&nbsp; olundu və sənədlər iclas iştirak&ccedil;ıları tərəfindən təsdiqləndi.</p>\r\n<table style="width: 100%;" border="0" align="left">\r\n<tbody>\r\n<tr>\r\n<td><a href="/public/Xəbərlər/s%201.jpg"><img title="/public/Xəbərlər/Untitled-1.jpg" src="http://atib.az/imagemanager/images/umumi%20y%201.jpg" alt="" width="173" height="130" /></a></td>\r\n<td><a href="/public/pdf-files/xeb/u%202.jpg"><img title="/public/Xəbərlər/Untitled-2.jpg" src="http://atib.az/imagemanager/images/umumi%20y%202.jpg" alt="" width="173" height="130" /></a></td>\r\n<td><a href="/public/Xəbərlər/s%202.jpg"><img src="http://atib.az/imagemanager/images/umumi%20y%203.jpg" alt="" width="173" height="130" /></a></td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n</tbody>\r\n</table>', '2013-02-26', 1966, '/Xəbərlər/sekil u. y boyuk.jpg', '/Xəbərlər/sekil u.y  kicik.jpg', 0),
