CREATE TABLE IF NOT EXISTS `vl1_questionsmodel` (
  `r_id` int(32) NOT NULL,
  `lang_id` char(2) NOT NULL,
  `itemTitle` text,
  `content` longtext,
  `date` date DEFAULT NULL,
  `id` int(32) NOT NULL AUTO_INCREMENT,
  `image` varchar(150) DEFAULT NULL,
  `smallImage` varchar(150) DEFAULT NULL,
  `forLoggedIn` int(1) DEFAULT NULL,
  `email` varchar(255) NOT NULL,
  `answered` smallint(2) DEFAULT '0',
  `filesLinks` text NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `Id` (`id`),
  KEY `R_id` (`r_id`),
  KEY `Lang id` (`lang_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=10420 ;

INSERT INTO `vl1_questionsmodel` (`r_id`, `lang_id`, `itemTitle`, `content`, `date`, `id`, `image`, `smallImage`, `forLoggedIn`, `email`, `answered`, `filesLinks`) VALUES
(10414, 'az', 'Salam her vaxtiniz xeyirli olsun bagislayin sizi bir sualla bagli narahat edirem menim etibarnameyle satdigim masin etibarnamenin vaxti bitdikden  sonra her hansi olumle neticelenen yol neqliyat hadisesi torederse mene hansisa inzibati pratakol ve ya meni mesulyete celb ede bilerlermi', NULL, NULL, 10415, NULL, NULL, NULL, 'd.dinar@box.az', 0, ''),
